    "contracts/interfaces/IBrawlerBearzConsumables.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IBrawlerBearzConsumables {
    error NotConsumed();
    error InvalidOwner();
    error InvalidItemType();

    struct Consumable {
        uint256 itemId;
        string name;
        string description;
        uint256 consumedAt;
    }

    event Consumed(uint256 indexed tokenId, uint256 itemTokenId);

    event Activated(uint256 indexed tokenId, uint256 itemTokenId);

    event Deactivated(uint256 indexed tokenId, uint256 itemTokenId);

    function setParentContract(address contractAddress) external;

    function setVendorContract(address contractAddress) external;

    function getConsumables(
        uint256 tokenId
    ) external view returns (bytes[] memory);

    function consume(
        uint256 tokenId,
        uint256 itemTokenId,
        bool isEnabled
    ) external;

    function activate(uint256 tokenId, uint256 itemTokenId) external;

    function deactivate(uint256 tokenId, uint256 itemTokenId) external;

    function isActiveConsumable(
        uint256 tokenId,
        uint256 itemTokenId
    ) external returns (bool);

    function toConsumableProperties(
        uint256 tokenId
    ) external view returns (string memory);
}
"
    },
    "contracts/interfaces/IBrawlerBearzDynamicItems.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155Upgradeable.sol";

interface IBrawlerBearzDynamicItems is IERC1155Upgradeable {
    struct CustomMetadata {
        string typeOf;
        string name;
        uint256 xp; // Min XP required to equip
        string rarity; // LEGENDARY, SUPER_RARE, RARE, UNCOMMON, COMMON
        uint256 atk; // Correlated to Strength
        uint256 def; // Correlated to Endurance
        uint256 usageChance; // Duration
        string usageDuration; // Persistent / Consumable
        string description;
        uint256 intel; // Correlated to intelligence
        uint256 luck; // Correlated to luck
    }

    function getMetadata(uint256 tokenId)
        external
        view
        returns (CustomMetadata memory);

    function getMetadataBatch(uint256[] calldata tokenIds)
        external
        view
        returns (CustomMetadata[] memory);

    function getItemType(uint256 tokenId) external view returns (string memory);

    function getItemName(uint256 tokenId) external view returns (string memory);

    function getItemXPReq(uint256 tokenId) external view returns (uint256);

    function setItemMetadata(
        uint256 tokenId,
        string calldata typeOf,
        string calldata name,
        uint256 xp
    ) external;

    function setItemMetadataStruct(
        uint256 tokenId,
        CustomMetadata memory metadata
    ) external;

    function shopDrop(address _toAddress, uint256 _amount) external;

    function dropItems(address _toAddress, uint256[] calldata itemIds) external;

    function burnItemForOwnerAddress(
        uint256 _typeId,
        uint256 _quantity,
        address _materialOwnerAddress
    ) external;

    function burnItemsForOwnerAddress(
        uint256[] memory _typeIds,
        uint256[] memory _quantities,
        address _materialOwnerAddress
    ) external;

    function mintItemToAddress(
        uint256 _typeId,
        uint256 _quantity,
        address _toAddress
    ) external;

    function mintBatchItemsToAddress(
        uint256[] memory _typeIds,
        uint256[] memory _quantities,
        address _toAddress
    ) external;

    function bulkSafeTransfer(
        uint256 _typeId,
        uint256 _quantityPerRecipient,
        address[] calldata recipients
    ) external;
}
