    "contracts/AlphaPassSale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {SignatureChecker} from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {EIP712Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";
import {NoncesUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/NoncesUpgradeable.sol";
import {IAlphaPassSale} from "./interfaces/IAlphaPassSale.sol";
import {IERC721Mintable} from "./interfaces/IERC721Mintable.sol";
import {IERC721MintableWithURI} from "./interfaces/IERC721MintableWithURI.sol";
import {NftSaleDetails} from "./libraries/LibNftSale.sol";
import {PurchaseItem} from "./libraries/PurchaseItem.sol";
import {PurchaseOrder, purchaseOrderHash} from "./libraries/PurchaseOrder.sol";
import "./libraries/Errors.sol" as Error;

contract AlphaPassSale is
    OwnableUpgradeable,
    EIP712Upgradeable,
    NoncesUpgradeable,
    ReentrancyGuardUpgradeable,
    IAlphaPassSale
{
    using EnumerableSet for EnumerableSet.AddressSet;
    using SafeERC20 for IERC20;

    /**
     * @notice Address of USDC contract
     */
    address public usdc;

    /**
     * @notice Address of treasury
     */
    address payable public treasury;

    /**
     * @notice Sale details for each NFT ID
     */
    mapping(uint256 id => NftSaleDetails) public saleDetails;

    /**
     * @notice Number of NFT IDs
     */
    uint256 public numberOfNFTs;

    address private _signer;

    /**
     * @notice Emitted when application signer address is changed
     * @param sender Address of account changing signer
     * @param oldSigner Address of old signer
     * @param newSigner Address of new signer
     */
    event SignerChanged(address indexed sender, address indexed oldSigner, address indexed newSigner);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Alpha Pass sale initializer
     * @param nfts Addresses of Alpha Pass NFTs in following order: King, Queen, Rook, Knight, Bishop, Pawn
     * @param prices Prices of NFTs
     * @param supplies Max supplies of NFTs
     * @param owner Contract owner
     * @param usdc_ Address of USDC contract
     * @param treasury_ Address of treasury
     * @param signer Address which signs ETH transactions
     */
    function initialize(
        address[] memory nfts,
        uint256[] memory prices,
        uint256[] memory supplies,
        address owner,
        address usdc_,
        address payable treasury_,
        address signer
    ) public initializer {
        __Ownable_init(owner);
        __EIP712_init("Alpha Pass Sale", "1.0.0");
        __Nonces_init();
        __ReentrancyGuard_init();

        if (usdc_ == address(0) || treasury_ == address(0) || signer == address(0)) {
            revert Error.InvalidAddress();
        }
        _initializeSales(nfts, prices, supplies);
        usdc = usdc_;
        treasury = treasury_;
        _signer = signer;
    }

    /**
     * @inheritdoc IAlphaPassSale
     */
    function buyNFTsWithEth(PurchaseOrder calldata order, bytes calldata signature) external payable nonReentrant {
        _validateSignature(purchaseOrderHash(order), signature);
        _validatePurchase(order.items, order.recipient);

        if (msg.value < order.value) {
            revert Error.InsufficientValue();
        }

        _useCheckedNonce(order.recipient, order.nonce);
        for (uint256 i = 0; i < order.items.length; ) {
            PurchaseItem memory item = order.items[i];

            if (item.quantity > 0) {
                NftSaleDetails memory pass = saleDetails[item.id];

                saleDetails[item.id].sold = uint128(pass.sold) + uint128(item.quantity);
                if (
                    item.uris.length > 0 &&
                    IERC165(pass.nft).supportsInterface(type(IERC721MintableWithURI).interfaceId)
                ) {
                    IERC721MintableWithURI(pass.nft).mintWithTokenURI(order.recipient, item.quantity, item.uris);
                } else {
                    IERC721Mintable(pass.nft).mint(order.recipient, item.quantity);
                }
            }
            unchecked {
                i += 1;
            }
        }

        if (order.value > 0) {
            // slither-disable-next-line arbitrary-send-eth
            treasury.transfer(order.value);
        }
    }

    /**
     * @inheritdoc IAlphaPassSale
     */
    function buyNFTsWithUsdc(PurchaseOrder calldata order, bytes calldata signature) external nonReentrant {
        _validateSignature(purchaseOrderHash(order), signature);
        _validatePurchase(order.items, order.recipient);

        _useCheckedNonce(order.recipient, order.nonce);
        for (uint256 i = 0; i < order.items.length; ) {
            PurchaseItem memory item = order.items[i];

            if (item.quantity > 0) {
                NftSaleDetails memory pass = saleDetails[item.id];

                saleDetails[item.id].sold = uint128(pass.sold) + uint128(item.quantity);
                if (
                    item.uris.length > 0 &&
                    IERC165(pass.nft).supportsInterface(type(IERC721MintableWithURI).interfaceId)
                ) {
                    IERC721MintableWithURI(pass.nft).mintWithTokenURI(order.recipient, item.quantity, item.uris);
                } else {
                    IERC721Mintable(pass.nft).mint(order.recipient, item.quantity);
                }
            }
            unchecked {
                i += 1;
            }
        }

        if (order.value > 0) {
            IERC20(usdc).safeTransferFrom(msg.sender, treasury, order.value);
        }
    }

    /**
     * @notice Returns number of available NFTs of selected type that can be purchased
     * @param id NFT type ID (0 - king, 1 - queen, 2- rook, 3 - bishop, 4 - knight, 5 - pawn)
     * @return Number of available NFTs
     */
    function getAvailableNFTs(uint256 id) public view returns (uint256) {
        NftSaleDetails memory pass = saleDetails[id];
        return pass.supply - pass.sold;
    }

    /**
     * @notice Returns a list of available NFTs of all types that can be purchased
     * @dev Available NFTs are returned as list in following order: [king, queen, rook, bishop, knight, pawn]
     * @return Number of available NFTs
     */
    function getAvailableNFTsList() public view returns (uint256[] memory) {
        uint256[] memory available = new uint256[](numberOfNFTs);
        for (uint256 i = 0; i < numberOfNFTs; ) {
            available[i] = getAvailableNFTs(i);
            unchecked {
                i += 1;
            }
        }
        return available;
    }

    /**
     * @notice Returns a list of NFTs prices of all types
     * @dev Prices are returned as list in following order: [king, queen, rook, bishop, knight, pawn]
     * @return Number of available NFTs
     */
    function getNFTPrices() external view returns (uint256[] memory) {
        uint256[] memory prices = new uint256[](numberOfNFTs);
        for (uint256 i = 0; i < numberOfNFTs; ) {
            prices[i] = saleDetails[i].price;
            unchecked {
                i += 1;
            }
        }
        return prices;
    }

    /**
     * @notice Sets sale details for selected NFT ID
     * @param id NFT type ID (0 - king, 1 - queen, 2- rook, 3 - bishop, 4 - knight, 5 - pawn)
     * @param nft NFT address
     * @param price NFT price in USDC in lowest units (1 USDC = 1000000)
     * @param supply NFT supply
     * @param sold Number of already sold NFTs
     */
    function setSaleDetails(uint256 id, address nft, uint256 price, uint256 supply, uint256 sold) public onlyOwner {
        saleDetails[id] = NftSaleDetails(nft, price, uint128(supply), uint128(sold));
    }

    /**
     * @notice Sets sale details for multiple NFT IDs
     * @param ids List of NFT type IDs (0 - king, 1 - queen, 2- rook, 3 - bishop, 4 - knight, 5 - pawn)
     * @param details NFT sale details
     */
    function setSaleDetailsBatch(uint256[] calldata ids, NftSaleDetails[] calldata details) external onlyOwner {
        if (ids.length != details.length) {
            revert Error.InvalidParameters();
        }
        for (uint256 i = 0; i < details.length; ) {
            NftSaleDetails memory sale = details[i];
            setSaleDetails(ids[i], sale.nft, sale.price, uint128(sale.supply), uint128(sale.sold));
            unchecked {
                i += 1;
            }
        }
    }

    /**
     * @notice Sets address of USDC contract
     * @param usdc_ New USDC address
     */
    function setUsdc(address usdc_) external onlyOwner {
        if (usdc_ == address(0)) {
            revert Error.InvalidAddress();
        }
        usdc = usdc_;
    }

    /**
     * @notice Sets address of treasury
     * @param treasury_ New treasury address
     */
    function setTreasury(address payable treasury_) external onlyOwner {
        if (treasury_ == address(0)) {
            revert Error.InvalidAddress();
        }
        treasury = treasury_;
    }

    /**
     * @notice Sets address of signer
     * @param signer New signer address
     */
    function setSigner(address signer) external onlyOwner {
        if (signer == address(0)) {
            revert Error.InvalidAddress();
        }

        address oldSigner = _signer;
        _signer = signer;
        emit SignerChanged(msg.sender, oldSigner, signer);
    }

    function _initializeSales(address[] memory nfts, uint256[] memory prices, uint256[] memory supplies) private {
        for (uint256 i = 0; i < nfts.length; ) {
            saleDetails[i] = NftSaleDetails(nfts[i], prices[i], uint128(supplies[i]), 0);
            unchecked {
                i += 1;
            }
        }
        numberOfNFTs = nfts.length;
    }

    function _validateSignature(bytes32 structHash, bytes calldata signature) private view {
        bool valid = SignatureChecker.isValidSignatureNow(_signer, _hashTypedDataV4(structHash), signature);
        if (!valid) {
            revert Error.InvalidSignature();
        }
    }

    function _validatePurchase(PurchaseItem[] memory items, address recipient) private view {
        if (recipient == address(0)) {
            revert Error.InvalidAddress();
        }
        if (items.length > numberOfNFTs) {
            revert Error.InvalidParameters();
        }
        uint256 totalQuantity = 0;
        for (uint256 i = 0; i < items.length; ) {
            PurchaseItem memory item = items[i];
            if (uint256(item.id) >= numberOfNFTs) {
                revert Error.InvalidId();
            }
            if (item.quantity > getAvailableNFTs(item.id)) {
                revert Error.InsufficientTokensAvailable();
            }
            totalQuantity += item.quantity;
            unchecked {
                i += 1;
            }
        }
        if (totalQuantity == 0) {
            revert Error.InvalidQuantity();
        }
    }

    uint256[44] private __gap;
}
"
