    "contracts/libraries/PurchaseItem.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

struct PurchaseItem {
    uint256 id;
    uint256 quantity;
    string[] uris;
}

bytes32 constant PURCHASE_ITEM_HASH = keccak256("PurchaseItem(uint256 id,uint256 quantity,string[] uris)");

function purchaseItemHash(PurchaseItem memory item) pure returns (bytes32) {
    bytes32[] memory uriHashes = new bytes32[](item.uris.length);
    for (uint256 i = 0; i < item.uris.length; i++) {
        uriHashes[i] = keccak256(abi.encodePacked(item.uris[i]));
    }
    return keccak256(abi.encode(PURCHASE_ITEM_HASH, item.id, item.quantity, keccak256(abi.encodePacked(uriHashes))));
}
"
    },
    "contracts/libraries/PurchaseOrder.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {PurchaseItem, purchaseItemHash} from "./PurchaseItem.sol";

struct PurchaseOrder {
    PurchaseItem[] items;
    address recipient;
    uint256 value;
    uint256 nonce;
}

bytes32 constant PURCHASE_ORDER_HASH = keccak256(
    "PurchaseOrder(PurchaseItem[] items,address recipient,uint256 value,uint256 nonce)PurchaseItem(uint256 id,uint256 quantity,string[] uris)"
);

function purchaseOrderHash(PurchaseOrder memory order) pure returns (bytes32) {
    bytes32[] memory itemsHashes = new bytes32[](order.items.length);
    for (uint256 i = 0; i < order.items.length; i++) {
        itemsHashes[i] = purchaseItemHash(order.items[i]);
    }
    return
        keccak256(
            abi.encode(
                PURCHASE_ORDER_HASH,
                keccak256(abi.encodePacked(itemsHashes)),
                order.recipient,
                order.value,
                order.nonce
            )
        );
}
"
