contract GuerillaNFT is Initializable, ERC721EnumerableUpgradeable, OwnableUpgradeable  {

    uint256 public basePrice; // Initial price
    uint256 public exponent;  // Exponential factor
    uint256 public tokenCounter;
    uint256 public totalLimit;
    uint256 public totalMinted;
    string public baseURI;

    uint256 public TRANSFER_LOCK_DURATION = 90 days;
    bool public transferLockOn = true;

    mapping(address => bool) public whitelist;
    mapping(address => uint256) public numMinted;
    mapping(uint256 => uint256) public purchaseTimes;

    uint256 public maxMintPerWallet = 2;

    event Mint(address indexed to, uint256 indexed tokenId);
    
    // Safeforge, SAFF+, aaa, 30

    function initialize(
        string memory _name,
        string memory _symbol,
        string memory _baseTokenURI,
        uint256 _totalLimit
    ) public initializer {
        __ERC721_init(_name, _symbol);
        __ERC721Enumerable_init();
        __Ownable_init(msg.sender);
        tokenCounter = 0;
        basePrice = 10000000000000000;
        exponent = 2;
        totalLimit = _totalLimit;
        totalMinted = 0;
        baseURI = _baseTokenURI;
    }
    function mint() external payable {
        //Checks
        require(totalMinted < totalLimit, "Minting limit reached");
        require(whitelist[msg.sender], "Address not whitelisted");
        require(numMinted[msg.sender] < maxMintPerWallet, "You have already minted the maximum allowed NFTs");
        require(msg.value >= getPrice(), "Insufficient funds");
        //Effects
        uint256 tokenId = totalSupply() + 1;
        numMinted[msg.sender]++;
        totalMinted++;
        purchaseTimes[tokenId] = block.timestamp;
        emit Mint(msg.sender, tokenId);

        //Interactions
        _safeMint(msg.sender, tokenId);

    }


    function transferFrom(address from, address to, uint256 tokenId) public virtual override(ERC721Upgradeable, IERC721) {
        
        if (transferLockOn) {
            require(block.timestamp >= purchaseTimes[tokenId] + TRANSFER_LOCK_DURATION, "Transfers are locked for 3 months");
        }
        super.transferFrom(from, to, tokenId);
    }

    function priceGrowth(uint256 _totalSupply, uint256 _basePrice) public pure returns(uint256) {
        uint256 divisor;
