        else if ((totalSupplyPlus >= 281) && (totalSupplyPlus < 451)) {
            divisor = 170;
            lowerLimit = 281;
            multiple = 16;
        }
        else if ((totalSupplyPlus >= 451) && (totalSupplyPlus < 681)) {
            divisor = 230;
            lowerLimit = 451;
            multiple = 32;
        }

        else if ((totalSupplyPlus >= 681) && (totalSupplyPlus < 981)) {
            divisor = 300;
            lowerLimit = 681;
            multiple = 64;
        }
        
        else if ((totalSupplyPlus >= 981) && (totalSupplyPlus < 1361)) {
            divisor = 380;
            lowerLimit = 981;
            multiple = 128;
        }

        else if ((totalSupplyPlus >= 981) && (totalSupplyPlus < 1361)) {
            divisor = 380;
            lowerLimit = 981;
            multiple = 128;
        }

        else if ((totalSupplyPlus >= 1361) && (totalSupplyPlus < 1831)) {
            divisor = 470;
            lowerLimit = 1361;
            multiple = 256;
        }

        else if ((totalSupplyPlus >= 1831) && (totalSupplyPlus < 2401)) {
            divisor = 570;
            lowerLimit = 1831;
            multiple = 512;
        }

        else if ((totalSupplyPlus >= 2401) && (totalSupplyPlus < 3081)) {
            divisor = 680;
            lowerLimit = 2401;
            multiple = 1024;
        }

        else if ((totalSupplyPlus >= 3081) && (totalSupplyPlus < 3881)) {
            divisor = 800;
            lowerLimit = 3081;
            multiple = 2048;
        }

        else if ((totalSupplyPlus >= 3881) && (totalSupplyPlus < 4811)) {
            divisor = 930;
            lowerLimit = 3881;
            multiple = 4096;
        }
        
        else if ((totalSupplyPlus >= 4811)) {
            divisor = 1070;
            lowerLimit = 4811;
            multiple = 8192;
        }


    return (_basePrice*multiple)+(_basePrice*multiple/divisor*(totalSupplyPlus-lowerLimit));

    }

    function getPrice() public view returns (uint256) {
        //return basePrice*((totalSupply() + 1)**exponent);
        return priceGrowth(totalSupply(), basePrice);
    }

    
    function setBasePrice(uint256 _newBasePrice) external onlyOwner {
        basePrice = _newBasePrice;
    }

    function setMaxMint(uint256 _newMintMax) external onlyOwner {
