pragma solidity ^0.8.0;

import {ERC721A} from "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";

contract Quick721a is ERC721A, ERC2981, Ownable {
    string public baseURI;
    mapping(address => bool) public blockedOperators;

    constructor(
        string memory name,
        string memory symbol,
        string memory baseURI_
    ) ERC721A(name, symbol) {
        baseURI = baseURI_;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    /**
     * @notice Change the base url for all NFTs
     */
    function setBaseURI(string calldata baseURI_) external onlyOwner {
        baseURI = baseURI_;
    }

    function safeMint(address to, uint256 quantity) external {
        _safeMint(to, quantity);
    }

    function burn(uint256 tokenId) external {
        _burn(tokenId);
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) public virtual override {
        require(!blockedOperators[operator], "Operator blocked");
        super.setApprovalForAll(operator, approved);
    }

    function approve(
        address to,
        uint256 tokenId
    ) public payable virtual override {
        require(!blockedOperators[to], "Operator blocked");
        super.approve(to, tokenId);
    }

    function isApprovedForAll(
        address owner,
        address operator
    ) public view virtual override returns (bool) {
        if (blockedOperators[operator]) return false;
        return super.isApprovedForAll(owner, operator);
    }

    function setBlockedOperator(
        address operator,
        bool blocked
    ) external onlyOwner {
        blockedOperators[operator] = blocked;
    }

    function setDefaultRoyalty(
        address receiver,
        uint96 feeNumerator
    ) public onlyOwner {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721A, ERC2981) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
"
