    "contracts/rewards-booster/interfaces/IOracle.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.10;

/**
 * @title Asymetrix Protocol V2 IOracle
 * @author Asymetrix Protocol Inc Team
 * @notice An interface that all custom oracles should implement.
 */
interface IOracle {
    /**
     * @notice Returns the latest answer.
     * @return _answer The latest answer.
     */
    function latestAnswer() external view returns (int256 _answer);

    /**
     * @notice Returns the data from the latest round.
     * @return _roundId The round ID.
     * @return _answer The answer from the latest round.
     * @return _startedAt Timestamp of when the round started.
     * @return _updatedAt Timestamp of when the round was updated.
     * @return _answeredInRound Deprecated. Previously used when answers could take multiple rounds to be computed.
     */
    function latestRoundData()
        external
        view
        returns (uint80 _roundId, int256 _answer, uint256 _startedAt, uint256 _updatedAt, uint80 _answeredInRound);

    /**
     * @notice Returns the number of decimals in the answer.
     * @return The number of decimals in the answer.
     */
    function decimals() external pure returns (uint8);
}
"
    },
    "contracts/rewards-booster/interfaces/IValuer.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.10;

/**
 * @title Asymetrix Protocol V2 IValuer
 * @author Asymetrix Protocol Inc Team
 * @notice An interface that all valuers should implement.
 */
interface IValuer {
    /**
     * @notice Returns the value of ERC-20 LP tokens (or ERC-721 NFT position) in USD in the liquidity pool.
     * @param _amountOrId An amount of ERC-20 LP tokens (or ERC-721 NFT position ID) to value.
     * @return _value The value of ERC-20 LP tokens (or ERC-721 NFT position) in USD.
     */
    function value(uint256 _amountOrId) external view returns (uint256 _value);

    /**
     * @notice Returns token amounts inside the position in USD.
     * @param _id Id of the Uniswap V3 position.
     * @return _value0 The first token amount in USD.
     * @return _value1 The second token amount in USD.
     */
    function getTokenAmountsInUSD(uint256 _id) external view returns (uint256 _value0, uint256 _value1);
}
"
