    "contracts/FukushimaFishNFT.sol": {
      "content": "pragma solidity ^0.8.9;

import "./SupplyController.sol";

import "ERC721A/ERC721A.sol";

import "@openzeppelin/contracts/token/common/ERC2981.sol";

import "operator-filter-registry/src/DefaultOperatorFilterer.sol";

import "solmate/src/auth/Owned.sol";
import "solmate/src/utils/ReentrancyGuard.sol";

contract FukushimaFishNFT is
    ERC721A("Fukushima Fish", "KOI"),
    Owned(msg.sender),
    ReentrancyGuard,
    ERC2981,
    DefaultOperatorFilterer
{
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721A, ERC2981) returns (bool) {
        return
            ERC721A.supportsInterface(interfaceId) ||
            ERC2981.supportsInterface(interfaceId) ||
            super.supportsInterface(interfaceId);
    }

    enum MintStatus {
        // 0 = closed
        CLOSED,
        // 1 = open for whitelisted addresses
        WHITELIST,
        // 2 = open for the public
        PUBLIC
    }

    uint256 public WHITELIST_MINT_COST = 0.05 ether;
    // Subject to change
    uint256 public PUBLIC_MINT_COST = 0.0777 ether;

    uint256 constant MAX_PUBLIC_MINT_PER_WALLET = 20;

    uint256 constant MAX_SUPPLY = 3888;

    string constant NO_MINTS_REMAINING = "You have no mints remaining";

    // The default mint status is CLOSED
    MintStatus public mintStatus = MintStatus.CLOSED;

    string _baseTokenURI = "";
    string _unrevealedURI = "";

    string public termsOfServiceURI;
    string public readMeURI;

    bytes32 whitelistMerkleProofRoot = bytes32(0);

    bool _updateOnTransfer = false;

    // the airdropper will mint 323 tokens to the wallet, then transfer the tokens based on who owned them
    address private airdropper;

    function setAirdropper(address _airdropper) external onlyOwner {
        airdropper = _airdropper;
    }

    function mintAirdropTeamMint(address to, uint256 amount) external {
        require(msg.sender == airdropper);
        _mint(to, amount);
    }

    SupplyController public controller;

    function setSupplyController(
        SupplyController _controller
    ) external onlyOwner {
        controller = _controller;
    }

    function setRoyaltyInfo(
        address royaltyReceiver,
        uint96 basisPoints
    ) external onlyOwner {
        _setDefaultRoyalty(royaltyReceiver, basisPoints);
    }

    function getMintTime(uint256 tokenId) external view returns (uint256) {
        TokenOwnership memory ownership = _ownershipOf(tokenId);
        return ownership.startTimestamp;
    }

    function setUpdateOnTransfer(bool status) external onlyOwner {
        _updateOnTransfer = status;
    }

    function _updateTimestampOnTransfer()
        internal
        virtual
        override
        returns (bool)
    {
        return _updateOnTransfer;
    }

    function exists(uint256 id) external view returns (bool) {
        return _exists(id);
    }

    function minted(address _addr) external view returns (uint256) {
        return _numberMinted(_addr);
    }

    function setTermsOfServiceURI(string calldata uri) external onlyOwner {
        termsOfServiceURI = uri;
    }

    function setReadMeURI(string calldata uri) external onlyOwner {
        readMeURI = uri;
    }

    function setMintStatus(MintStatus status) external onlyOwner {
        mintStatus = status;
    }

    function setMerkleRoot(bytes32 merkleRoot) external onlyOwner {
        whitelistMerkleProofRoot = merkleRoot;
    }

    function setBaseTokenURI(string calldata uri) external onlyOwner {
        _baseTokenURI = uri;
    }

    function setUnrevealedTokenURI(string calldata uri) external onlyOwner {
        _unrevealedURI = uri;
    }

    function setWhitelistMintPrice(uint256 cost) external onlyOwner {
        WHITELIST_MINT_COST = cost;
    }

    function setPublicMintPrice(uint256 cost) external onlyOwner {
        PUBLIC_MINT_COST = cost;
    }

    function ownerMint(address to, uint256 amount) external onlyOwner {
        // supply limit checks
        require(_totalMinted() < MAX_SUPPLY, "minted out.");
        require(
            _totalMinted() + amount <= MAX_SUPPLY,
            "mint amount would be out of range."
        );
        _mint(to, amount);
    }

    // Validate checks if the given address and proof result in the merkle tree root.
    // if the proof & the hashed address resolves to the provided proof, then the address
    // is within the whitelist.
    function validate(
        address addr,
        uint256 limit,
        bytes32[] calldata proof,
        uint256 path
    ) public view returns (bool) {
        bytes32 hash = keccak256(abi.encode(addr, limit));

        for (uint256 i; i < proof.length; i++) {
            // check if the path is odd and inverse the hash
            if (path & 1 == 1) {
                hash = keccak256(abi.encodePacked(hash, proof[i]));
            } else {
                hash = keccak256(abi.encodePacked(proof[i], hash));
            }

            // this divides the path by 2 lol bitwise ops > division
            path >>= 1;
        }

        return hash == whitelistMerkleProofRoot;
    }

    function publicMint(uint256 amount) external payable nonReentrant {
        uint256 currentSupply = _totalMinted();
        // supply limit checks
        require(
            msg.sender == tx.origin &&
                amount > 0 &&
                amount <= MAX_PUBLIC_MINT_PER_WALLET &&
                currentSupply < MAX_SUPPLY &&
                currentSupply + amount <= MAX_SUPPLY &&
                mintStatus == MintStatus.PUBLIC
        );

        uint256 minimumPayment = amount * PUBLIC_MINT_COST;

        require(msg.value >= minimumPayment, "not enough ether sent for mint!");

        _mint(msg.sender, amount);

        if (msg.value > minimumPayment) {
            // refund if the user somehow overpaid
            uint256 refund = msg.value - minimumPayment;
            (bool ok, ) = payable(msg.sender).call{value: refund}("");
            require(ok);
        }
    }

    function whitelistMint(
        uint256 amount,
        uint256 limit,
        bytes32[] calldata proof,
        uint256 path
    ) external payable nonReentrant {
        address msgSender = msg.sender;

        uint256 currentSupply = _totalMinted();

        // supply & sanity checks
        require(
            amount > 0 &&
                amount <= limit &&
                currentSupply < MAX_SUPPLY &&
                currentSupply + amount <= MAX_SUPPLY &&
                msg.sender == tx.origin &&
                mintStatus != MintStatus.CLOSED
        );

        // account mint limit checks
        uint256 _minted = _numberMinted(msgSender);
        require(proof.length > 0 && validate(msgSender, limit, proof, path));
        require(_minted < limit, "Mint limit reached!");
        uint256 remaining = limit - _minted;
        require(amount > 0 && amount <= remaining);

        // payment checks

        uint256 minimumPayment = amount * WHITELIST_MINT_COST;

        require(msg.value >= minimumPayment, "not enough ether sent for mint!");

        _mint(msgSender, amount);

        if (msg.value > minimumPayment) {
            // refund if the user somehow overpaid
            uint256 refund = msg.value - minimumPayment;
            (bool ok, ) = payable(msgSender).call{value: refund}("");
            require(ok);
        }
    }

    function tokenURI(uint256 id) public view override returns (string memory) {
        require(_exists(id));
        // baseTokenURI is empty, assume the token is unrevealed, and default to the unrevealed URI
        // else concatenate the base URI with the token ID and the JSON URI
        return
            bytes(_baseTokenURI).length == 0
                ? _unrevealedURI
                : string(
                    abi.encodePacked(_baseTokenURI, _toString(id), ".json")
                );
    }

    function _startTokenId() internal pure override returns (uint256) {
        return 1;
    }

    function withdraw(address to) external onlyOwner {
        (bool ok, ) = payable(to).call{value: address(this).balance}("");
        require(ok);
    }

    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal override {
        if (address(controller) != address(0)) {
            controller.onPreTransfer(from, to, startTokenId, quantity);
        }
    }

    function _afterTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal override {
        if (address(controller) != address(0)) {
            controller.onPostTransfer(from, to, startTokenId, quantity);
        }
    }

    /**
        Overrides for OperatorFilterer   
     */

    function setApprovalForAll(
        address operator,
        bool approved
    ) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function approve(
        address operator,
        uint256 tokenId
    ) public payable override onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public payable override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}
"
    },
    "contracts/FukushimaFishData.sol": {
      "content": "pragma solidity ^0.8.9;

contract FukushimaFishData {

    error TokenNotInitiated();

    uint8 constant CELESTIAL_FLAG = 0x01;
    uint8 constant BONUS_FLAG = 0x02;

    enum AuraLevel {
        NONE, // 0
        LOW,   // 1
        MED, // 2
        HIGH, // 3
        OVERFLOWING // 4
    }


    address public owner;

    mapping(AuraLevel => uint256) auraDailyYields;

    // Uint256 encoded Token Metadata
    mapping(uint256 => uint256) _metadata;

    mapping(address => bool) _admin;

    // // 0.054 $AURA / day
    // uint256 constant NONE = 0.054 ether;

    // // 0.304 $AURA / day
    // uint256 constant LOW = 0.304 ether;

    // // 0.75 $AURA / day
    // uint256 constant MED = 0.75 ether;

    // // 3 $AURA / day
    // uint256 constant HIGH = 3 ether;

    // // 10 $AURA / day
    // uint256 constant OVERFLOWING = 10 ether;

    // // 20 $AURA / day
    uint256 public BONUS = 20 ether;

    constructor() {
        auraDailyYields[AuraLevel.NONE] = 0.054 ether;
        auraDailyYields[AuraLevel.LOW] = 0.304 ether;
        auraDailyYields[AuraLevel.MED] = 0.75 ether;
        auraDailyYields[AuraLevel.HIGH] = 3 ether;
        auraDailyYields[AuraLevel.OVERFLOWING] = 10 ether; 

        owner = msg.sender;
        _admin[msg.sender] = true;
    }

    function hasFlag(uint256 flags, uint256 flag) internal pure returns(bool) {
        return (flags & flag) != 0;
    }


    modifier onlyAdmin() {
        require(_admin[msg.sender]);
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function transferOwnership(address to) external onlyOwner {
        owner = to;
    }

    function setAdmin(address addr, bool status) external onlyOwner {
        _admin[addr] = status;
    }

    function setBonus(uint256 bonusAmount) external onlyOwner {
        BONUS = bonusAmount;
    }

    /**
     * Updates a given aura level
     * 
     * @param auraLevel the level to update
     * @param amount  the amount for the given level
     */
    function updateYield(AuraLevel auraLevel, uint256 amount) external onlyOwner {
            auraDailyYields[auraLevel] = amount;
    }

    uint256 constant REACTOR_MODIFIER = 0x01;

    bytes32 public rootHash;


    function importData(bytes32 root) external onlyAdmin {
        rootHash = root;
    }


    function isCelestial(uint256 tokenId) external view returns (bool) {
        uint256 metadata = _metadata[tokenId];
         if (metadata == 0) revert TokenNotInitiated();
         (,,uint16 flags) = decode(metadata);
         return hasFlag(flags, CELESTIAL_FLAG);
    }

    function getAuraYieldForToken(uint256 tokenId) external view returns (uint256) {
        uint256 metadata = _metadata[tokenId];
        // no token will ever be encoded as 0
        if (metadata == 0) revert TokenNotInitiated();

        (, uint16 level, uint16 flags) = decode(metadata);

        uint256 baseYield = auraDailyYields[AuraLevel(level)]; 

        if (hasFlag(flags, BONUS_FLAG)) {
            baseYield += BONUS;
        }

        return baseYield;
    }


    function decode(uint256 encoded) internal pure returns(uint16 token, uint16 level, uint16 flags) {
        token = uint16(encoded);
        level = uint16(encoded >> 16);
        flags = uint16(encoded >> 32);
    }

    function isTokenInitiated(uint256 tokenId) external view returns(bool) { 
        return _metadata[tokenId] != 0;
    }  

    function initTokenData(
        uint256 encoded,
        uint256 path,
        bytes32[] calldata proof
    ) external  {
        // validates the merkle tree
        bytes32 leaf = keccak256(abi.encode(encoded));

        for (uint256 i; i < proof.length; i++) {
            // check if the path is odd and inverse the hash
            if (path & 1 == 1) {
                leaf = keccak256(abi.encodePacked(leaf, proof[i]));
            } else {
                leaf = keccak256(abi.encodePacked(proof[i], leaf));
            }

            path /= 2;
        }

        require(leaf == rootHash, "invalid proof.");

        // after verifying the encoded information is legit, set it
        (uint16 tokenId,,) = decode(encoded);
        _metadata[tokenId] = encoded;
    }
}
"
