         0x40 to store the current end of memory. We write past it (as
         writes are memory extensions), but don't update the offset so
         Solidity will reuse it. The memory used here is only needed for
         this context.
         FIXME: inline assembly can't access return values
        */
        bool ret;
        address addr;
        assembly {
            let size := mload(0x40)
            mstore(size, _hash)
            mstore(add(size, 32), _v)
            mstore(add(size, 64), _r)
            mstore(add(size, 96), _s)
            ret := call(3000, 1, 0, size, 128, size, 32) // NOTE: we can reuse the request memory because we deal with the return code.
            addr := mload(size)
        }
        return (ret, addr);
    }
    /*
     The following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    */
    function ecrecovery(bytes32 _hash, bytes memory _sig) internal returns (bool _success, address _recoveredAddress) {
        bytes32 r;
        bytes32 s;
        uint8 v;
        if (_sig.length != 65) {
            return (false, address(0));
        }
        /*
         The signature format is a compact form of:
           {bytes32 r}{bytes32 s}{uint8 v}
         Compact means, uint8 is not padded to 32 bytes.
        */
        assembly {
            r := mload(add(_sig, 32))
            s := mload(add(_sig, 64))
            /*
             Here we are loading the last 32 bytes. We exploit the fact that
             'mload' will pad with zeroes if we overread.
             There is no 'mload8' to do this, but that would be nicer.
            */
            v := byte(0, mload(add(_sig, 96)))
            /*
              Alternative solution:
              'byte' is not working due to the Solidity parser, so lets
              use the second best option, 'and'
              v := and(mload(add(_sig, 65)), 255)
            */
        }
        /*
         albeit non-transactional signatures are not specified by the YP, one would expect it
         to match the YP range of [27, 28]
         geth uses [0, 1] and some clients have followed. This might change, see:
         https://github.com/ethereum/go-ethereum/issues/2053
        */
        if (v < 27) {
            v += 27;
        }
        if (v != 27 && v != 28) {
            return (false, address(0));
        }
        return safer_ecrecover(_hash, v, r, s);
    }

    function safeMemoryCleaner() internal pure {
        assembly {
            let fmem := mload(0x40)
            codecopy(fmem, codesize, sub(msize, fmem))
        }
    }
}
// </provableAPI>

library MetaGlitchUtils{
    using SafeMath for uint;
    using Math for uint;
    
    uint constant mintFeeStart        = 8 finney;
    uint constant mintFeeIncrementBase = 2 finney;
    uint constant mintFeeIncrementBP   = 1000;
    uint constant sireRewardBP = 5000; //50% goes to sire after oracle fees. Remainder is for nft development and community events.
    
    uint constant BASIS_POINTS = 10000;
    
    function GetNewVal(uint8 valSire, uint8 valMin, uint8 valMax, uint8 mutability, uint8 roll) internal pure returns (uint8) {
        uint valSireStablized = uint(valSire).mul(10);
        uint rand = uint(roll % (valMax+1));
        uint8 val = uint8(
                        rand
                        .mul(uint(mutability))
                        .add(valSireStablized)
                        .div(uint(mutability+10))
                    );
        if(val<valMin)
            val = valMin;
        if(val>valMax)
            val = valMax;
        return val;
    }

    function GetAmountFromBasisPoints(uint amt, uint bp) internal pure returns (uint) {
        return amt.mul(bp).div(BASIS_POINTS);
    }
    function GetOffsetAtIndexUint8(uint8 bytesOffset, uint index, uint source) internal pure returns (uint8) {
        return uint8(source>>uint(bytesOffset).add(index.mul(8)).mul(8));
    }
    
    
    
    function GetNextSiringFee(uint siringFee) internal pure returns (uint) {
        return siringFee.add(
            GetAmountFromBasisPoints(
                siringFee,
                mintFeeIncrementBP
            )
            ).add(
                mintFeeIncrementBase
            );
    }
}

pragma solidity ^0.5.11;

contract OwnableDelegateProxy { }

contract ProxyRegistry {
    mapping(address => OwnableDelegateProxy) public proxies;
}

contract MetaGlitch is ERC721Full, Ownable, ReentrancyGuard, usingProvable {
    using SafeMath for uint;
    using Math for uint;

    address payable ethereal;

    //ERC721 PRIVATE VARIABLES
    string _name = "MetaGlitch";
    string _symbol = "Glitch";
    string _tokenURI = "https://api.metaglitch.xyz/tokenid/";
    uint _currentTokenId = 170; //after founder mints
    address proxyRegistryAddress;

    //MATH CONSTS
    uint constant GWEI = 1000000000;

    //PROVABLE ORACLE
    uint gasPrice = 2010000000; //many set exactly 3gwei, so adding 0.01 gwei increases speed much more than expected.
    uint gasAmount = 700000;
    uint constant NUM_RANDOM_BYTES_REQUESTED = 32;
    struct MintingData {
        uint baseId;
        uint sireId;
        uint tokenId;
        address baseContract;
        address minter;
    }
    
    mapping(bytes32 => MintingData) provableQueryToMintingData;

    //MINT FEES AND PAYOUTS
    uint constant mintFeeStart        = 8 finney;
    uint constant mintFeeIncrementBase = 2 finney;
    uint constant mintFeeIncrementBP   = 1000;
    uint constant sireRewardBP = 5000; //50% goes to sire after oracle fees. Remainder is for nft development and community events.

    uint openMintStart;

    //Variable mins, maxes, steps (mx+min must add to less than 256)
    uint8 constant delayMin  = 0; //100x scale
    uint8 constant delayMax  = 50;
    uint8 constant seedMin   = 1; //1x
    uint8 constant seedMax   = 99;
    uint8 constant quantityMin  = 1; //1x
    uint8 constant quantityMax  = 20;
    uint8 constant timescaleMin = 1;
    uint8 constant timescaleMax = 50;
    uint8 constant sizeMin  = 5; //2x scale
    uint8 constant sizeMax  = 160;
    uint8 constant posMin   = 0; //2x scale
    uint8 constant posMax   = 160;
    uint8 constant mutabilityMin = 1; //1x
    uint8 constant mutabilityMax = 100;
    uint8 constant speedMin = 4; //1x
    uint8 constant speedMax = 168;
    uint8 constant glitchCountMin = 1; //1x
    uint8 constant glitchCountMax = 4;

    uint constant minArea = 1250; //2x

    //MetaGlitch NFT data
    struct Glitch{
      uint8 delay;
      uint8 seed;
      uint8 quantity;
      uint8 timescale;
      uint8 sizeW;
      uint8 sizeH;
      uint8 posX;
      uint8 posY;
    }
    struct Stats{
        uint8 glitchCount;
        uint8 speed;
        uint8 mutability;
        address baseContract;
        uint siringTimer;
        uint siringFee;
        uint sireId;
        uint baseId;
        uint generation;
        uint[4] glitchIds;
    }
    mapping(uint => Stats) public nftStats;
    mapping(uint => Glitch) public glitches;
    uint _currentGlitchId = 1000; //First 1k are reserved for importing from old contract
    mapping(address => mapping(uint => uint)) public baseAddressTokenIdToMetaGlitch;
    
    event LogBaseChange(uint metaGlitchID);
    event LogSired(uint sireId);

    //Allowed base contracts - NFTs who have agreed MetaGlitch complies with their licensing terms.
    mapping(address => bool) allowedBaseContracts;

    modifier onlyEthereal {
        require(msg.sender == ethereal,"Only @ethereal can do this.");
        _;
    }
    modifier whenStandardMinting {
        require(now.max(openMintStart)==now,"Must be after open minting starts.");
        _;
    }

    constructor(address _proxyRegistryAddress, uint startTime)  ERC721Full(_name,_symbol) public{
        ethereal = msg.sender;
        openMintStart = startTime;
        proxyRegistryAddress = _proxyRegistryAddress;
        provable_setCustomGasPrice(gasPrice);
    }

    function mintGlitch(uint sireId, uint baseId, address baseContract) public payable nonReentrant whenStandardMinting{
        Stats memory sireStats = nftStats[sireId];
        require(sireStats.siringFee==msg.value,"Eth sent must be equal to the siring fee of the selected sire.");
        require(now.max(sireStats.siringTimer)==now,"Sire must be available.");
        require(allowedBaseContracts[baseContract],"Token must be from participating NFT contract.");
        require(IERC721(baseContract).ownerOf(baseId)==msg.sender,"You must own the base token to mint a glitch for it.");
        require(baseAddressTokenIdToMetaGlitch[baseContract][baseId]==0,"Base NFT must not be currently glitched."); //Note: this theoretically allows 1 double glitch for metaglitch 0, but reduced cost is an acceptable tradeof.

        baseAddressTokenIdToMetaGlitch[baseContract][baseId] = _currentTokenId;

        sireStats.siringTimer = now.add(uint(sireStats.speed).mul(1 hours));
        uint siringFee = sireStats.siringFee;
        sireStats.siringFee = MetaGlitchUtils.GetNextSiringFee(siringFee);
        nftStats[sireId] = sireStats;
        
        emit LogSired(sireId);

        bytes32 queryId = provable_newRandomDSQuery(
            0, //Execution delay
            NUM_RANDOM_BYTES_REQUESTED,
            gasAmount
        );
        provableQueryToMintingData[queryId] = MintingData({
            baseId: baseId,
            sireId: sireId,
            tokenId: _currentTokenId,
            baseContract: baseContract,
            minter: msg.sender
        });

        _currentTokenId = _currentTokenId.add(1);

        address(uint160(ownerOf(sireId)))
            .transfer(MetaGlitchUtils.GetAmountFromBasisPoints(siringFee,sireRewardBP));
    }

    function changeBase(uint metaGlitchId, uint baseId, address baseContract) public {
        require(ownerOf(metaGlitchId)==msg.sender,"Sender most own the MetaGlitch token.");
        require(allowedBaseContracts[baseContract],"Base token must be from participating NFT contract.");
        require(IERC721(baseContract).ownerOf(baseId)==msg.sender,"Sender must own the base token to mint a glitch for it.");
        require(baseAddressTokenIdToMetaGlitch[baseContract][baseId]==0,"Base NFT must not be currently glitched."); //Note: this theoretically allows 1 double glitch for metaglitch 0, but reduced cost is an acceptable tradeof.
        Stats memory metaglitchStats = nftStats[metaGlitchId];
        
        //remove old base contract+ids
        delete baseAddressTokenIdToMetaGlitch[metaglitchStats.baseContract][metaglitchStats.baseId];
        
        baseAddressTokenIdToMetaGlitch[baseContract][baseId] = metaGlitchId;
        metaglitchStats.baseContract = baseContract;
        metaglitchStats.baseId = baseId;
        nftStats[metaGlitchId] = metaglitchStats;
        
        emit LogBaseChange(metaGlitchId);
    }
    function unsetBase(uint metaGlitchId) public {
        Stats memory stats = nftStats[metaGlitchId];
        require(ownerOf(metaGlitchId)!=IERC721(stats.baseContract).ownerOf(stats.baseId),"MetaGlitch owner must not own the base NFT.");
        delete baseAddressTokenIdToMetaGlitch[stats.baseContract][stats.baseId];
        stats.baseContract = address(0x0);
        stats.baseId = 0;
        nftStats[metaGlitchId] = stats;
    }

    function onlyEthereal_adjustOracleGas(uint newGasPrice, uint newGasAmount) public onlyEthereal{
        gasPrice = newGasPrice;
        provable_setCustomGasPrice(gasPrice);
        gasAmount = newGasAmount;
    }
    function onlyEthereal_changeBaseURI(string memory newURI) public onlyEthereal{
        _tokenURI = newURI;
    }
    function onlyEthereal_toggleBaseContract(address baseContract) public onlyEthereal{
        allowedBaseContracts[baseContract] = !allowedBaseContracts[baseContract];
    }
    function onlyEthereal_withdrawEth(uint weiAmt) public onlyEthereal{
        ethereal.transfer(weiAmt);
    }
    
    //this comment documents the stupidity of Ethereal who ignored a bug report and set fee = time.
    //Normally the following two functions would have requires to limite Ethereal's activity.
    //Unfortunately, doing so would put the contract over the byte limit.
    function onlyEthereal_devMintFromOldBuggyContract(uint oldMetaGlitchId) public onlyEthereal{
        //require(oldMetaGlitchId < 170,"Only 170 old founders exist.");
        MetaGlitch oldMetaGlitchContract = MetaGlitch(address(0x90D86699b60938FCA1cb5817c6E85f65e84663eb));
        //require(oldMetaGlitchContract.ownerOf(oldMetaGlitchId)!=address(0x0),"oldMetaGlitchId must have been minted.");
        (
        uint8 glitchCount,
        uint8 speed,
        uint8 mutability,
        address baseContract,
        uint siringTimer,
        uint siringFee,
        uint sireId,
        uint baseId,
        uint generation
        ) = oldMetaGlitchContract.nftStats(oldMetaGlitchId);
        uint[4] memory glitchIds;
        for(uint8 i; i<glitchCount; i++){
            uint glitchId = oldMetaGlitchContract.GetGlitchID(oldMetaGlitchId, i);
            glitchIds[i] = glitchId;
        }
        nftStats[oldMetaGlitchId] = Stats(glitchCount,speed,mutability,baseContract,siringTimer,siringFee,(8 finney),baseId,generation,glitchIds);
        baseAddressTokenIdToMetaGlitch[baseContract][baseId] = oldMetaGlitchId;
        _safeMint(oldMetaGlitchContract.ownerOf(oldMetaGlitchId), oldMetaGlitchId);
    }
    function onlyEthereal_loadGlitchFromOldBuggyContract(uint oldGlitchId) public onlyEthereal{
        //require(oldGlitchId < 1000,"New glitches start at 1k - old must be less than 1k.");
        MetaGlitch oldMetaGlitchContract = MetaGlitch(address(0x90D86699b60938FCA1cb5817c6E85f65e84663eb));
        (
            uint8 delay,
            uint8 seed,
            uint8 quantity,
            uint8 timescale,
            uint8 sizeW,
            uint8 sizeH,
            uint8 posX,
            uint8 posY
            ) = oldMetaGlitchContract.glitches(oldGlitchId);
        glitches[oldGlitchId] = Glitch(delay,seed,quantity,timescale,sizeW,sizeH,posX,posY);
    }

    /**
     * Provable callback for minting
     */
    function __callback(
        bytes32 queryId,
        string memory _result
    ) public {
        require(msg.sender == provable_cbAddress(),"Callback can only come from Provable.");

        MintingData memory mintingData = provableQueryToMintingData[queryId];

        Stats memory sire;
        
        sire = nftStats[mintingData.sireId];

        uint randStats = uint(
                keccak256(abi.encodePacked(_result)) ^ blockhash(block.number-1)
            );

        uint[4] memory glitchIds;
        Stats memory metaglitchStats = Stats({
            speed: MetaGlitchUtils.GetNewVal(sire.speed, speedMin, speedMax, sire.mutability, uint8(randStats)),
            mutability: MetaGlitchUtils.GetNewVal(sire.mutability, mutabilityMin, mutabilityMax, sire.mutability, uint8(randStats>>8)),
            siringTimer: 0,
            siringFee: mintFeeStart,
            sireId: mintingData.sireId,
            baseId: mintingData.baseId,
            baseContract: mintingData.baseContract,
            glitchCount: MetaGlitchUtils.GetNewVal(sire.glitchCount, glitchCountMin, glitchCountMax, sire.mutability, uint8(randStats>>16)),
            generation: sire.generation.add(1),
            glitchIds: glitchIds
        });
        
        

        uint randGlitch = randStats ^ uint(blockhash(block.number-2)); //max 32 uint8 or 8 glitch properties

        for(uint i; i<metaglitchStats.glitchCount; i++){
            Glitch memory sireGlitch = glitches[sire.glitchIds[i]];
            Glitch memory glitch = Glitch({
                delay: MetaGlitchUtils.GetNewVal(sireGlitch.delay, delayMin, delayMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(0, i, randGlitch)),
                seed: MetaGlitchUtils.GetNewVal(sireGlitch.seed, seedMin, seedMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(1, i, randGlitch)),
                quantity: MetaGlitchUtils.GetNewVal(sireGlitch.quantity, quantityMin, quantityMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(2, i, randGlitch)),
                timescale: MetaGlitchUtils.GetNewVal(sireGlitch.timescale, timescaleMin, timescaleMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(3, i, randGlitch)),
                sizeW: MetaGlitchUtils.GetNewVal(sireGlitch.sizeW, sizeMin, sizeMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(4, i, randGlitch)),
                sizeH: MetaGlitchUtils.GetNewVal(sireGlitch.sizeH, sizeMin, sizeMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(5, i, randGlitch)),
                posX: MetaGlitchUtils.GetNewVal(sireGlitch.posX, posMin, posMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(6, i, randGlitch)),
                posY: MetaGlitchUtils.GetNewVal(sireGlitch.posY, posMin, posMax, sire.mutability, MetaGlitchUtils.GetOffsetAtIndexUint8(7, i, randGlitch))
            });
            if(uint(glitch.sizeW) * uint(glitch.sizeH) < uint(minArea)){
                if(glitch.sizeW > glitch.sizeH){
                    glitch.sizeW = uint8(minArea / glitch.sizeH);
                }else{
                    glitch.sizeH = uint8(minArea / glitch.sizeW) ;
                }
            }
            if(uint(glitch.posX) + uint(glitch.sizeW) > sizeMax)
                glitch.posX = sizeMax - glitch.sizeW;
            if(uint(glitch.posY) + uint(glitch.sizeH) > sizeMax)
                glitch.posY = sizeMax - glitch.sizeH;

            metaglitchStats.glitchIds[i] = _currentGlitchId;
            glitches[_currentGlitchId] = glitch;
            _currentGlitchId = _currentGlitchId.add(1);
        }
        
        nftStats[mintingData.tokenId] = metaglitchStats;
        
        _safeMint(mintingData.minter, mintingData.tokenId);

        delete provableQueryToMintingData[queryId];
    }

    /**
   * Override isApprovedForAll to whitelist user's OpenSea proxy accounts to enable gas-less listings.
   */
  function isApprovedForAll(
    address owner,
    address operator
  )
    public
    view
    returns (bool)
  {
    // Whitelist OpenSea proxy contract for easy trading.
    ProxyRegistry proxyRegistry = ProxyRegistry(proxyRegistryAddress);
    if (address(proxyRegistry.proxies(owner)) == operator) {
        return true;
    }

    return super.isApprovedForAll(owner, operator);
  }


    function tokenURI(uint metaGlitchId) external view returns(string memory){
        return strConcat(_tokenURI,uint2str(metaGlitchId));
    }

    //MetaGlitch Utilities
    function GetGlitchID(uint tokenId, uint8 index) external view returns(uint){
        return nftStats[tokenId].glitchIds[index];
    }
    
}
