contract RobinhoodVestingPool {

    address immutable public token;

    struct Vesting {
        uint256 amount;
        uint256 claimed;
        uint256 vestingAt;
        uint256 vestingRate;
    }

    event Claimed(address to, uint256 amount);
    event VestingAdded(address to, uint256 amount, uint256 vestingAt, uint256 vestingRate);

    mapping(address => Vesting) public vestingPools;

    constructor(address _token) {
        token = _token;
    }

    function addVesting(
        address _to,
        uint256 _amount,
        uint256 _vestingAt,
        uint256 _vestingPeriod
    ) external {
        require(_to != address(0), '!0');
        require(_vestingPeriod > 0 && _vestingPeriod <= 1 ether, 'invalid period');
        require(vestingPools[_to].amount == 0 && _amount > 0, 'invalid vesting');

        uint256 vestingRate = _amount * 1 ether / _vestingPeriod;
        vestingPools[_to].amount = _amount;
        vestingPools[_to].vestingAt = _vestingAt;
        vestingPools[_to].vestingRate = vestingRate;

        SafeERC20.safeTransferFrom(IERC20(token), msg.sender, address(this), _amount);

        emit VestingAdded(_to, _amount, _vestingAt, vestingRate);
    }

    function claimable(address _to) public view returns (uint256) {
        Vesting memory pool = vestingPools[_to];
        if (pool.amount == 0) return 0;

        uint256 timeDiff = block.timestamp > pool.vestingAt ? block.timestamp - pool.vestingAt : 0;
        uint256 releaseAmount = pool.vestingRate * timeDiff / 1 ether;
        releaseAmount = releaseAmount > pool.amount ? pool.amount : releaseAmount;
        return releaseAmount - pool.claimed;
    }

    function transferOwnership(address _to) external {
        uint256 _claimable = claimable(msg.sender);
        require(_claimable > 0, 'nothing to transfer');
        require(_to != address(0), '!0');
        require(vestingPools[_to].amount == 0, 'invalid vesting');
        vestingPools[msg.sender].claimed =  vestingPools[msg.sender].amount;
        vestingPools[_to].amount = vestingPools[msg.sender].amount;
        vestingPools[_to].vestingAt = vestingPools[msg.sender].vestingAt;
        vestingPools[_to].vestingRate = vestingPools[msg.sender].vestingRate;
        emit VestingAdded(_to,vestingPools[_to].amount, vestingPools[_to].vestingAt, vestingPools[_to].vestingRate);
    }

    function claim() external {
        uint256 _claimable = claimable(msg.sender);
        require(_claimable > 0, 'nothing here');

        vestingPools[msg.sender].claimed += _claimable;
        SafeERC20.safeTransfer(IERC20(token), msg.sender, _claimable);

        emit Claimed(msg.sender, _claimable);
    }

