    struct snap_struct
    {
	uint48 blk;
	uint48 time;
	address sender;
	uint256 totalSupply;
	uint256 holdersLength;
    }
    mapping(uint256 => snap_struct)public snapInfo;
    uint256 public snapLength;
    address[] public holders;
    struct holder_struct
    {
        bool set;
        uint48 time_funder;
        uint48 time_sweep;
        uint48 txs;
        uint48 tx_num_in;
        uint48 tx_num_out;
        uint256 value;
	uint256 balance;
        uint48[] blks;
        uint48[] times;
        uint256[] balances;
    }
    mapping(address => holder_struct)public holdersInfo;
    function decimals() public view virtual override returns (uint8)
    {
        return 18;
    }
    function burn(uint256 amount)public
    {
        _burn(_msgSender(),amount);
	_holder_transfer(_msgSender(), address(0), amount);
    }
    function mint(address addr,uint256 amount)public onlyAdmin
    {
	if(addr == address(0))addr = _msgSender();
        _mint(addr,amount);
	_holder_transfer(address(0), addr, amount);
	snapshot();
    }
    function snapshot() public onlyAdmin returns(uint256 id)
    {
	uint256 t = totalSupply();
        id = _snapshot();
	snapInfo[id].blk = uint48(block.number);
	snapInfo[id].time = uint48(block.timestamp);
	snapInfo[id].sender = _msgSender();
	snapInfo[id].totalSupply = t;
	snapInfo[id].holdersLength = holders.length;
	snapLength = id;
    }
    function _beforeTokenTransfer(address from, address to, uint256 amount)internal override(ERC20, ERC20Snapshot)
    {
        if(holdersInfo[from].set == false)
        {
            holdersInfo[from].set = true;
            holdersInfo[from].time_funder = uint48(block.timestamp);
            holders.push(from);
        }
        if(holdersInfo[to].set == false)
        {
            holdersInfo[to].set = true;
            holdersInfo[to].time_funder = uint48(block.timestamp);
            holders.push(to);
        }
        super._beforeTokenTransfer(from, to, amount);
    }
    function _transfer(address sender, address recipient, uint256 amount)internal override(ERC20)
    {
        super._transfer(sender, recipient, amount);
	_holder_transfer(sender, recipient, amount);
    }
    function _holder_transfer(address sender, address recipient, uint256 amount)internal
    {
	holdersInfo[sender].txs++;
        holdersInfo[sender].tx_num_out++;
        holdersInfo[recipient].tx_num_in++;

        holdersInfo[sender].blks.push(uint48(block.number));
        holdersInfo[recipient].blks.push(uint48(block.number));

        holdersInfo[sender].times.push(uint48(block.timestamp));
        holdersInfo[recipient].times.push(uint48(block.timestamp));

        holdersInfo[sender].value += amount;
        holdersInfo[recipient].value += amount;

	uint256 b_sender = balanceOf(sender);
	uint256 b_recipient = balanceOf(recipient);
        holdersInfo[sender].balances.push(b_sender);
        holdersInfo[recipient].balances.push(b_recipient);

        holdersInfo[sender].balance = b_sender;
        holdersInfo[recipient].balance = b_recipient;

	if(b_sender == 0)
        holdersInfo[sender].time_sweep = uint48(block.timestamp);
	else
        holdersInfo[sender].time_sweep = 0;

	if(b_recipient == 0)
        holdersInfo[recipient].time_sweep = uint48(block.timestamp);
	else
        holdersInfo[recipient].time_sweep = 0;

    }
    function balanceList(address addr)public view returns(uint256[]memory balances,uint48[]memory blks,uint48[]memory times)
    {
	balances 	= holdersInfo[addr].balances;
	blks		= holdersInfo[addr].blks;
	times 		= holdersInfo[addr].times;
    }
    function holdersLength()public view returns(uint256)
    {
	return holders.length;
    }
    function balanceOfAtById(uint256 holder_id,uint256 snap)public view returns(uint256)
    {
	address addr = holders[holder_id];
	return balanceOfAt(addr,snap);
    }
    function holdersListAt(uint256 snap)public view returns(uint256,address[] memory,uint256[] memory)
    {
	address addr;
	uint256 i;
	uint256 l = snapInfo[snap].holdersLength;
	address[] memory out1 = new address[](l);
	uint256[] memory out2 = new uint256[](l);
	if(l>0)
	for(i=1;i<=l;i++)
	{
	    addr = holders[i];
	    out1[i-1] = addr;
	    out2[i-1] = balanceOfAt(addr,snap);
	}
	return(l,out1,out2);
    }
    function snapHolderLen(uint256 snap)public view returns(uint256)
    {
	return snapInfo[snap].holdersLength;
    }
}
