// File: EthsMarketV3.sol










pragma solidity 0.8.9;

contract EthsMarketV2 is Initializable, OwnableUpgradeable, UUPSUpgradeable, ReentrancyGuardUpgradeable{

    error EthscriptionNotDeposited();
    error ZeroBalance();

    struct Order {
        bytes32 tokenId;
    }
    mapping(bytes32 => Order) public orders;

    mapping(bytes32 =>  mapping(uint256 => bool)) private ethscriptions_signatures;
    mapping(bytes32 => mapping(address => uint256)) private ethscriptionReceivedOnBlockNumber;

    uint256 public protocol_rate;
    uint256 public timeFrame;

    address public authorized_verifier;

    //TRANSFER_BLOCK_CONFIRMATIONS
    uint256 public ethscription_transfer_cooldown_blocks;

    mapping(bytes32 => uint256) public orders_sign_time;
    uint256 public orders_time_range;

    event OrderInitializes(bytes32 indexed tokenId, address indexed seller, uint256 block_number);
    event OrderBought(bytes32 indexed tokenId, address indexed seller, string c_name, address indexed buyer, uint256 price, uint256 timestamp);
    event Orderwithdrawn(bytes32 indexed tokenId ,address indexed seller, uint256 timestamp, string c_name);

    event ethscriptions_protocol_TransferEthscriptionForPreviousOwner(
        address indexed previousOwner,
        address indexed recipient,
        bytes32 indexed ethscriptionId
    );
    event ethscriptions_protocol_TransferEthscription(address indexed recipient,bytes32 indexed ethscriptionId);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(uint256 _rate, address _authorized_s) public initializer {
        protocol_rate = _rate;
        authorized_verifier = _authorized_s;
        timeFrame = 120;
        ethscription_transfer_cooldown_blocks = 5;

        __Ownable_init();
        __UUPSUpgradeable_init();
    }

    function setRate(uint256 _rate)external onlyOwner {
        protocol_rate = _rate;
    }

    function setVerifier(address _s)external onlyOwner {
        authorized_verifier = _s;
    }

    function set_orders_time_range(uint256 _t)external onlyOwner {
        orders_time_range = _t;
    }

    function setTimeFrame(uint256 _t)external onlyOwner {
        timeFrame = _t;
    }

    function setDeplayBlocks(uint256 _deplay_blocks)external onlyOwner {
        ethscription_transfer_cooldown_blocks = _deplay_blocks;
    }

    function toBytes32(address addr) pure internal returns (bytes32) {
        return bytes32(uint256(uint160(addr)));
    }

    function protocel_fee_get(uint256 _price, uint256 _discount)view internal returns (uint256){
        if(protocol_rate > 0){
            uint256 protocol_rate_end = _discount > 0 ? (protocol_rate * (100-_discount) / 100) : (protocol_rate);
            return _price * protocol_rate_end / 10000;
        }else{
            return 0;
        }
    }

    function creator_fee_get(uint256 _price, uint256 _creator_fee_rate)pure internal returns (uint256){
        if(_creator_fee_rate > 0){
            return _price * _creator_fee_rate / 10000;
        }else{
            return 0;
        }
    }

    function orderBuy(
        bytes32 _tokenId, 
        uint256 _price, 
        address _seller, 
        address _buyer, 
        uint256 _timestamp, 
        uint256 _discount,
        address _creator,
        uint256 _creator_fee_rate,
        bytes memory _signature,
        string memory _c_name
    ) public payable nonReentrant{
        require(_seller != address(0x0), "Error: invalid seller");
        require(msg.sender == _buyer, "Error: invilad buyer");
        require((block.timestamp - _timestamp) < timeFrame, "Error: expired signature");
        require((block.timestamp - orders_sign_time[_tokenId]) > orders_time_range, "Error: invalid signature time"); 
        require(_price == msg.value, "Error: invalid amount");
        require(ethscriptions_signatures[_tokenId][_timestamp] != true, "Error: invalid signature");
        require(_discount < 100, "Error: invalid discount");
        require(_creator_fee_rate < 20, "Error: invalid creator_fee_rate");

        {
            address signer = ECDSAUpgradeable.recover(getEthscripHash(_buyer, _seller, _tokenId, _price, _timestamp, _discount, _creator, _creator_fee_rate), _signature);
            require(signer == authorized_verifier,"Error: invalid signature");
        }
        {
            uint256 creator_fee = _creator != address(0x0) ? creator_fee_get(_price,_creator_fee_rate) : 0;
            payable(_creator).transfer(creator_fee);
            payable(_seller).transfer(_price - protocel_fee_get(_price,_discount) - creator_fee);
        }
       
        emit ethscriptions_protocol_TransferEthscriptionForPreviousOwner(_seller, _buyer, _tokenId);
        ethscriptions_signatures[_tokenId][_timestamp] = true;
        orders_sign_time[_tokenId] = _timestamp;

        emit OrderBought(_tokenId, _seller, _c_name, _buyer, _price, _timestamp);
        emit ethscriptions_protocol_TransferEthscription(_buyer, _tokenId);
    }


    function getEthscripHash(
        address _buyer, 
        address _seller, 
        bytes32 _e_id, 
        uint256 _price, 
        uint256 _timestamp, 
        uint256 _discount, 
        address _creator, 
        uint256 _creator_fee_rate
    ) internal pure returns (bytes32) {
        bytes32 message = keccak256(abi.encodePacked(_buyer, _seller, _e_id, _price, _timestamp, _discount, _creator, _creator_fee_rate));
        bytes32 ethSignedMessage = ECDSAUpgradeable.toEthSignedMessageHash(message);
        return ethSignedMessage;
    }

    function getEthscripHash_withdrawn(
        address _from, 
        bytes32 _e_id, 
        uint256 _timestamp
    ) internal pure returns (bytes32) {
        bytes32 message = keccak256(abi.encodePacked(_from, _e_id, _timestamp));
        bytes32 ethSignedMessage = ECDSAUpgradeable.toEthSignedMessageHash(message);
        return ethSignedMessage;
    }

    function withdrawn(
        address _from, 
        bytes32 _e_id, 
        uint256 _timestamp, 
        bytes memory _signature,
        string memory _c_name
    ) public nonReentrant{
        require((block.timestamp - _timestamp) < timeFrame, "Error: Expired signature");
        require((block.timestamp - orders_sign_time[_e_id]) > orders_time_range, "Error: invalid signature time"); 
        require(ethscriptions_signatures[_e_id][_timestamp] != true, "Error: invalid signature");

        bytes32 messageHash = getEthscripHash_withdrawn(_from, _e_id, _timestamp);
        address signer = ECDSAUpgradeable.recover(messageHash, _signature);
        require(signer == authorized_verifier,"Error: invalid signature");
        require(msg.sender == _from, "Error: No permissions");

        emit ethscriptions_protocol_TransferEthscriptionForPreviousOwner(_from, msg.sender, _e_id);

        ethscriptions_signatures[_e_id][_timestamp] = true;
        orders_sign_time[_e_id] = _timestamp;

        emit Orderwithdrawn(_e_id, msg.sender, _timestamp, _c_name);
        emit ethscriptions_protocol_TransferEthscription(msg.sender, _e_id);
    }

    function send_protocol_fee(address _rec) external onlyOwner{
        require(address(this).balance != 0, "Error: invalid balance");
        require(_rec != address(0x0), "Error: invalid address");
        payable(_rec).transfer(address(this).balance);        
    }

    fallback() external {}

    function _authorizeUpgrade(address newImplementation)
        internal
        onlyOwner
        override
    {}

}
