// File: contracts/TeamZ.sol






pragma solidity >=0.8.17 <0.9.0;

error IsNotTokenOwnerNorApproved();
error AlreadyClaim();
error CallerIsContract();
error CannotMintFromContract();
error InsufficientFunds();
error InvalidMintAmount();
error InvalidMintPriceChange();
error InvalidSalesPhaseChange();
error InvalidWithdrawalAmount();
error MintAmountExceedsSupply();
error MintAmountFreeExceedsSupply();
error WithdrawalFailed();
error WrongSalesPhase();
error InvalidMerkleProof();
error MintAmountExceedsUserAllowance();

contract TeanZ is ERC721A, Ownable {
    using SafeERC20 for IERC20;
    event SetURI(
        string indexed uri
    );
    event Burn(
        uint256 indexed tokenId
    );
    uint256 public  TICKET_SUPPLY =1000;
    mapping(uint48=>uint256) private _EPRICE;
    mapping(uint48=>uint256) private _PRICE;
    uint256 public BIRDTIME = 0;
    address public FeeRecipient = 0xFed48472012bddcDE18E076e6D171Df41a394008;
    address public USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;
    uint48 public VIP = 5;
    uint48 public RED = 4;
    uint48 public BUS =3;
    uint48 public EXP =1;
    uint48 public GEN =2;
    uint256 public TICKET_SUPPLYED =0;
    string public METADATA_URL;
    constructor() ERC721A("TEAMZ 2024 WEB3 ticket", "TeamZ 2024") {}

    //modifiers
    modifier callerIsUser() {
        if (tx.origin != msg.sender) revert CallerIsContract();
        _;
    }
    modifier mintPublic(uint48 _mintAmount,uint8 _mintType) {
            require(
            TICKET_SUPPLYED + _mintAmount <= TICKET_SUPPLY,
            "Vip supply exceeded!"
            );
            _;
            require(_mintType<=VIP,
                "error mint type "
            );
    }

    function publicMint(uint48 _mintAmount,uint8 _mintType)
        external
        callerIsUser
        mintPublic(_mintAmount,_mintType)
    {
        if (_mintAmount == 0) revert InvalidMintAmount();
        uint256 Price = getPrice(_mintType);
        uint256 FeeAmount =Price * _mintAmount;
        require(FeeAmount > 0,"error Fee" );
        require(IERC20(USDC).allowance(msg.sender,address(this))>=FeeAmount,"Insufficient authorization limit");
        IERC20(USDC).safeTransferFrom(msg.sender, FeeRecipient, FeeAmount);
        _mint(msg.sender, _mintAmount,_mintType);
        TICKET_SUPPLYED += _mintAmount;
    }
    function getPrice(uint8 _mintType)
        public view returns (uint256 ){
            if (block.timestamp<BIRDTIME){
                return _EPRICE[_mintType];
            }else {
                return _PRICE[_mintType];
            }

    }
    // onlyOwner functions

    function devMint(address _to, uint48 _numberOfTokens,uint8 _mintType)
        external 
        onlyOwner 
    {     
        require(_mintType<=VIP,
                "error mint type ");
        _mint(_to, _numberOfTokens,_mintType);
    }

    function setURI(string calldata _uri) 
        external 
        onlyOwner 
    {
        METADATA_URL = _uri;
        emit SetURI(_uri);
    }
    function setBirdTime(uint256 _mintTime) 
        external 
        onlyOwner 
    {
        BIRDTIME = _mintTime;
    }
       function setApproveTime(uint256 _time) 
        external 
        onlyOwner 
    {
        _setTransfer(_time);
    }
    function setTransferAllowList(address _address,bool isAllow ) 
        external 
        onlyOwner 
    {
        _setAllowList(_address,isAllow);
    }
     function setPrice(uint48 _mintType,uint256 _mintPrice, uint256 _mintEPrice) 
        external 
        onlyOwner 
    {
        _EPRICE[_mintType] = _mintEPrice;
        _PRICE[_mintType] = _mintPrice;
    }
     function setFeeRecipient(address _feeRecipient) 
        external 
        onlyOwner 
    {
        FeeRecipient = _feeRecipient;
    }
         
    function setUSDT(address _USDT) 
        external 
        onlyOwner 
    {
        USDC = _USDT;
    }    

    function withdraw(uint256 _amount, address _to) 
        external 
        onlyOwner 
    {
        uint256 contractBalance = address(this).balance;
        if (contractBalance < _amount) revert InvalidWithdrawalAmount();

        (bool success,) = payable(_to).call{value: _amount}("");
        if (!success) revert WithdrawalFailed();
    }

    function burn(uint256 _tokenId) 
        external
        onlyOwner 
    {
        if(!_isApprovedOrOwner(_msgSender(), _tokenId)) revert IsNotTokenOwnerNorApproved();
        _burn(_tokenId, true);
        emit Burn(_tokenId);
    }

    function _isApprovedOrOwner(address spender, uint256 tokenId) internal view virtual returns (bool) {
        address owner = ERC721A.ownerOf(tokenId);
        return (spender == owner || isApprovedForAll(owner, spender) || getApproved(tokenId) == spender);
    }

    function _baseURI() 
        internal 
        view 
        virtual 
        override 
        returns (string memory) 
    {
        return METADATA_URL;
    }
    function _type(uint256 _tokenId)
        internal
        view
        virtual
        returns (uint8 )
        {
            return getTypes(_tokenId);
        }
}
