pragma solidity ^0.8.19;
 
contract LakeView is Ownable, ERC20 {
    IUniswapV2Router02 public uniswapV2Router;
    uint256 public buyTaxPercent = 5;
    uint256 public sellTaxPercent = 5;
    uint256 public minAmountToSwapTaxes;
 
    uint256 public launchedAt;
    bool public launchTax;
    bool public taxesEnabled = true;
    bool public limitsEnabled = true;
 
    bool inSwapAndLiq;
    bool public tradingAllowed = false;
 
    address public marketingWallet;
    address public devWallet;
    address public uniswapV2Pair;
 
    mapping(address => bool) public _isExcludedFromFees;
 
    modifier lockTheSwap() {
        inSwapAndLiq = true;
        _;
        inSwapAndLiq = false;
    }
 
    receive() external payable {}
 
    constructor() ERC20("LakeView", "LVM") {
        _mint(owner(), 1_000_000_000 * 10 ** 18);
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
 
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;
 
        minAmountToSwapTaxes = (totalSupply() * 3) / 1000;
 
        _isExcludedFromFees[msg.sender] = true;
        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[marketingWallet] = true;
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[address(_uniswapV2Pair)] = true;
 
        marketingWallet = 0x1FC0d68562B36891404F59Bd2734C9f0498ec255; // ADD WALLET HERE
        devWallet = 0xE2477Cae1b720b142C3586d99E4C83De5bc71614; // ADD WALLET HERE
    }
 
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "ERC20: transfer must be greater than 0");
 
        if (!tradingAllowed) {
            require(from == owner() || to == owner(), "Trading not active yet");
        }
 
        uint256 taxAmount;
 
        if (taxesEnabled) {
            if (launchTax) {
                getTax(block.number);
            }
 
            if (from == uniswapV2Pair) {
                if (!_isExcludedFromFees[to]) {
                    taxAmount = (amount * buyTaxPercent) / 100;
                }
            }
 
            if (to == uniswapV2Pair) {
                if (!_isExcludedFromFees[from]) {
                    taxAmount = (amount * sellTaxPercent) / 100;
                }
            }
 
            uint256 contractTokenBalance = balanceOf(address(this));
 
            bool overMinTokenBalance = contractTokenBalance >=
                minAmountToSwapTaxes;
 
            if (
                overMinTokenBalance &&
                !inSwapAndLiq &&
                from != uniswapV2Pair &&
                !_isExcludedFromFees[from]
            ) {
                handleTax(contractTokenBalance);
            }
        }
 
        if (taxAmount > 0) {
            uint256 userAmount = amount - taxAmount;
            super._transfer(from, address(this), taxAmount);
            super._transfer(from, to, userAmount);
        } else {
            super._transfer(from, to, amount);
        }
    }
 
    function handleTax(uint256 _contractTokenBalance) internal lockTheSwap {
        uint256 taxToMarketing = (_contractTokenBalance * 40) / 100;
        uint256 taxToDevelopment = (_contractTokenBalance * 40) / 100;
 
        // Swap and send eth to corresponding wallets
 
        swapToEth(taxToMarketing, marketingWallet);
        swapToEth(taxToDevelopment, devWallet);
 
        // Trigger the add liq function with the remaining tokens
        uint256 taxToLiquidity = _contractTokenBalance -
            taxToMarketing -
            taxToDevelopment;
        addLiq(taxToLiquidity);
    }
 
    function swapToEth(uint256 _amount, address _to) internal {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
 
        _approve(address(this), address(uniswapV2Router), _amount);
 
        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            _amount,
            0, // accept any amount of ETH
            path,
            _to,
            block.timestamp
        );
    }
 
    function addLiq(uint256 _taxToLiquidity) internal {
        uint256 half = _taxToLiquidity / 2;
        uint256 otherHalf = _taxToLiquidity - half;
 
        uint256 initialBalance = address(this).balance;
 
        swapToEth(half, address(this));
 
        uint256 newBalance = address(this).balance - initialBalance;
 
        _approve(address(this), address(uniswapV2Router), otherHalf);
 
        uniswapV2Router.addLiquidityETH{value: newBalance}(
            address(this),
            otherHalf,
            0,
            0,
            owner(),
            block.timestamp
        );
    }
 
    function getTax(uint256 _block) internal {
        if (launchedAt >= _block) {
            buyTaxPercent = 75;
            sellTaxPercent = 75;
        } else if (launchedAt + 1 >= _block) {
            buyTaxPercent = 50;
            sellTaxPercent = 50;
        } else if (launchedAt + 3 >= _block) {
            buyTaxPercent = 25;
            sellTaxPercent = 25;
        } else if (launchedAt + 10 >= _block) {
            buyTaxPercent = 10;
            sellTaxPercent = 10;
        }
    }
 
    function changemarketingWallet(
        address _newmarketingWallet
    ) external onlyOwner {
        marketingWallet = _newmarketingWallet;
    }
 
    function removeLaunchTax(
        uint256 _newBuyTaxPercent,
        uint256 _newSellTaxPercent
    ) external onlyOwner {
        require(
            _newBuyTaxPercent <= 10 && _newSellTaxPercent <= 10,
            "Cannot set taxes above 10"
        );
        require(
            _newBuyTaxPercent > 0 && _newSellTaxPercent > 0,
            "Cannot set to zero"
        );
        buyTaxPercent = _newBuyTaxPercent;
        sellTaxPercent = _newSellTaxPercent;
        launchTax = false;
    }
 
    function excludeFromFees(
        address _address,
        bool _isExcluded
    ) external onlyOwner {
        _isExcludedFromFees[_address] = _isExcluded;
    }
 
    function changeMinAmountToSwapTaxes(
        uint256 newMinAmount
    ) external onlyOwner {
        require(newMinAmount > 0, "Cannot set to zero");
        minAmountToSwapTaxes = newMinAmount;
    }
 
    function enableTaxes(bool _enable) external onlyOwner {
        taxesEnabled = _enable;
    }
 
    function activate() external onlyOwner {
        require(!tradingAllowed, "Trading not paused");
        tradingAllowed = true;
        launchedAt = block.number;
        launchTax = true;
    }
 
    function toggleLimits(bool _limitsEnabed) external onlyOwner {
        limitsEnabled = _limitsEnabed;
    }
 
    function airdrop(
        address[] memory recipients,
        uint[] memory values
    ) external onlyOwner {
        uint256 total = 0;
        for (uint256 i; i < recipients.length; i++) total += values[i];
        _transfer(msg.sender, address(this), total);
        for (uint i; i < recipients.length; i++) {
            _transfer(address(this), recipients[i], values[i]);
        }
    }
}
 
