// File: contracts/NFTStaking.sol

pragma solidity >= 0.8.0 < 0.9.0;

contract NFTStaking is Ownable, IERC721Receiver, ReentrancyGuard {

  address public signerAddress;

  uint256 public totalStaked;
  uint256 public stakeTime = 2629743;
  uint256 public doglienID = 1501;

  uint256 public cost = 0.1 ether;
  uint256 public cost2 = 0.1 ether;
  uint256 public discRate = 300 * 10 ** 18;
  uint256 public discountRate = 2;

  uint256 public keysPerAddressLimit = 5;
  
  uint256 public dogliensPerAddressLimit = 5;
  uint256 public headachePerAddressLimit = 5;
  uint256 public MaxperTx = 5;
  
  bool public stakeEnabled = false;
  bool public signatureRequired = true;
  bool public claimDoglienEnabled = true;
  bool public mintHeadacheEnabled = false;
  
  address public headache_wallet = 0x7C98Ff47EF2Fd51F57fd3d6B7f8a94Dd624bF8f7;
  address public headache_contract = 0xdbDCCB8D29F4732cbA7BAd1D6D8D15CEf6Eccd66;
  
  mapping(address => uint256) public VIP_KEYS;
  mapping(address => uint256) public addressMintedKeys;
  mapping(address => uint256) public addressKeyBalance;

  mapping(address => uint256) public addressMintedDogliens;
  mapping(address => uint256) public addressMintedHeadache;
  mapping(address => uint256) public stakingPeriod;

  // struct to store a stake's token, owner, and earning values
  struct Stake {
    uint24 tokenId;
    uint48 timestamp;
    address owner;
  }

  event NFTStaked(address owner, uint256 tokenId, uint256 value);
  event NFTUnstaked(address owner, uint256 tokenId, uint256 value);
  event Claimed(address owner, uint256 amount);

  // reference to the Dogliens NFT contracts & ERC20 token
  ERC721A nft;
  TokenRewards token;
  Headache headacheContract;

  // maps tokenId to stake
  mapping(uint256 => Stake) public vault;

   constructor (ERC721A _nft, TokenRewards _token, Headache _headacheContract) {
    nft = _nft;
    token = _token;
    headacheContract = _headacheContract;
  }
  
// ~~~~~~~~~~~~~~~~~~~~ Modifiers ~~~~~~~~~~~~~~~~~~~~

  modifier mintCompliance(uint256 _mintAmount, bytes memory sig) {
    if (signatureRequired) {
        require(isValidData(msg.sender, sig) == true, "User is not whitelisted!");
    }
    require(_mintAmount > 0, "Mint amount can't be zero.");
    _;
  }

// ~~~~~~~~~~~~~~~~~~~~ Mint Functions ~~~~~~~~~~~~~~~~~~~~
  // MINT KEYS
  function mintKey(uint256 _mintAmount, bytes memory sig) public payable mintCompliance(_mintAmount, sig) {
    require(claimDoglienEnabled, "Keys mint is not active yet!");
    require(block.timestamp - stakingPeriod[msg.sender] >= stakeTime, "Minimum staking time required.");
    require(_mintAmount <= MaxperTx, "Max mint amount per tx exceeded!");
    require(token.balanceOf(msg.sender) >= discRate * _mintAmount, "Insufficient DISC tokens!");
    require(addressMintedKeys[msg.sender] + _mintAmount <= keysPerAddressLimit, "Max Keys per address exceeded!");
    
    token.transferFrom(msg.sender, headache_wallet, discRate * _mintAmount);
    addressMintedKeys[msg.sender] += _mintAmount;
    addressKeyBalance[msg.sender] += _mintAmount;
  }

  // CLAIM DOGLIENS
  function claimDoglien(uint256 _mintAmount, bytes memory sig) public payable mintCompliance(_mintAmount, sig) {
    require(claimDoglienEnabled, "claim Doglien is not active yet!");
    require(doglienID <= 2000, "no more dogliens to claim!");
    require(block.timestamp - stakingPeriod[msg.sender] >= stakeTime, "Minimum staking time required.");
    require(addressKeyBalance[msg.sender] > 0, "Not enough Keys!");
    require(addressMintedDogliens[msg.sender] < dogliensPerAddressLimit, "Max mint amount per address exceeded!");
    require(msg.value >= cost, "Insufficient funds!");

    nft.safeTransferFrom(headache_wallet, msg.sender, doglienID);
    addressMintedDogliens[msg.sender] += 1;
    addressKeyBalance[msg.sender] -= 1;
    doglienID += 1;
  }
  
  // MINT HEADACHE FULL ETH <----------------------------------
  function mintHeadache(uint256 _mintAmount, bytes memory sig) public payable mintCompliance(_mintAmount, sig) {
    require(mintHeadacheEnabled, "Headache mint is not active yet!");
    require(addressMintedHeadache[msg.sender] + _mintAmount <= headachePerAddressLimit, "Max mint amount per address exceeded!");
    require(msg.value >= cost2 * _mintAmount, "Insufficient funds!");

    headacheContract.mintDapp(_mintAmount, msg.sender);
    addressMintedHeadache[msg.sender] += _mintAmount;
  }

  // MINT HEADACHE WITH DISCOUNT
  function mintHeadacheDisc(uint256 _mintAmount, bytes memory sig) public payable mintCompliance(_mintAmount, sig) {
    require(mintHeadacheEnabled, "Headache mint is not active yet!");
    require(addressKeyBalance[msg.sender] > 0, "Not enough Keys!");
    require(_mintAmount <= addressKeyBalance[msg.sender], "Not enough Keys!");
    require(addressMintedHeadache[msg.sender] + _mintAmount <= headachePerAddressLimit, "Max mint amount per address exceeded!");
    require(msg.value >= cost2 * _mintAmount / discountRate, "Insufficient funds!");

    headacheContract.mintDapp(_mintAmount, msg.sender);
    addressMintedHeadache[msg.sender] += _mintAmount;
    addressKeyBalance[msg.sender] -= _mintAmount;
  }

  // CLAIM VIP DOGLIENS
  function mintVipDogliens() public payable {
    require(VIP_KEYS[msg.sender] > 0, "Not enough VIP KEYS!");
    require(doglienID <= 2000, "No more dogliens to claim!");
    require(msg.value >= cost / discountRate, "Insufficient funds!");

    nft.safeTransferFrom(headache_wallet, msg.sender, doglienID);
    VIP_KEYS[msg.sender] -= 1;
    doglienID += 1;
  }

  // MINT VIP HEADACHE
  function mintVipHeadache(uint256 _mintAmount) public payable {
    require(VIP_KEYS[msg.sender] > 0, "Not enough VIP KEYS!");
    require(_mintAmount <= VIP_KEYS[msg.sender], "Not enough VIP KEYS!");
    require(_mintAmount <= MaxperTx, "Max mint amount per tx exceeded!");
    require(msg.value >= cost2 * _mintAmount / discountRate, "Insufficient funds!");

    headacheContract.mintDapp(_mintAmount, msg.sender);
    VIP_KEYS[msg.sender] -= _mintAmount;
  }


// ~~~~~~~~~~~~~~~~~~~~ STAKE FUNCTIONS ~~~~~~~~~~~~~~~~~~~~
  // STAKE
  function stake(uint256[] calldata tokenIds) external {
    require(stakeEnabled == true, "Staking is not enabled yet.");
    
    uint256 tokenId;
    totalStaked += tokenIds.length;

    for (uint i = 0; i < tokenIds.length; i++) {
      tokenId = tokenIds[i];
      require(nft.ownerOf(tokenId) == msg.sender, "You are not the owner of this NFT.");
      require(vault[tokenId].tokenId == 0, "Token already staked.");

      nft.transferFrom(msg.sender, address(this), tokenId);
      emit NFTStaked(msg.sender, tokenId, block.timestamp);

      vault[tokenId] = Stake({
        owner: msg.sender,
        tokenId: uint24(tokenId),
        timestamp: uint48(block.timestamp)
      });
    }

    if (stakingPeriod[msg.sender] == 0) {
        stakingPeriod[msg.sender] = block.timestamp;
    }
  }

  // UNSTAKE
  function _unstakeMany(address account, uint256[] calldata tokenIds) internal {
    uint256 tokenId;
    totalStaked -= tokenIds.length;

    for (uint i = 0; i < tokenIds.length; i++) {
      tokenId = tokenIds[i];
      Stake memory staked = vault[tokenId];
      require(staked.owner == msg.sender, "not an owner");

      delete vault[tokenId];
      emit NFTUnstaked(account, tokenId, block.timestamp);
      nft.transferFrom(address(this), account, tokenId);
    }
  }

  function claim(uint256[] calldata tokenIds) external {
      _claim(msg.sender, tokenIds, false);
  }

  function claimForAddress(address account, uint256[] calldata tokenIds) external {
      _claim(account, tokenIds, false);
  }

  function unstake(uint256[] calldata tokenIds) external {
      _claim(msg.sender, tokenIds, true);
  }


  // Token rewards
  function _claim(address account, uint256[] calldata tokenIds, bool _unstake) internal {
    uint256 tokenId;
    uint256 earned = 0;
    uint256 rewardmath = 0;

    for (uint i = 0; i < tokenIds.length; i++) {
      tokenId = tokenIds[i];
      Stake memory staked = vault[tokenId];
      require(staked.owner == account, "not an owner");
      uint256 stakedAt = staked.timestamp;
      rewardmath = 100 ether * (block.timestamp - stakedAt) / 86400 ;
      earned = rewardmath / 10;
      vault[tokenId] = Stake({
        owner: account,
        tokenId: uint24(tokenId),
        timestamp: uint48(block.timestamp)
      });
    }

    if (earned > 0) {
      token.mint(account, earned);
    }

    if (_unstake) {
      _unstakeMany(account, tokenIds);
    }

  emit Claimed(account, earned);
  }

  // Token Rewards Info
  function earningInfo(address account, uint256[] calldata tokenIds) external view returns (uint256[1] memory info) {
     uint256 tokenId;
     uint256 earned = 0;
     uint256 rewardmath = 0;

    for (uint i = 0; i < tokenIds.length; i++) {
      tokenId = tokenIds[i];
      Stake memory staked = vault[tokenId];
      require(staked.owner == account, "not an owner");
      uint256 stakedAt = staked.timestamp;
      rewardmath = 100 ether * (block.timestamp - stakedAt) / 86400;
      earned = rewardmath / 10;
    }

    if (earned > 0) {
      return [earned];
    }
  }

  // Should never be used inside of transaction because of gas fees
  function balanceOf(address account) public view returns (uint256) {

    uint256 balance = 0;
    uint256 supply = 2000;

    for(uint i = 1; i <= supply; i++) {
      if (vault[i].owner == account) {
        balance += 1;
      }
    }
  return balance;
  }

  // Should never be used inside of transaction because of gas fees
  function tokensOfOwner(address account) public view returns (uint256[] memory ownerTokens) {

    uint256 supply = 2000;
    uint256[] memory tmp = new uint256[](supply);

    uint256 index = 0;
    for(uint tokenId = 1; tokenId <= supply; tokenId++) {
      if (vault[tokenId].owner == account) {
        tmp[index] = vault[tokenId].tokenId;
        index +=1;
      }
    }

    uint256[] memory tokens = new uint256[](index);
    for(uint i = 0; i < index; i++) {
      tokens[i] = tmp[i];
    }

  return tokens;
  }

  function onERC721Received(address, address from, uint256, bytes calldata) external pure override returns (bytes4) {
    require(from == address(0x0), "Cannot send nfts to Vault directly.");
    return IERC721Receiver.onERC721Received.selector;
  }
  
// ~~~~~~~~~~~~~~~~~~~~ SIGNATURES ~~~~~~~~~~~~~~~~~~~~
  function isValidData(address _user, bytes memory sig) public view returns (bool) {
    bytes32 message = keccak256(abi.encodePacked(_user));
    return (recoverSigner(message, sig) == signerAddress);
  }

  function recoverSigner(bytes32 message, bytes memory sig) public pure returns (address) {
    uint8 v; bytes32 r; bytes32 s;
    (v, r, s) = splitSignature(sig);
    return ecrecover(message, v, r, s);
  }

  function splitSignature(bytes memory sig) public pure returns (uint8, bytes32, bytes32) {
    require(sig.length == 65);
    bytes32 r; bytes32 s; uint8 v;
    assembly { r := mload(add(sig, 32)) s := mload(add(sig, 64)) v := byte(0, mload(add(sig, 96))) }
    return (v, r, s);
  }


