    "contracts/protocol/libraries/services/DebtService.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Arrays} from "@openzeppelin/contracts/utils/Arrays.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {IGToken} from "../../../interfaces/IGToken.sol";
import {ISToken} from "../../../interfaces/ISToken.sol";
import {IDToken} from "../../../interfaces/IDToken.sol";
import {Types} from "../types/Types.sol";
import {ExchequerService} from "./ExchequerService.sol";
import {StrategusService} from "./StrategusService.sol";

library DebtService {
	using ExchequerService for Types.Exchequer;
	using SafeCast for uint256; 

	event CreateLineOfCredit(
		uint128 indexed id,
		uint128 rate,
		address indexed borrower,
		address indexed exchequer,
		uint256 borrowMax,
		uint40 expirationTimestamp
	);

	event OriginationFee(
		uint128 indexed id,
		address indexed borrower,
		address indexed exchequer,
		uint256 borrowMax,
		uint256 feeAmount
	);

	event Borrow(
		uint128 indexed lineOfCreditId,
		uint128 rate,
		address indexed borrower,
		address indexed exchequer,
		uint256 amount
	);

	event Repay(
		uint128 indexed lineOfCreditId,
		address indexed borrower,
		address indexed exchequer,
		uint256 amount
	);

	event Delinquent(
		uint128 indexed lineOfCreditId,
		address indexed borrower,
		address indexed exchequer,
		uint256 remainingBalance,
		uint40 expirationTimestamp
	);

	event CloseLineOfCredit(
		uint128 indexed lineOfCreditId,
		address indexed borrower,
		address indexed exchequer,
		uint40 expirationTimestamp
	);

	function createLineOfCredit(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		uint256 linesOfCreditCount,
		address borrower,
		address underlyingAsset,
		uint256 borrowMax,
		uint128 rate,
		uint40 termDays
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		uint256 protocolBorrowFee = exchequer.calculateProtocolFee(borrowMax);
		StrategusService.guardCreateLineOfCredit(
			exchequer,
			linesOfCredit,
			underlyingAsset,
			borrower,
			borrowMax,
			protocolBorrowFee
		);
		linesOfCredit[borrower].underlyingAsset = underlyingAsset;
		IDToken(exchequer.dTokenAddress).updateRate(borrower, rate);
		linesOfCredit[borrower].creationTimestamp = uint40(block.timestamp);
		linesOfCredit[borrower].expirationTimestamp = uint40(block.timestamp + termDays * 1 days);
		linesOfCredit[borrower].id = uint128(linesOfCreditCount + 1);
		linesOfCredit[borrower].deliquent = false;		
		linesOfCredit[borrower].borrowMax = borrowMax;
		exchequer.totalDebt += linesOfCredit[borrower].borrowMax;
		ISToken(exchequer.sTokenAddress).transferOnOrigination(borrower, protocolBorrowFee);
		emit OriginationFee(
			linesOfCredit[borrower].id,
			borrower,
			underlyingAsset,
			borrowMax,
			protocolBorrowFee
		);

		emit CreateLineOfCredit(
			linesOfCredit[borrower].id,
			rate,
			borrower,
			underlyingAsset,
			borrowMax,
			linesOfCredit[borrower].expirationTimestamp
		);
	}

	function borrow(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address underlyingAsset,
		uint256 amount
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		uint128 rate = IDToken(exchequer.dTokenAddress).userRate(msg.sender);
		exchequer.update();
		StrategusService.guardBorrow(
			exchequer,
			linesOfCredit,
			msg.sender,
			amount
		);
		IDToken(exchequer.dTokenAddress).mint(
			msg.sender,
			amount
		);
		exchequer.updateSupplyRate();
		ISToken(exchequer.sTokenAddress).transferUnderlying(msg.sender, amount);
		emit Borrow(
			linesOfCredit[msg.sender].id,
			rate,
			msg.sender,
			underlyingAsset,
			amount
		);
	}

	function repay(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address underlyingAsset,
		uint256 amount
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		exchequer.update();

		StrategusService.guardRepay(
			exchequer,
			linesOfCredit,
			msg.sender,
			amount
		);
		IDToken(exchequer.dTokenAddress).burn(
			msg.sender,
			amount
		);
		IERC20(underlyingAsset).transferFrom(msg.sender, exchequer.sTokenAddress, amount);
		exchequer.updateSupplyRate();
		linesOfCredit[msg.sender].lastRepaymentTimestamp = uint40(block.timestamp);
		emit Repay(
			linesOfCredit[msg.sender].id,
			msg.sender,
			underlyingAsset,
			amount
		);
	}

	function markDelinquent(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address borrower,
		address underlyingAsset
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		StrategusService.guardDelinquency(
			exchequer,
			linesOfCredit,
			borrower
		);
		uint256 remainingBalance = IDToken(exchequer.dTokenAddress).balanceOf(borrower);
		// uint256 userBalance = ISToken(exchequer.sTokenAddress).balanceOf(borrower);
		linesOfCredit[borrower].deliquent = true;
		// handle liquidation here
		// address exchequerSafe = ISToken(exchequer.sTokenAddress).getExchequerSafe();
		// if (remainingBalance > userBalance) {
		// 	ISToken(exchequer.sTokenAddress).transferOnLiquidation(
		// 		borrower,
		// 		exchequerSafe,
		// 		userBalance
		// 	);
		// 	ISToken(exchequer.sTokenAddress).transferUnderlying(
		// 		exchequer.sTokenAddress, 
		// 		userBalance
		// 	);
		// 	IDToken(exchequer.dTokenAddress).burn(
		// 		borrower,
		// 		userBalance
		// 	);
		// } else {
		// 	ISToken(exchequer.sTokenAddress).transferOnLiquidation(
		// 		borrower,
		// 		exchequerSafe,
		// 		remainingBalance
		// 	);
		// 	ISToken(exchequer.sTokenAddress).transferUnderlying(
		// 		exchequer.sTokenAddress, 
		// 		remainingBalance
		// 	);
		// 	IDToken(exchequer.dTokenAddress).burn(
		// 		borrower,
		// 		remainingBalance
		// 	);
		// }

		// uint256 postRemainingBalance = IDToken(exchequer.dTokenAddress).balanceOf(borrower);
		// uint256 grantCollateralBalance = IERC20(underlyingAsset).balanceOf(exchequer.gTokenAddress);

		// if (postRemainingBalance > grantCollateralBalance) {
		// 	IERC20(underlyingAsset).transferFrom(
		// 		exchequer.gTokenAddress, 
		// 		exchequer.sTokenAddress, 
		// 		grantCollateralBalance
		// 	);
		// } else {
		// 	IERC20(underlyingAsset).transferFrom(
		// 		exchequer.gTokenAddress, 
		// 		exchequer.sTokenAddress, 
		// 		postRemainingBalance
		// 	);
		// }

		emit Delinquent(
			linesOfCredit[borrower].id,
			borrower,
			underlyingAsset,
			remainingBalance,
			linesOfCredit[borrower].expirationTimestamp
		);
	}

	function closeLineOfCredit(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address borrower,
		address underlyingAsset
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		if (exchequer.totalDebt <= linesOfCredit[borrower].borrowMax) {
			exchequer.totalDebt = 0;
		} else {
			exchequer.totalDebt -= linesOfCredit[borrower].borrowMax;
		}

		uint128 logId = linesOfCredit[borrower].id;
		uint40 logExpirationTimestamp = linesOfCredit[borrower].expirationTimestamp;
		delete linesOfCredit[borrower];
		emit CloseLineOfCredit(
			logId,
			borrower,
			underlyingAsset,
			logExpirationTimestamp
		);
	}
}"
    },
    "contracts/protocol/libraries/services/ExchequerService.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {AddressUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {ISToken} from "../../../interfaces/ISToken.sol";
import {IDToken} from "../../../interfaces/IDToken.sol";
import {MathUtils} from "../math/MathUtils.sol";
import {WadRayMath} from "../math/WadRayMath.sol";
import {Types} from "../types/Types.sol";
import {DebtService} from "./DebtService.sol";
import {StrategusService} from "./StrategusService.sol";


library ExchequerService {
	using WadRayMath for uint256;
	using SafeCast for uint256;
	using ExchequerService for Types.Exchequer;

	function addExchequer(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(uint256 => address) storage exchequersList,
		address underlyingAsset,
		address sTokenAddress,
		address dTokenAddress,
		address gTokenAddress,
		uint8 decimals,
		uint256 protocolBorrowFee,
		uint16 exchequersCount,
		uint16 maxExchequersCount
	) internal returns (bool) {
		require(AddressUpgradeable.isContract(underlyingAsset), "NOT_CONTRACT");
		require(exchequers[underlyingAsset].sTokenAddress == address(0), "EXCHEQUER_ALREADY_INITIALIZED");

		exchequers[underlyingAsset].supplyIndex = uint128(WadRayMath.RAY);
		exchequers[underlyingAsset].collateralFactor = WadRayMath.RAY;
		exchequers[underlyingAsset].sTokenAddress = sTokenAddress;
		exchequers[underlyingAsset].dTokenAddress = dTokenAddress;
		exchequers[underlyingAsset].gTokenAddress = gTokenAddress;
		exchequers[underlyingAsset].decimals = decimals;

		bool exchequerAlreadyAdded = exchequers[underlyingAsset].id != 0 ||
			exchequersList[0] == underlyingAsset;
		require(!exchequerAlreadyAdded, "EXCHEQUER_ALREADY_ADDED");

		for (uint16 i = 0; i < exchequersCount; i++) {
			if (exchequersList[i] == address(0)) {
				exchequers[underlyingAsset].id = i;
				exchequersList[i] = underlyingAsset;
				return false;
			}
		}

		require(exchequersCount < maxExchequersCount, "NO_MORE_RESERVES_ALLOWED");
		exchequers[underlyingAsset].id = exchequersCount;
		exchequersList[exchequersCount] = underlyingAsset;
		exchequers[underlyingAsset].protocolBorrowFee = protocolBorrowFee;
		exchequers[underlyingAsset].active = true;
		return true;
	}

	function deleteExchequer(
		mapping(address => Types.Exchequer) storage exchequers,
		mapping(uint256 => address) storage exchequersList,
		address underlyingAsset
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		StrategusService.guardDeleteExchequer(exchequersList, exchequer, underlyingAsset);
		exchequersList[exchequers[underlyingAsset].id] = address(0);
		delete exchequers[underlyingAsset];
	}

	function update(Types.Exchequer storage exchequer) internal {
		_updateIndexes(exchequer);
		// _accrueToExchequerSafe();
		// create accrual to exchequerSafe if desired

	}

	function _updateIndexes(Types.Exchequer storage exchequer) internal {
		// actually create logic to update indexes and related state based on repayments
		uint nextSupplyIndex = exchequer.supplyIndex;

		if (exchequer.supplyRate != 0) {
			uint256 cumulatedInterest = MathUtils.calculateLinearInterest(
				exchequer.supplyRate,
				exchequer.lastUpdateTimestamp
			);
			exchequer.supplyIndex = uint128(cumulatedInterest.rayMul(nextSupplyIndex));
		}
		exchequer.lastUpdateTimestamp = uint40(block.timestamp);
	}

	// function _accrueToExchequerSafe(Types.Exchequer storage exchequer) internal {
	// 	if (exchequer.exchequerFactor == 0) {
	// 		return;
	// 	}


	// }

	function calculateProtocolFee(
		Types.Exchequer storage exchequer,
		uint256 borrowMax
	) internal view returns (uint256) {
		uint256 protocolBorrowFee = exchequer.protocolBorrowFee;
		return uint256(borrowMax.wadMul(protocolBorrowFee));
	}

	function _getSupplyRate(
		uint256 totalDebt, 
		uint256 totalSupply,
		uint256 currentAverageRate
	) internal pure returns (uint128) {
		uint256 currentSupplyRate = 0;		
		uint256 usageRate = totalDebt.rayDiv(totalSupply);
		currentSupplyRate = currentAverageRate.rayMul(usageRate);
		return uint128(currentSupplyRate);
	}

	function getNormalizedReturn(Types.Exchequer storage exchequer) internal view returns (uint256) {
		uint40 timestamp = exchequer.lastUpdateTimestamp;

		if (timestamp == block.timestamp) {
			return exchequer.supplyIndex;
		} else {
			return MathUtils.calculateLinearInterest(exchequer.supplyRate, timestamp).rayMul(
				exchequer.supplyIndex
			);
		}
	}

	function updateSupplyRate(Types.Exchequer storage exchequer) internal {
		uint256 totalDebt = IDToken(exchequer.dTokenAddress).totalSupply();
		uint256 totalSupply = ISToken(exchequer.sTokenAddress).totalSupply();
		uint256 averageRate = IDToken(exchequer.dTokenAddress).getAverageRate();
		exchequer.supplyRate = _getSupplyRate(totalDebt, totalSupply, averageRate);
	}
}"
    },
    "contracts/protocol/libraries/services/StrategusService.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import {AddressUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {ISToken} from "../../../interfaces/ISToken.sol";
import {IDToken} from "../../../interfaces/IDToken.sol";
import {WadRayMath} from "../math/WadRayMath.sol";
import {Types} from "../types/Types.sol";
import {ExchequerService} from "./ExchequerService.sol";

library StrategusService {
	using ExchequerService for Types.Exchequer;
	using WadRayMath for uint256;
	using SafeCast for uint256;
	using AddressUpgradeable for address;

	function guardDeleteExchequer(
		mapping(uint256 => address) storage exchequersList,
		Types.Exchequer storage exchequer,
		address underlyingAsset
	) internal view {
		require(underlyingAsset != address(0), "ZERO_ADDRESS_NOT_VALID");
		require(exchequer.id != 0 || exchequersList[0] == underlyingAsset, "ASSET_NOT_LISTED");
		require(IERC20Upgradeable(exchequer.sTokenAddress).totalSupply() == 0, "STOKEN_SUPPLY_NOT_ZERO");
		require(IERC20Upgradeable(exchequer.gTokenAddress).totalSupply() == 0, "GTOKEN_SUPPLY_NOT_ZERO");
	}

	function guardAddSupply(
		Types.Exchequer storage exchequer,
		uint256 amount
	) internal view {
		require(amount != 0, "INVALID_AMOUNT");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
		require(exchequer.supplyCap == 0 || 
			exchequer.supplyCap >= (ISToken(exchequer.sTokenAddress).scaledTotalSupply().rayMul(
			exchequer.supplyIndex) + amount), 
			"SUPPLY_CAP_EXCEEDED"
		);
	}

	function guardAddGrantSupply(
		Types.Exchequer storage exchequer,
		uint256 amount
	) internal view {
		require(amount != 0, "INVALID_AMOUNT");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
	}

	function guardWithdraw(
		Types.Exchequer storage exchequer,
		uint256 userBalance,
		uint256 withdrawableBalance,
		uint256 amount
	) internal view {
		require(amount != 0, "INVALID_AMOUNT");
		require(userBalance >= amount, "USER_BALANCE_TOO_LOW");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
		require(amount <= withdrawableBalance, "WITHDRAWABLE_BALANCE_TOO_LOW");
	}

	function guardGrantWithdraw(
		Types.Exchequer storage exchequer,
		uint256 userBalance,
		uint256 amount
	) internal view {
		require(amount != 0, "INVALID_AMOUNT");
		require(userBalance >= amount, "USER_BALANCE_TOO_LOW");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
	}

	function guardCreateLineOfCredit(
		Types.Exchequer storage exchequer,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address underlyingAsset,
		address borrower, 
		uint256 borrowMax,
		uint256 protocolBorrowFee
	) internal view {
		require(linesOfCredit[borrower].deliquent == false, "USER_HAS_DELIQUENT_DEBT");
		require(borrowMax != 0, "INVALID_BORROW_MAX");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
		require(exchequer.borrowingEnabled, "BORROWING_NOT_ENABLED");
		require(IERC20(underlyingAsset).balanceOf(exchequer.sTokenAddress) >= borrowMax, 
			"NOT_ENOUGH_UNDERLYING_ASSET_BALANCE"
		);
		require(exchequer.totalDebt + borrowMax <= exchequer.borrowCap || exchequer.borrowCap == 0, 
			"EXCHEQUER_MUST_STAY_BELOW_BORROW_CAP"
		);
		uint256 userBalance = ISToken(exchequer.sTokenAddress).balanceOf(borrower);
		uint256 decimalConversion = (10 ** 18 / 10 ** exchequer.decimals);
		uint256 projectedTotalDebtWad = (exchequer.totalDebt + borrowMax) * decimalConversion;
		uint256 projectedTotalDebtRay = projectedTotalDebtWad.wadToRay();
		uint256 sTokenBalance = IERC20(underlyingAsset).balanceOf(exchequer.sTokenAddress);
		uint256 gTokenBalance = IERC20(underlyingAsset).balanceOf(exchequer.gTokenAddress);
		uint256 totalCollateralWad = (sTokenBalance + gTokenBalance - protocolBorrowFee) * decimalConversion;
		uint256 totalCollateralRay = totalCollateralWad.wadToRay();
		uint256 projectedCollateralFactor = projectedTotalDebtRay.rayDiv(
			totalCollateralRay
		);
		require(userBalance > protocolBorrowFee, "BORROWER_CANNOT_PAY_FEE");
		require(projectedCollateralFactor < exchequer.collateralFactor.wadToRay(), "NOT_ENOUGH_COLLATERAL");
		require(linesOfCredit[borrower].expirationTimestamp < block.timestamp || linesOfCredit[borrower].expirationTimestamp == 0,
			"LINE_OF_CREDIT_NOT_EXPIRED"
		);
		// require(linesOfCredit[borrower].underlyingAsset == address(0), "USER_ALREADY_HAS_BORROW_POSITION");
	}

	function guardBorrow(
		Types.Exchequer storage exchequer,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address borrower, 
		uint256 amount
	) internal view {
		require(amount != 0, "INVALID_AMOUNT");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
		require(exchequer.borrowingEnabled, "BORROWING_NOT_ENABLED");
		require(linesOfCredit[borrower].borrowMax != 0, "USER_DOES_NOT_HAVE_LINE_OF_CREDIT");
		require(linesOfCredit[borrower].deliquent == false, "USER_HAS_DELINQUENT_DEBT");
		require(IDToken(exchequer.dTokenAddress).balanceOf(borrower) + amount <= linesOfCredit[borrower].borrowMax,
			"USER_CANNOT_BORROW_OVER_MAX_LIMIT"
		);
		require(block.timestamp < linesOfCredit[borrower].expirationTimestamp, "LINE_OF_CREDIT_EXPIRED");
	}

	function guardRepay(
		Types.Exchequer storage exchequer,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address borrower, 
		uint256 amount
	) internal view {
		require(amount != 0, "INVALID_AMOUNT");
		require(exchequer.active, "EXCHEQUER_INACTIVE");
		require(linesOfCredit[borrower].borrowMax != 0, "USER_DOES_NOT_HAVE_LINE_OF_CREDIT");
		// require(!linesOfCredit[borrower].deliquent, "USER_DEBT_IS_DELIQUENT");
		// require(block.timestamp < linesOfCredit[borrower].expirationTimestamp, "LINE_OF_CREDIT_EXPIRED");
	}

	function guardDelinquency(
		Types.Exchequer storage exchequer,
		mapping(address => Types.LineOfCredit) storage linesOfCredit,
		address borrower
	) internal view {
		require(block.timestamp > linesOfCredit[borrower].expirationTimestamp, "LINE_OF_CREDIT_HAS_NOT_EXPIRED");
		// require(IDToken(exchequer.dTokenAddress).balanceOf(borrower) > 10**(exchequer.decimals - 3), "USER_DEBT_BALANCE_APPROX_ZERO");
	}

	// function guardCloseLineOfCredit(
	// 	Types.Exchequer storage exchequer,
	// 	address borrower
	// ) internal view {
	// 	require(IDToken(exchequer.dTokenAddress).balanceOf(borrower) < 10**(exchequer.decimals - 3), "USER_DEBT_BALANCE_IS_NOT_ZERO");
	// }
}"
