    "contracts/protocol/libraries/services/SupplyService.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IGToken} from "../../../interfaces/IGToken.sol";
import {ISToken} from "../../../interfaces/ISToken.sol";
import {IThurmanToken} from "../../../interfaces/IThurmanToken.sol";
import {Types} from "../types/Types.sol";
import {ExchequerService} from "./ExchequerService.sol";
import {StrategusService} from "./StrategusService.sol";
import {WadRayMath} from "../math/WadRayMath.sol";

library SupplyService {
	using WadRayMath for uint256;
	using ExchequerService for Types.Exchequer;

	event Supply(address indexed exchequer, address indexed user, uint256 amount);
	event GrantSupply(address indexed exchequer, address indexed user, uint256 amount);
	event Withdraw(address indexed exchequer, address indexed user, uint256 amount);
	event GrantWithdraw(address indexed exchequer, address indexed user, uint256 amount);

	function addSupply(
		mapping(address => Types.Exchequer) storage exchequers,
		address underlyingAsset,
		address governanceAsset,
		uint256 amount
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		exchequer.update();
		StrategusService.guardAddSupply(exchequer, amount);

		IERC20(underlyingAsset).transferFrom(msg.sender, exchequer.sTokenAddress, amount);
		ISToken(exchequer.sTokenAddress).mint(
			msg.sender,
			amount,
			exchequer.supplyIndex
		);
		IThurmanToken(governanceAsset).mint(msg.sender, amount);
		exchequer.updateSupplyRate();

		emit Supply(underlyingAsset, msg.sender, amount);

	}

	function addGrantSupply(
		mapping(address => Types.Exchequer) storage exchequers,
		address underlyingAsset,
		address governanceAsset,
		uint256 amount
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		StrategusService.guardAddGrantSupply(exchequer, amount);
		IERC20(underlyingAsset).transferFrom(msg.sender, exchequer.gTokenAddress, amount);
		IGToken(exchequer.gTokenAddress).mint(msg.sender, amount);
		IThurmanToken(governanceAsset).mint(msg.sender, amount);

		emit GrantSupply(underlyingAsset, msg.sender, amount);
	}

	function withdraw(
		mapping(address => Types.Exchequer) storage exchequers,
		address underlyingAsset,
		address governanceAsset,
		uint256 amount
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		exchequer.update();

		uint256 userBalance = ISToken(exchequer.sTokenAddress).scaledBalanceOf(msg.sender).rayMul(
			exchequer.supplyIndex
		);
		uint256 withdrawableBalance = ISToken(exchequer.sTokenAddress).withdrawableBalance(
			msg.sender,
			exchequer.totalDebt
		);
		StrategusService.guardWithdraw(
			exchequer, 
			userBalance, 
			withdrawableBalance,
			amount
		);

		ISToken(exchequer.sTokenAddress).burn(
			msg.sender,
			amount,
			exchequer.supplyIndex
		);
		IThurmanToken(governanceAsset).burn(msg.sender, amount);
		exchequer.updateSupplyRate();

		emit Withdraw(underlyingAsset, msg.sender, amount);
	}

	function grantWithdraw(
		mapping(address => Types.Exchequer) storage exchequers,
		address underlyingAsset,
		address governanceAsset,
		uint256 amount
	) internal {
		Types.Exchequer storage exchequer = exchequers[underlyingAsset];
		uint256 userBalance = IGToken(exchequer.gTokenAddress).balanceOf(msg.sender);
		StrategusService.guardGrantWithdraw(
			exchequer,
			userBalance,
			amount
		);
		IGToken(exchequer.gTokenAddress).burn(msg.sender, amount);
		IThurmanToken(governanceAsset).burn(msg.sender, amount);

		emit GrantWithdraw(underlyingAsset, msg.sender, amount);
	}
}"
    },
    "contracts/protocol/libraries/types/Types.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

library Types {
  struct Exchequer {
    uint128 supplyIndex;
    uint128 supplyRate;
    uint40 lastUpdateTimestamp;
    uint16 id;
    uint8 decimals;
    bool active;
    bool borrowingEnabled;
    uint256 protocolBorrowFee;
    uint256 totalDebt;
    uint256 borrowCap;
    uint256 supplyCap;
    uint256 collateralFactor; // expressed in ray
    address sTokenAddress;
    address dTokenAddress;
    address gTokenAddress;
    uint128 accruedToExchequerSafe;
  }

  // a user can only have one open line of credit and LoCs are closed when the balance is zero
  // the balance can be tracked through dToken state
  struct LineOfCredit {
    uint256 borrowMax;
    address underlyingAsset;
    // uint256 rate; // interest should accrue per second => rate based on per second accrual
    uint40 lastRepaymentTimestamp;
    uint40 creationTimestamp;
    uint40 expirationTimestamp;
    uint128 id;
    bool deliquent;
  }
}"
    },
    "contracts/protocol/polemarch/Polemarch.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {TimelockControllerUpgradeable} from "@openzeppelin/contracts-upgradeable/governance/TimelockControllerUpgradeable.sol";
// import {GovernorTimelockControlUpgradeable} from "@openzeppelin/contracts-upgradeable/governance/extensions/GovernorTimelockControlUpgradeable.sol";
import {IPolemarch} from "../../interfaces/IPolemarch.sol";
import {Types} from "../libraries/types/Types.sol";
import {ConfigurationService} from "../libraries/services/ConfigurationService.sol";
import {ExchequerService} from "../libraries/services/ExchequerService.sol";
import {SupplyService} from "../libraries/services/SupplyService.sol";
import {DebtService} from "../libraries/services/DebtService.sol";
import {PolemarchStorage} from "./PolemarchStorage.sol";


contract Polemarch is Initializable, OwnableUpgradeable, PolemarchStorage, IPolemarch {
	using ExchequerService for Types.Exchequer;

	modifier onlyGovernance {
		require(msg.sender == address(_timelock), "Not timelock");
		_;
	}

	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
	    _disableInitializers();
	}

	function initialize() external virtual initializer {
		__Ownable_init();
		_maxExchequersCount = 10;
	}

	function addExchequer(
		address underlyingAsset,
		address sTokenAddress,
		address dTokenAddress,
		address gTokenAddress,
		uint8 decimals,
		uint256 protocolBorrowFee
	) external onlyOwner {
		if (ExchequerService.addExchequer(
			_exchequers,
			_exchequersList,
			underlyingAsset,
			sTokenAddress,
			dTokenAddress,
			gTokenAddress,
			decimals,
			protocolBorrowFee,
			_exchequersCount,
			_maxExchequersCount
		)) {
			_exchequersCount++;
		}
	}

	function supply(
		address underlyingAsset,
		uint256 amount
	) public virtual override {
		SupplyService.addSupply(_exchequers, underlyingAsset, _THURMAN, amount);
	}

	function grantSupply(
		address underlyingAsset,
		uint256 amount
	) public virtual override {
		SupplyService.addGrantSupply(_exchequers, underlyingAsset, _THURMAN, amount);
	}

	function withdraw(
		address underlyingAsset,
		uint256 amount
	) public virtual override {
		SupplyService.withdraw(_exchequers, underlyingAsset, _THURMAN, amount);
	}

	function grantWithdraw(
		address underlyingAsset,
		uint256 amount
	) public virtual override {
		SupplyService.grantWithdraw(_exchequers, underlyingAsset, _THURMAN, amount);
	}

	function deleteExchequer(address underlyingAsset) external onlyOwner {
		ExchequerService.deleteExchequer(_exchequers, _exchequersList, underlyingAsset);
	}

	function getExchequer(address underlyingAsset) external view returns (Types.Exchequer memory) {
		return _exchequers[underlyingAsset];
	}

	function createLineOfCredit(
		address borrower,
		address underlyingAsset,
		uint256 borrowMax,
		uint128 rate,
		uint40 termDays
	) external onlyGovernance {
		DebtService.createLineOfCredit(
			_exchequers,
			_linesOfCredit,
			_linesOfCreditCount,
			borrower,
			underlyingAsset,
			borrowMax,
			rate,
			termDays
		);
		_linesOfCreditCount++;
	}

	function borrow(
		address underlyingAsset,
		uint256 amount
	) public virtual override {
		DebtService.borrow(
			_exchequers, 
			_linesOfCredit,
			underlyingAsset,
			amount
		);
	}

	function repay(
		address underlyingAsset,
		uint256 amount
	) public virtual override {
		DebtService.repay(
			_exchequers,
			_linesOfCredit,
			underlyingAsset,
			amount
		);
	}

	function markDelinquent(address underlyingAsset, address borrower) external onlyOwner {
		DebtService.markDelinquent(
			_exchequers, 
			_linesOfCredit, 
			borrower, 
			underlyingAsset
		);
	}

	function closeLineOfCredit(address underlyingAsset, address borrower) external onlyOwner {
		DebtService.closeLineOfCredit(
			_exchequers,
			_linesOfCredit,
			borrower,
			underlyingAsset
		);
	}

	function getLineOfCredit(address borrower) external view returns (Types.LineOfCredit memory) {
		return _linesOfCredit[borrower];
	}

	function getNormalizedReturn(address underlyingAsset)
		external 
		view 
		virtual 
		override 
		returns (uint256)
	{
		return _exchequers[underlyingAsset].getNormalizedReturn();
	}

	function setThurmanToken(address governanceToken) external onlyOwner {
		_THURMAN = governanceToken;
	}

	function setTimelock(TimelockControllerUpgradeable timelock) external onlyOwner {
		_timelock = timelock;
	}

	function setExchequerBorrowing(address underlyingAsset, bool enabled) external onlyOwner {
		ConfigurationService.setExchequerBorrowing(_exchequers, underlyingAsset, enabled);
	}

	function setExchequerActive(address underlyingAsset, bool active) external onlyOwner {
		ConfigurationService.setExchequerActive(_exchequers, underlyingAsset, active);
	}

	function setSupplyCap(address underlyingAsset, uint256 supplyCap) external onlyOwner {
		ConfigurationService.setSupplyCap(_exchequers, underlyingAsset, supplyCap);
	}

	function setBorrowCap(address underlyingAsset, uint256 borrowCap) external onlyOwner {
		ConfigurationService.setBorrowCap(_exchequers, underlyingAsset, borrowCap);
	}
	// function closeLineOfCredit(){}
}"
    },
    "contracts/protocol/polemarch/PolemarchStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {TimelockControllerUpgradeable} from "@openzeppelin/contracts-upgradeable/governance/TimelockControllerUpgradeable.sol";
// import {GovernorTimelockControlUpgradeable} from "@openzeppelin/contracts-upgradeable/governance/extensions/GovernorTimelockControlUpgradeable.sol";
import {Types} from "../libraries/types/Types.sol";

contract PolemarchStorage {
	mapping(address => Types.Exchequer) internal _exchequers;
	mapping(uint256 => address) internal _exchequersList;
	// mapping can delete lines of credit when it's closed and keep deliquent positions
	// can always just keep events to track historical data
	mapping(address => Types.LineOfCredit) internal _linesOfCredit;
	// both map to underlying assets
	uint256 internal _linesOfCreditCount;
	address internal _THURMAN;
	TimelockControllerUpgradeable _timelock;
	// GovernorTimelockControlUpgradeable _governor;
	uint16 internal _exchequersCount;
	uint16 internal _maxExchequersCount;
}"
