      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./ISprayData.sol";
import "../pings/PingAtts.sol";
import "../pings/Pings.sol";
import "../Planes.sol";
import "../Structs.sol";

contract SprayData is ISprayData, ReentrancyGuard, AccessControlEnumerable{

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    mapping (uint => PaintData) paintDataBySkyId;

    bytes32 public constant DATA_WRITE_ROLE = keccak256("DATA_RW");

    function getWriteRole() public pure override returns (bytes32 role) {
        return DATA_WRITE_ROLE;
    }

    function getPaintBySkyId(uint skyId) public view override returns (PaintData memory paintData) {
        return paintDataBySkyId[skyId];
    }

    function setPaintType(uint skyId, uint8 paintType) public override {
        _checkRole(DATA_WRITE_ROLE, msg.sender);
        paintDataBySkyId[skyId].paintType = paintType;
    }

    function pushPingId(uint skyId, uint pingId) public override {
        _checkRole(DATA_WRITE_ROLE, msg.sender);
        paintDataBySkyId[skyId].pingIds.push(pingId);
    }

    function setExtraData(uint skyId, uint extraDataIdx, uint[] memory extraData) public override {
        _checkRole(DATA_WRITE_ROLE, msg.sender);

        uint[][] storage ed = paintDataBySkyId[skyId].extraData;
        if(ed.length < extraDataIdx) {
            for (uint i = ed.length; i < extraDataIdx; i++) {
                ed.push();
            }
        }
        ed.push(extraData);
    }

    function setExtraData(uint skyId, uint[][] memory extraData) public override {
        _checkRole(DATA_WRITE_ROLE, msg.sender);
        paintDataBySkyId[skyId].extraData = extraData;
    }

    function setPingIds(uint skyId, uint[] memory pingIds) public override {
        _checkRole(DATA_WRITE_ROLE, msg.sender);
        paintDataBySkyId[skyId].pingIds = pingIds;
    }

    function setPaintBySkyId(uint skyId, PaintData memory paintData) public override {
        _checkRole(DATA_WRITE_ROLE, msg.sender);
        paintDataBySkyId[skyId] = paintData;
    }

}"
    },
