    string private _symbol = "XPOZ"; 
    uint8 private _decimals = 18; 

    //Create total supply of 10 billion tokens
    uint256 public _totalSupply; 
    uint256 public initialSupply = 10000000000 * 10 **_decimals;

    address public pair; // Set Pair Address
    IUniswapV2Router02 public router; // Set Router 

    //Set trading active 
    bool public tradingActive = false;
    uint public tradingStartTime;

    uint creationTime = block.timestamp; // Set creation time 

    //ICO Rounds 
    bool public icoActive = false; 

    uint public icoRoundInterval = 14 days; // Set ICO Round Interval

    uint public icoRoundOneStart = creationTime; // Set ICO Round 1 Start Time
    uint public icoRoundTwoStart = creationTime + icoRoundInterval; // Set ICO Round 2 Start Time 
    uint public icoRoundThreeStart = icoRoundTwoStart + icoRoundInterval; // Set ICO Round 3 Start Time

    uint256 public icoRoundOnePrice = 9.2e-7 ether; // Set ICO Round 1 Price (current time of writing $0.0015)
    uint256 public icoRoundTwoPrice = 0.0000015 ether; // Set ICO Round 2 Price (current time of writing $0.0015)
    uint256 public icoRoundThreePrice = 0.0000031 ether; // Set ICO Round 3 Price (current time of writing $0.005)

    uint256 public icoRoundOneBonus = 30; // Set ICO Round 1 Bonus (30%)
    uint256 public icoRoundTwoBonus = 25; // Set ICO Round 2 Bonus (25%)
    uint256 public icoRoundThreeBonus = 20; // Set ICO Round 3 Bonus (20%)

    uint256 public icoRoundOneTokensSold; 
    uint256 public icoRoundTwoTokensSold;
    uint256 public icoRoundThreeTokensSold;

    uint256 public icoRoundOneTokenLimit = 200000000 * 10 ** _decimals; // 200 million tokens
    uint256 public icoRoundTwoTokenLimit = 100000000 * 10 ** _decimals; // 100 million tokens
    uint256 public icoRoundThreeTokenLimit = 100000000 * 10 ** _decimals; // 100 million tokens


    constructor(){
        router = IUniswapV2Router02(UNISWAP_ROUTER);
        pair = IUniswapV2Factory(router.factory()).createPair(router.WETH(), address(this));

        // Mint supply to contract
        _mint(address(this), initialSupply); 

    }

    /**
     * @dev Claim remaining tokens from ICO
     */

    function claimRemaining() external onlyOwner {
        require(block.timestamp > icoRoundThreeStart + icoRoundInterval, "ICO has not ended");
        uint256 remaining = _balances[address(this)];
        _transfer(address(this), owner(), remaining);
    }

    /**
     * @dev Giveaway tokens
     * @param _amount Amount of tokens to giveaway (send in whole numbers, prior to decimals)
     */
    
    function giveaway(address _to, uint256 _amount) external onlyOwner {
        _transfer(address(this), _to, (_amount * 10 ** _decimals));
    }

    /** 
     * @dev Activate trading 
     */
    function activateTrading() external onlyOwner {
        require(tradingActive == false, "Trading is already active"); 
        tradingActive = true;
        tradingStartTime = block.timestamp; 
    }

    /**
     * @dev Activate ICO
     */
    function activateICO() external onlyOwner {
        require(icoActive == false, "ICO is already active"); 
        creationTime = block.timestamp; 
        icoActive = true; 
        icoRoundOneStart = block.timestamp; 
        icoRoundTwoStart = block.timestamp + icoRoundInterval; 
        icoRoundThreeStart = icoRoundTwoStart + icoRoundInterval; 
    }

    /**
     * @dev Burn Tokens 
     */
    function burn(uint256 value) public virtual {
        _burn(_msgSender(), value); 
    }

    /**
     * @dev Allows tokens for ICO rounds
     *
     */

    function icoPurchase(address _to, uint256 _amount) external payable {
        require(icoActive == true, "ICO is not active");
        //Check if any ICO rounds are active 
        require(block.timestamp > creationTime && block.timestamp < icoRoundThreeStart + icoRoundInterval, "ICO has ended");

        

        // Check if ICO round 1 is active
        if(block.timestamp > creationTime && block.timestamp < icoRoundTwoStart) {
            uint256 total_eth_required = (_amount * icoRoundOnePrice).div(10 ** _decimals); // Calculate total ETH required for purchase
            require(icoRoundOneTokensSold < icoRoundOneTokenLimit, "ICO Round 1 has ended"); // Check if max token limit has been reached for round 1 
            require(msg.value >= total_eth_required, "Insufficient ETH sent"); // Check if ETH sent is greater than or equal to ICO Round 1 price
            require(_amount <= icoRoundOneTokenLimit, "Purchase exceeds ICO Round 1 limit"); // Check if purchase would exceed max token limit for round 1
            require(_amount <= icoRoundOneTokenLimit.sub(icoRoundOneTokensSold), "Purchase exceeds ICO Round 1 limit"); // Check if purchase would exceed max token limit for round 1
            // Send ETH to owner
            payable(owner()).transfer(msg.value);
            
            uint256 bonus_tokens = _amount.mul(icoRoundOneBonus).div(100); // Calculate bonus tokens 

            // Transfer tokens to buyer
            _transfer(address(this), _to, _amount.add(bonus_tokens));

            // Update tokens sold
            icoRoundOneTokensSold = icoRoundOneTokensSold.add(_amount);
        }
        // Check if ICO round 2 is active 
        if(block.timestamp > icoRoundTwoStart && block.timestamp < icoRoundThreeStart) {
            uint256 total_eth_required = (_amount * icoRoundTwoPrice).div(10 ** _decimals); // Calculate total ETH required for purchase
            require(icoRoundTwoTokensSold <= icoRoundTwoTokenLimit, "ICO Round 2 has ended"); 
            require(msg.value >= total_eth_required, "Insufficient ETH sent"); // Check if ETH sent is greater than or equal to ICO Round 2 price
            require(_amount <= icoRoundTwoTokenLimit, "Purchase exceeds ICO Round 2 limit"); // Check if purchase would exceed max token limit for round 2
            require(_amount <= icoRoundTwoTokenLimit.sub(icoRoundTwoTokensSold), "Purchase exceeds ICO Round 2 limit"); // Check if purchase would exceed max token limit for round 2

            // Send ETH to owner 
            payable(owner()).transfer(msg.value); 

