    address owner_,
    uint256 offset_,
    uint256 limit_
  ) external view returns (uint256[] memory, uint256[] memory);

  function getTokenTier(
    uint256 tokenId_
  ) external view returns (uint256); 

  function getMaxTier(
  ) external view returns (uint256);

  function getTierInfo(
    uint256 tier_
  ) external view returns (TierInfo memory);

  function getMintedTokenList(
    uint256 offset_,
    uint256 limit_
  ) external view returns (
    uint256,
    uint256[] memory,
    uint256[] memory,
    address[] memory
  );

  function isTokenRunning(
    uint256 tokenId_
  ) external view returns (bool);

  function toggleRunning(
    uint256 tokenId_
  ) external;

  function mint(
    address to_,
    uint256 tier_
  ) external returns (uint256);

  function updateBaseUri(
    string memory baseUri_
  ) external;

  function updateTierCap(
    uint256 tier_,
    uint256 cap_
  ) external;

  function setDefaultRoyalty(
    address beneficiary_,
    uint96 feeNumerator_
  ) external;

  function getRewardContractAddress(
  ) external view returns (address);

  function setRewardContractAddress(
    address rewardContract_
  ) external;

  function destroy(
    address payable to_
  ) external;
}"
