        bottle.batchMint(to, amount, orderId);
    }

    function _pay(
        address token,
        uint256 amount
    ) internal {
        uint amountToPay = getPrice(token, amount);
        if (token == address(0)) {
            _checkPayment(amountToPay, msg.value);
