// File contracts/Metanoise.sol









contract Metanoise is ERC721A, AccessControl, EIP712 {
    enum State {
        PublicMint,
        Paused,
        SoldOut
    }

    //PUBLIC
    State public STATE;
    uint256 public MAX_SUPPLY = 360;
    uint256 public MAX_PER_WALLET = 10;
    uint256 public MINT_PRICE = 5 ether;

    //MAPPINGS
    mapping(bytes32 => address) private _hashes;
    mapping(address => uint256) private _maxPerWallet;

    //ROLES
    bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');
    bytes32 public constant OPERATOR_ROLE = keccak256('OPERATOR_ROLE');
    bytes32 public constant SIGNER_ROLE = keccak256('SIGNER_ROLE');
    bytes32 public constant WITHDRAWER_ROLE = keccak256('WITHDRAWER_ROLE');

    //PRIVATE
    string private baseURI = '';
    string private _contractURI;

    //Errors
    error StateError(State actual, State expected);
    error AmountError();
    error MaxPerWalletError();
    error ValueBelowPriceError();
    error MaxSupplyError();
    error SignatureError();
    error HashAlreadyUsedError();
    error ArraySizeError();
    error BalanceError();
    error WithdrawError();

    constructor(
        string memory name,
        string memory symbol,
        string memory version
    ) ERC721A(name, symbol) EIP712(name, version) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
    }

    function safeMintArray(address[] calldata to, uint256[] calldata amount) external onlyRole(MINTER_ROLE) {
        if (to.length != amount.length) {
            revert ArraySizeError();
        }

        for (uint256 i = 0; i < to.length; i++) {
            _safeMint(to[i], amount[i], '');
        }
    }

    function safeMint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _safeMint(to, amount, '');
    }

    function getSigner(
        address account,
        uint256 amount,
        uint256 nonce,
        bytes calldata signature
    ) external view returns (address) {
        bytes32 hash = _hash(account, amount, nonce);
        return ECDSA.recover(hash, signature);
    }

    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    function mint(
        uint256 amount,
        uint256 nonce,
        bytes calldata signature
    ) external payable {
        if (STATE != State.PublicMint) {
            revert StateError(STATE, State.PublicMint);
        }

        if (amount == 0) {
            revert AmountError();
        }

        if (msg.value < (MINT_PRICE * amount)) {
            revert ValueBelowPriceError();
        }

        if (_maxPerWallet[msg.sender] + amount > MAX_PER_WALLET) {
            revert MaxPerWalletError();
        }

        uint256 currentSupply = totalSupply();

        if ((currentSupply + amount) > MAX_SUPPLY) {
            revert MaxSupplyError();
        }

        bytes32 hash = _hash(msg.sender, amount, nonce);
        if (!_verify(hash, signature)) {
            revert SignatureError();
        }

        if (!_verify(hash, signature)) {
            revert SignatureError();
        }

        if (_hashes[hash] != address(0)) {
            revert HashAlreadyUsedError();
        }
        _safeMint(msg.sender, amount);
        _maxPerWallet[msg.sender] = _maxPerWallet[msg.sender] + amount;
        _hashes[hash] = msg.sender;
    }

    function setPrice(uint256 price) external onlyRole(OPERATOR_ROLE) {
        MINT_PRICE = price;
    }

    function setMaxSupply(uint256 maxSupply) external onlyRole(OPERATOR_ROLE) {
        MAX_SUPPLY = maxSupply;
    }

    function getMaxPerWalletPerAccount(address account) external view returns (uint256) {
        return _maxPerWallet[account];
    }

    function setMaxPerWallet(uint256 maxPerWallet) external onlyRole(OPERATOR_ROLE) {
        MAX_PER_WALLET = maxPerWallet;
    }

    function exists(uint256 tokenId) public view returns (bool) {
        return super._exists(tokenId);
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory _newBaseURI) public onlyRole(OPERATOR_ROLE) {
        baseURI = _newBaseURI;
    }

    function setContractURI(string memory newContractURI) external onlyRole(OPERATOR_ROLE) {
        _contractURI = newContractURI;
    }

    ///Returns the contract URI for OpenSea
    function contractURI() public view returns (string memory) {
        return _contractURI;
    }

    function _hash(
        address account,
        uint256 amount,
        uint256 nonce
    ) internal view returns (bytes32) {
        return
            _hashTypedDataV4(keccak256(abi.encode(keccak256('NFT(address account,uint256 amount,uint256 nonce)'), account, amount, nonce)));
    }

    function _verify(bytes32 digest, bytes memory signature) internal view returns (bool) {
        return hasRole(SIGNER_ROLE, ECDSA.recover(digest, signature));
    }

    function withdraw() external onlyRole(WITHDRAWER_ROLE) {
        uint256 balance = address(this).balance;
        if (balance == 0) {
            revert BalanceError();
        }

        (bool sent, ) = payable(msg.sender).call{value: balance}('');
        if (!sent) {
            revert WithdrawError();
        }
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721A, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
