    error NoDice(string);
    error TooMuch(string);
    error NotEnough(string);

    string public _uri = '';
    bool public _saleOn = false;

    IERC721A immutable _milady = IERC721A(0x5Af0D9827E0c53E4799BB226655A1de152A425a5);
    IERC721A immutable _miladystation = IERC721A(0xB24BaB1732D34cAD0A7C7035C3539aEC553bF3a0);
