    IERC721A immutable _miaura = IERC721A(0x2fC722C1c77170A61F17962CC4D039692f033b43);
    IERC721A immutable _cig = IERC721A(0xEEd41d06AE195CA8f5CaCACE4cd691EE75F0683f);

    IMiladyColaCapsV2 private _cap = IMiladyColaCapsV2(0x0C3106C67BD3D8DC570147c509cF95474C1c9134);

    address private _mony = 0x1821BD18CBdD267CE4e389f893dDFe7BEB333aB6;
    address private _angel = 0xFfDAe607cd28f6Db1303eC333Aa37A13E01f78Dd;
    address public _raffle;
    bool public raffleOn;
    uint256 compDenom = 3;

    //MILADY pool
    address immutable _nftxLiquidity = 0x15A8E38942F9e353BEc8812763fb3C104c89eCf4;
    address immutable _WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    IUniswapV2Router02 private _router = IUniswapV2Router02(0xd9e1cE17f2641f24aE83637ab66a2cca9C378B9F);
    address[] public _path = 
                [
                //WETH
                0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2,
                //MILADY ERC20
                0x227c7DF69D3ed1ae7574A1a7685fDEd90292EB48
                ];
    
    uint256 public bottlePrice;
    uint256 public friendPrice;
    uint256 public bulkPrice;

    constructor() ERC721A("MiladyColaV2", "MC2") {
        _initializeOwner(msg.sender);
        bottlePrice = 12500000000000000;
        friendPrice = 10000000000000000;
        bulkPrice = 11000000000000000;
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 839;
    }

    function mintNew(uint256 numberOfTokens) public payable fairSale(numberOfTokens) {
        if (numberOfTokens < 10) {
            if(bottlePrice*numberOfTokens > msg.value){revert NotEnough("Ether value sent is not correct");}
        } else {
            if(bulkPrice*numberOfTokens > msg.value){revert NotEnough("Ether value sent is not correct");}
        }
        _safeMint(msg.sender, numberOfTokens);
    }

    function mintFriend(uint256 numberOfTokens) public payable miladyFriends fairSale(numberOfTokens) {
        if(friendPrice*numberOfTokens > msg.value){revert NotEnough("Ether value sent is not correct");}
        _safeMint(msg.sender, numberOfTokens);
    }

    function mintToBurn(uint256 numberOfTokens) public payable fairSale(numberOfTokens) {
        if(!raffleOn){revert NoDice("Raffle Not Live");}
        if(friendCheck(msg.sender)){
            if(friendPrice*numberOfTokens > msg.value){revert NotEnough("Ether value sent is not correct");}
        } else {
            if (numberOfTokens < 10) {
                if(bottlePrice*numberOfTokens > msg.value){revert NotEnough("Ether value sent is not correct");}
            } else {
                if(bulkPrice*numberOfTokens > msg.value){revert NotEnough("Ether value sent is not correct");}
            }
        }
        _currentIndex += numberOfTokens;
        _burnCounter += numberOfTokens;
        //mint caps
        _cap.mintLCaps(numberOfTokens,msg.sender);
    }

    function adminMint(uint256 numberOfTokens, address recipient) public onlyOwner{
        _mint(recipient,numberOfTokens);
    }

    function changePrice(uint256 bottle, uint256 friend, uint256 bulk) public onlyOwner {
        bottlePrice = bottle;
        friendPrice = friend;
        bulkPrice = bulk;
    }

    function configure(address newMony, address newAngel, address newCaps, address newRaffle, uint256 newDenom, bool raffleState) public onlyOwner {
        if(newMony!=address(0)){_mony = newMony;}
        if(newAngel!=address(0)){_angel = newAngel;}
        if(newCaps!=address(0)){_cap = IMiladyColaCapsV2(newCaps);}
        if(newRaffle!=address(0)){_raffle = newRaffle;}
        if(newDenom!=0){compDenom = newDenom;}
        raffleOn = raffleState;
    }

    function flipSaleState() public onlyOwner {
        _saleOn = !_saleOn;
    }
    
    function withdraw() public onlyOwner {
        uint256 compWad = address(this).balance / compDenom;
        payArt(compWad / 2);
        payDev(compWad / 2);
        buyMilady();
    }

    function adminWithdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function payDev(uint256 amt) internal {
        payable(_mony).transfer(amt);
    }

    function payArt(uint256 amt) internal {
        payable(_angel).transfer(amt);
    }

    function buyMilady() internal {
        _router.swapExactETHForTokens
            { value: address(this).balance }
            (
                whatCanWeBuyRN(),
                _path,
                _raffle,
                block.timestamp + 1
            );
    }

    function whatCanWeBuyRN() public view returns (uint256){
        uint256 miladyPrice = _estimateFloor();
        //slip 5%
        return address(this).balance * 1e18 / miladyPrice * 9500 / 10000;
    }

    //SWITCH TO INTERNAL
    // MiladyCola Friend check for frontend
    function friendCheck(address holder) internal view returns (bool) {
        if(_milady.balanceOf(holder) > 0){
            return true;
        } else if(_remilio.balanceOf(holder) > 0){
            return true;
        } else if(_miladystation.balanceOf(holder) > 0){
            return true;
        } else if(_cig.balanceOf(holder) > 0){
            return true;
        } else if(_miaura.balanceOf(holder) > 0){
            return true;
        } else {
            return false;
        }
    }

    //SLP nftx from erc721M
    // Use NFTX SLP for aligned NFT as floor price oracle and for determining WETH required for adding liquidity
    // Using NFTX as a price oracle is intentional, as Chainlink/others weren't sufficient or too expensive
    function _estimateFloor() internal view returns (uint256) {
        // Retrieve SLP reserves to calculate price of NFT token in WETH
        (uint112 reserve0, uint112 reserve1,) = IUniswapV2Pair(address(_nftxLiquidity)).getReserves();
        // Calculate value of NFT spot in WETH using SLP reserves values
        uint256 spotPrice;
        // Reverse reserve values if token1 isn't WETH
        if (IUniswapV2Pair(address(_nftxLiquidity)).token1() != address(_WETH)) {
            spotPrice = ((10**18 * uint256(reserve0)) / uint256(reserve1));
        } else { spotPrice = ((10**18 * uint256(reserve1)) / uint256(reserve0)); }
        return (spotPrice);
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        _uri = baseURI;
    }

    function _baseURI() internal view virtual override(ERC721A) returns (string memory) {
        return _uri;
    }

    modifier miladyFriends() {
        if(!friendCheck(msg.sender)){revert NoDice("Milady Friends Only");}
        _;
    }

    modifier fairSale(uint256 numberOfTokens) {
        if(!_saleOn){revert NoDice("Sale Must be Active to Mint");}
        if(numberOfTokens > 48){revert TooMuch("48 bottle limit");}
        if(_nextTokenId() + numberOfTokens > 10000){revert TooMuch("Purchase would exceed max supply");}
        _;
    }

}
