      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "./IUni.sol";

contract UniDelegator {
    event Delegated(address indexed delegator, address indexed delegatee, address indexed referrer);

    Uni public immutable uni = Uni(0x1f9840a85d5aF5bf1D1762F925BDADdC4201F984);

    /**
     * @dev Delegate votes from `msg.sender` to `delegatee`
     * @param delegatee The address to delegate votes to
     * @param referrer The address who referred the delegation
     */
    function delegate(address delegatee, address referrer) public {
        uni.delegate(delegatee);
        emit Delegated(msg.sender, delegatee, referrer);
    }

    /**
     * @dev Returns the current amount of votes that `account` has
     */
    function getCurrentVotes(address account) external view returns (uint96) {
        return uni.getCurrentVotes(account);
    }

    /**
     * @dev Returns the value of tokens owned by `account`
     */
    function balanceOf(address account) external view returns (uint256) {
        return uni.balanceOf(account);
    }
}
"
