pragma solidity ^0.8.17;

interface IUniswapV2Factory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface IUniswapV2Pair {
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface IUniswapV3Factory {
    function getPool(address tokenA, address tokenB, uint24 fee) external view returns (address pool);
}

interface IUniswapV3Pool {
    function liquidity() external view returns (uint256);
    function fee() external view returns (uint24);
}
contract TokenPoolChecker is Ownable {
    address public UNISWAP_V2_FACTORY = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
    address public UNISWAP_V3_FACTORY = 0x1F98431c8aD98523631AE4a59f267346ea31F984;
    address public constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address public constant USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;
    address public constant USDT = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    uint24[] public feeTiers = [100, 500, 3000, 10000];
    
    mapping(address => mapping(address => uint24)) public bestFeeTiers;
    enum PoolVersion { None, V2, V3 }

    struct V3Data {
        address[] v3Path;
        uint24[] poolFees;
    }

    function getPoolVersion(address token) public view returns (PoolVersion) {
        bool isV2 = hasLiquidityV2(token);
        bool isV3;
        (isV3, ) = hasLiquidityV3(token);

        if (isV2 && isV3) {
            uint256 v2Liquidity = getV2Liquidity(token);
            (, address baseToken) = hasLiquidityV3(token);
            uint256 v3Liquidity = getV3Liquidity(token, baseToken);

            return v2Liquidity > v3Liquidity ? PoolVersion.V2 : PoolVersion.V3;
        } else if (isV2) {
            return PoolVersion.V2;
        } else if (isV3) {
            return PoolVersion.V3;
        } else {
            return PoolVersion.None;
        }
    }

    function getV3Data(address token) public view returns (V3Data memory) {
        address baseToken;
        bool hasLiq;
        (hasLiq, baseToken) = hasLiquidityV3(token);
        // Check if the token has V3 liquidity with any base token
        if(!hasLiq) {
            return V3Data({v3Path: new address[](0), poolFees: new uint24[](0)});
        }
        return V3Data({
            v3Path: determineV3SwapPath(token, baseToken), 
            poolFees: determineV3SwapFees(token, baseToken)  
        });
    }

    function hasLiquidityV2(address token) public view returns (bool) {
        IUniswapV2Factory factoryV2 = IUniswapV2Factory(UNISWAP_V2_FACTORY);
        address pair = factoryV2.getPair(token, WETH);
        return pair != address(0);
    }

    function hasLiquidityV3(address token) public view returns (bool, address) {
        IUniswapV3Factory factoryV3 = IUniswapV3Factory(UNISWAP_V3_FACTORY);
        for (uint i = 0; i < feeTiers.length; i++) {
            if (factoryV3.getPool(token, WETH, feeTiers[i]) != address(0)) {
                return (true, WETH);
            } else if (factoryV3.getPool(token, USDC, feeTiers[i]) != address(0)) {
                return (true, USDC);
            } else if (factoryV3.getPool(token, USDT, feeTiers[i]) != address(0)) {
                return (true, USDT);
            }
        }
        return (false, address(0));
    }

    function getV2Liquidity(address token) public view returns (uint256) {
        address pairAddress = IUniswapV2Factory(UNISWAP_V2_FACTORY).getPair(token, WETH);
        (address token0, ) = sortTokens(token, WETH);
        (uint112 reserve0, uint112 reserve1, ) = IUniswapV2Pair(pairAddress).getReserves();
        return token == token0 ? reserve0 : reserve1;
    }

    function determineV3SwapFees(address tokenIn, address baseToken) public view returns(uint24[] memory) {
        IUniswapV3Factory factoryV3 = IUniswapV3Factory(UNISWAP_V3_FACTORY);
            
        uint24 bestFeeTierTokenToBase = 0;
        uint24 bestFeeTierBaseToWETH = 0;
        uint256 maxLiquidityTokenToBase = 0;
        uint256 maxLiquidityBaseToWETH = 0;

        IUniswapV3Pool poolTokenToBase;
        IUniswapV3Pool poolBaseToWETH;

        for (uint i = 0; i < feeTiers.length; i++) {
            address poolAddressTokenToBase = factoryV3.getPool(tokenIn, baseToken, feeTiers[i]);
            address poolAddressBaseToWETH = factoryV3.getPool(baseToken, WETH, feeTiers[i]);

            if (poolAddressTokenToBase != address(0)) {
                poolTokenToBase = IUniswapV3Pool(poolAddressTokenToBase);
                uint256 currentLiquidity = poolTokenToBase.liquidity();
                if (currentLiquidity > maxLiquidityTokenToBase) {
                    maxLiquidityTokenToBase = currentLiquidity;
                    bestFeeTierTokenToBase = feeTiers[i];
                }
            }
            if (poolAddressBaseToWETH != address(0)) {
                poolBaseToWETH = IUniswapV3Pool(poolAddressBaseToWETH);
                uint256 currentLiquidity = poolBaseToWETH.liquidity();
                    
                if (currentLiquidity > maxLiquidityBaseToWETH) {
                    maxLiquidityBaseToWETH = currentLiquidity;
                    bestFeeTierBaseToWETH = feeTiers[i];
                }
            }
        }

        uint24[] memory fees = (baseToken == WETH) ? new uint24[](1) : new uint24[](2);
        fees[0] = bestFeeTierTokenToBase;
        if(baseToken != WETH) {
            fees[1] = bestFeeTierBaseToWETH;
        }
        
        return fees;
    }

    function sortTokens(address tokenA, address tokenB) internal pure returns (address, address) {
        return tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
    }
    function updateUniswapV2Factory(address _uniswapV2Factory) public onlyOwner {
        UNISWAP_V2_FACTORY = _uniswapV2Factory;
    }
    function updateUniswapV3Factory(address _uniswapV3Factory) public onlyOwner {
        UNISWAP_V3_FACTORY = _uniswapV3Factory;
    }
    function determineV3SwapPath(address tokenIn, address baseToken) public view returns(address[] memory) {
        IUniswapV3Factory factoryV3 = IUniswapV3Factory(UNISWAP_V3_FACTORY);
        
        for (uint i = 0; i < feeTiers.length; i++) {
            address poolAddress = factoryV3.getPool(tokenIn, baseToken, feeTiers[i]);
            
            // Direct path for tokenIn -> WETH
            if (poolAddress != address(0) && baseToken == WETH) {
                address[] memory path = new address[](2);
                path[0] = tokenIn;
                path[1] = WETH;
                return path;
            }
            
            // Path for tokenIn -> USDC -> WETH
            if (poolAddress != address(0) && (baseToken == USDC || baseToken == USDT) ) {
                address[] memory path = new address[](3);
                path[0] = tokenIn;
                path[1] = baseToken;
                path[2] = WETH;
                return path;
            }
        }
        return new address[](0);
    }
    function getV3Liquidity(address tokenin, address basetoken) public view returns (uint256) {
        uint256 totalLiquidity = 0;
        IUniswapV3Factory factoryV3 = IUniswapV3Factory(UNISWAP_V3_FACTORY);
        for (uint i = 0; i < feeTiers.length; i++) {
            address poolAddress = factoryV3.getPool(tokenin, basetoken, feeTiers[i]);
            if (poolAddress != address(0)) {
                IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
                totalLiquidity += pool.liquidity();
            }
        }
        return totalLiquidity;
    }
}


// File contracts/donorship.sol

// Original license: SPDX_License_Identifier: MIT
pragma solidity ^0.8.17;
//hardhat console
interface WETH {
    function approve(address guy, uint256 wad) external returns (bool);
    function withdraw(uint256 wad) external;
    function transfer(address dst, uint256 wad) external returns (bool);
    function deposit() external payable;
    event Approval(address indexed src, address indexed guy, uint256 wad);
    event Deposit(address indexed dst, uint256 wad);
    event Withdrawal(address indexed src, uint256 wad);
}

contract DonorshipPaymentProcessor is Ownable, Pausable, ReentrancyGuard {

    event DonationProcessed
    (
        address charity,
        uint256 totalDonation
    );
    address givEthBeneficiary;
    
    function setGiveEthBeneficiary(address _givEthBeneficiary) public onlyOwner {
        givEthBeneficiary = _givEthBeneficiary;
    }
    using SafeERC20 for IERC20;
    //external contracts
    IV3SwapRouter public swapRouter;
    Router02 public router02;
    TokenPoolChecker public poolchecker;
    AffiliateManager public affiliateManager;
    WETH public weth;

    constructor(
        address _swapRouter,
        address payable _router02,
        address _poolchecker,
        address payable _affiliateManager,
        address _weth
    ) {
        swapRouter = IV3SwapRouter(_swapRouter);
        router02 = Router02(_router02);
        poolchecker = TokenPoolChecker(_poolchecker);
        affiliateManager = AffiliateManager(_affiliateManager);
        weth = WETH(_weth);
        givEthBeneficiary = 0x4C260a08D56602e2B3447313b6f2e6883366CaE6;
    }
    //set foundation for token swaps
    struct PoolData {
        bool isPool;
        uint poolVersion;
        address[] v3Path;
        uint24[] poolFees;
    }
    enum PoolVersion {
        None,
        V2,
        V3
    }
    struct V3Data {
        address[] v3Path;
        uint24[] poolFees;
    }

    function getAffiliateFeeAmount(bytes32 _affiliateID) public view returns(uint256){
        //call to AffiliateManager
        return affiliateManager.getAffiliateFeeAmount(_affiliateID);
    }

    function enactPrimaryFee(uint256 _amountIn) public view returns(uint256 _amountDeducted, uint256 _totalOutAfterFee)
    {
        uint256 primaryFee = affiliateManager.getPrimaryFee();
        require(_amountIn > 0, "Amount must be greater than 0");
        uint256 amountOutAfterFee = (_amountIn * (10_000 - primaryFee)) / 10_000;
        uint256 totalDeduction = (_amountIn - amountOutAfterFee);
        return (totalDeduction, amountOutAfterFee);
    }
    function enactSecondaryFee(uint256 _amountIn, bytes32 _affiliateID) public view returns(uint256 _amountDeducted, uint256 _totalOutAfterFee)
    {
        require(_amountIn > 0, "Amount must be greater than 0");
        uint256 secondaryFee = affiliateManager.getAffiliateFeeAmount(_affiliateID);
        uint256 amountOutAfterFee = (_amountIn * (10_000 - secondaryFee)) / 10_000;
        uint256 totalDeduction = (_amountIn - amountOutAfterFee);
        return (totalDeduction , amountOutAfterFee);
    }
    function enactGivEthFee(uint256 _amountIn) public pure returns(uint256 _amountDeducted, uint256 _totalOutAfterFee)
    {
        require(_amountIn > 0, "Amount must be greater than 0");
        uint256 givEthFee = 100;
        uint256 amountOutAfterFee = (_amountIn * (10_000 - givEthFee)) / 10_000;
        uint256 totalDeduction = (_amountIn - amountOutAfterFee);
        return (totalDeduction, amountOutAfterFee);
    }
    function calculateFeesAfterSwap(uint256 _amountIn, bytes32 _affiliateID, bool _isGivEthOrg) public view returns(uint256 _primaryDeduction, uint256 _secondaryDeduction, uint256 _givEthDeduction, uint256 _totalDonationToCharity)
    {
        uint primaryFee = affiliateManager.getPrimaryFee();
        // Primary fee calculation (7.5% of _amountIn)
        uint256 primaryDeductionAmount = _amountIn * primaryFee / 10000;
        // Initialize secondary deduction amount
        uint256 secondaryDeductionAmount = 0;
        // Initialize GivETH deduction, 1% of _amountIn, only if applicable
        uint256 givEthDeduction = _isGivEthOrg ? _amountIn * 100 / 10000 : 0; // 1%
        // Check if the affiliate fee is enabled and applicable
        bool feeEnabledIndividually = affiliateManager.getAffiliateFeeEnabledIndividually(_affiliateID);
        bool isAffiliateFeeEnabled = affiliateManager.getAffiliateFeeEnabled();
        if (isAffiliateFeeEnabled && feeEnabledIndividually) {
            uint affiliateFeeAmount = affiliateManager.getAffiliateFeeAmount(_affiliateID);
            secondaryDeductionAmount = _amountIn * affiliateFeeAmount / 10000;
        }
        // Calculate the total donation to charity after all deductions
        uint256 totalDeductions = primaryDeductionAmount + secondaryDeductionAmount + givEthDeduction;
        uint256 totalDonationToCharity = _amountIn - totalDeductions;
        return (primaryDeductionAmount, secondaryDeductionAmount, givEthDeduction, totalDonationToCharity);
    }

    struct EthTransfers{
        address payable recipient;
        uint256 amount;
    }
    function swapTokens(address _tokenAddress, uint256 _tokenAmount) internal returns(uint256){
        TokenPoolChecker.PoolVersion version = poolchecker.getPoolVersion(_tokenAddress);
        IERC20(_tokenAddress).safeTransferFrom(msg.sender, address(this), _tokenAmount);
        uint256 amountWETH;
        if (version == TokenPoolChecker.PoolVersion.V2) {
            amountWETH = swapV2TokensForEth(_tokenAddress, _tokenAmount, address(this));
        } else if (version == TokenPoolChecker.PoolVersion.V3) {
            TokenPoolChecker.V3Data memory v3Data = poolchecker.getV3Data(_tokenAddress);
            uint24 fee0 = v3Data.poolFees[0];
            uint24 fee1 = (v3Data.v3Path.length == 3) ? v3Data.poolFees[1] : 0;
            amountWETH = swapV3TokensForEth(_tokenAddress, _tokenAmount, v3Data.v3Path, fee0, fee1);
        } else {
            revert("Pool not found");
        }
        return amountWETH;
    }
    function processTokenDonation(
        bytes32 _affiliateID,
        address _charity,
        uint256 _tokenAmount,
        address tokenAddress,
        bool _isGivEthOrg
    ) public payable whenNotPaused {
        require(affiliateManager.doesAffiliateExist(_affiliateID), "Affiliate ID doesnt exist");
        uint256 amountWETH = swapTokens(tokenAddress, _tokenAmount);
        weth.withdraw(amountWETH);
        (
            uint256 primaryDeductionAmount,
            uint256 secondaryDeductionAmount,
            uint256 givEthOrgDeduction,
            uint256 totalDonationToCharity
        ) = calculateFeesAfterSwap(amountWETH, _affiliateID, _isGivEthOrg);

        uint size = 2;
        if (secondaryDeductionAmount > 0) size++;
        if (_isGivEthOrg) size++;
        EthTransfers[] memory ethTransfers = new EthTransfers[](size);

        uint currentIndex = 0;
        ethTransfers[currentIndex++] = EthTransfers({recipient: payable(_charity), amount: totalDonationToCharity});
        ethTransfers[currentIndex++] = EthTransfers({recipient: payable(affiliateManager.primaryBeneficiary()), amount: primaryDeductionAmount});
        
        if (secondaryDeductionAmount > 0) {
            address affReceiver = affiliateManager.getAffiliateReceiver(_affiliateID);
            ethTransfers[currentIndex++] = EthTransfers({recipient: payable(affReceiver), amount: secondaryDeductionAmount});
        }

        if (_isGivEthOrg) {
            ethTransfers[currentIndex] = EthTransfers({recipient: payable(givEthBeneficiary), amount: givEthOrgDeduction});
        }

        for(uint i = 0; i < ethTransfers.length; i++) {
            ethTransfers[i].recipient.transfer(ethTransfers[i].amount);
        }
    }

    function swapV2TokensForEth(address _tokenAddress, uint256 _tokenAmountIn, address _recipient) internal nonReentrant returns(uint256) {
        IERC20(_tokenAddress).safeApprove(address(router02), _tokenAmountIn);
        uint allowance = IERC20(_tokenAddress).allowance(address(this), address(router02));
        require(allowance >= _tokenAmountIn, "Failed to approve tokens for swap");

        address[] memory path = new address[](2);
        path[0] = _tokenAddress;
        path[1] = router02.WETH();

        // Get the estimated output amount
        uint[] memory amountsOut = router02.getAmountsOut(_tokenAmountIn, path);
        uint estimatedOutput = amountsOut[1];

        // Perform the swap
        router02.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _tokenAmountIn, 
            0, 
            path, 
            _recipient, 
            block.timestamp
        );

        return estimatedOutput; 
    }
    function swapV3TokensForEth(
        address _tokenAddress,
        uint256 _tokenAmountIn,
        address[] memory v3Path,
        uint24 fee0,
        uint24 fee1 // This can be 0 if there's no second fee.
    ) internal nonReentrant returns (uint256 amountETH) {
        IERC20(_tokenAddress).safeApprove(address(swapRouter), _tokenAmountIn);

        bytes memory path;
        if (v3Path.length == 2) { 
            // Direct ETH pair
            path = abi.encodePacked(_tokenAddress, fee0, address(weth));
        } else { 
            // Non-ETH pair
            path = abi.encodePacked(_tokenAddress, fee0, v3Path[1], fee1, address(weth));
        }

        IV3SwapRouter.ExactInputParams memory params = IV3SwapRouter.ExactInputParams({
            path: path,
            recipient: address(this),
            amountIn: _tokenAmountIn,
            amountOutMinimum: 0 
        });    

        amountETH = swapRouter.exactInput(params);
        return amountETH;
    }


    function processDonation(bytes32 _affiliateID, address _charity, bool _isGivEthOrg) public payable nonReentrant whenNotPaused{
        require(msg.value > 0, "value must be greater than 0 for msg.value");
        require(_charity != address(0), "invalid address");

        // Check if the affiliate ID exists
        require(affiliateManager.doesAffiliateExist(_affiliateID), "Affiliate ID does not exist");
        // Fee always active for primary
        (
            uint256 primaryDeductionAmount, 
            uint256 secondaryDeductionAmount,
            uint256 givEthDeduction,
            uint256 totalDonationToCharity
        ) = calculateFeesAfterSwap(msg.value, _affiliateID, _isGivEthOrg);
        uint size = 2;
        if (secondaryDeductionAmount > 0) size++;
        if (_isGivEthOrg) size++;
        
        EthTransfers[] memory ethTransfers = new EthTransfers[](size);
        uint currentIndex = 0;
        ethTransfers[currentIndex++] = EthTransfers({recipient: payable(_charity), amount: totalDonationToCharity});
        ethTransfers[currentIndex++] = EthTransfers({recipient: payable(affiliateManager.primaryBeneficiary()), amount: primaryDeductionAmount});
        
        if (secondaryDeductionAmount > 0) {
            address affReceiver = affiliateManager.getAffiliateReceiver(_affiliateID);
            ethTransfers[currentIndex++] = EthTransfers({recipient: payable(affReceiver), amount: secondaryDeductionAmount});
        }

        if (_isGivEthOrg) {
            ethTransfers[currentIndex] = EthTransfers({recipient: payable(givEthBeneficiary), amount: givEthDeduction});
        }

        for(uint i = 0; i < ethTransfers.length; i++) {
            ethTransfers[i].recipient.transfer(ethTransfers[i].amount);
        }

        emit DonationProcessed(_charity, totalDonationToCharity);
    }
    receive() external payable {
        //do nothing
    }
}
