      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.15;

import {ERC20} from "solmate/tokens/ERC20.sol";
import {SafeTransferLib} from "solmate/utils/SafeTransferLib.sol";
import {IL1ERC20Bridge} from "../optimism/interfaces/IL1ERC20Bridge.sol";
import {Rebalancer} from "./abstract/Rebalancer.sol";

/// @title L1Rebalancer
/// @notice The rebalancer contract on L1, receives tokens from Optimism/Arbitrum and deposits to the L1 bridge
contract L1Rebalancer is Rebalancer {
    using SafeTransferLib for ERC20;

    /*//////////////////////////////////////////////////////////////
                               IMMUTABLES
    //////////////////////////////////////////////////////////////*/

    /// @notice The L2 deposit gas
    uint32 public constant l2Gas = 400000;

    /*//////////////////////////////////////////////////////////////
                               CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    /// @notice Constructor
    /// @param _owner The owner
    /// @param _bridge The bridge
    /// @param _to The accounts contract address
    constructor(address _owner, address _bridge, address _to) Rebalancer(_owner, _bridge, _to) {}

    /*//////////////////////////////////////////////////////////////
                            REBALANCE LOGIC
    //////////////////////////////////////////////////////////////*/

    /// @notice Rebalances the bridge by depositing the received tokens to the Accounts contract on Aevo
    /// @dev Callable by anyone
    /// @param l1Token The L1 token address
    function rebalance(address l1Token) external override {
        address l2Token = tokens[l1Token];
        require(l2Token != address(0), "L2_ADDRESS_ZERO");

        uint256 balance = ERC20(l1Token).balanceOf(address(this));
        require(balance != 0, "ZERO_BALANCE");

        // Approve the tokens from this contract to the L1 bridge
        ERC20(l1Token).safeApprove(bridge, balance);

        // Deposit the tokens to the accounts contract on L2
        IL1ERC20Bridge(bridge).depositERC20To(l1Token, l2Token, to, balance, l2Gas, "");
    }
}
