    "src/rebalancer/abstract/Rebalancer.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.15;

import {Owned} from "solmate/auth/Owned.sol";
import {IRebalancer} from "../interfaces/IRebalancer.sol";

/// @title Rebalancer
abstract contract Rebalancer is IRebalancer, Owned {
    /*//////////////////////////////////////////////////////////////
                               IMMUTABLES
    //////////////////////////////////////////////////////////////*/

    /// @notice The bridge contract
    /// @notice ARB/OP Rebalancer usage is Arb gateway / OP Bridge, Mainnet usage is Aevo Bridge
    address public immutable bridge;

    /*//////////////////////////////////////////////////////////////
                                STORAGE
    //////////////////////////////////////////////////////////////*/

    /// @notice The to address for rebalance flow
    /// @notice ARB/OP Rebalancer usage is L1Rebalancer address, Mainnet usage is Accounts address on Aevo L2
    address public to;

    /// @notice Mapping of the L1 token => L2 token address
    /// @notice ARB/OP Rebalancer usage is L1 to ARB / OP L2, Mainnet Rebalancer usage is L1 to Aevo L2
    mapping(address => address) public tokens;

    /*//////////////////////////////////////////////////////////////
                               CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    /// @notice Constructor
    /// @param _owner The owner
    /// @param _bridge The bridge
    /// @param _to The L1Rebalancer contract address
    constructor(address _owner, address _bridge, address _to) Owned(_owner) {
        bridge = _bridge;
        to = _to;
    }

    /*//////////////////////////////////////////////////////////////
                            OWNER OPERATIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Updates the L2 token address
    /// @dev Only callable by the owner
    /// @param l1Token The L1 token address
    /// @param l2Token The L2 token address
    function updateToken(address l1Token, address l2Token) external onlyOwner {
        require(l1Token != address(0), "L1_ADDRESS_ZERO");
        require(l2Token != address(0), "L2_ADDRESS_ZERO");
        require(tokens[l1Token] == address(0), "ADDRESS_EXISTS");

        tokens[l1Token] = l2Token;
    }

    /*//////////////////////////////////////////////////////////////
                            REBALANCE LOGIC
    //////////////////////////////////////////////////////////////*/

    /// @notice Rebalances
    /// @param l1Token The L1 token address
    function rebalance(address l1Token) external virtual {}
}
"
