}


contract Presale is Ownable, ReentrancyGuard {
    //variables for oprating sale
    uint256 public softCap;
    uint256 public hardCap;
    uint256 public startTime;
    uint256 public endTime;
    uint256 public presaleRate;
    uint256 public listingRate;
    uint256 public maxBuyPerParticipant;
    uint256 public minBuyPerParticipant;
    uint256 public liquidityPercent;
    address public constant DEV_WALLET = 0x2206944b8D0fDd8EBCFF7Ddd66AC7C3B81b7B25d;
    address public constant OWNER_WALLET = 0x43bA8ad15E8466337Fcb8b494cf3C27Dbbdab1f1;
    address public constant MARKETING_WALLET = 0x0F8b5D2BFB2b094Faa67Fbe60C90a1f82FFa8FA8;
    uint256 public constant LP_LOCK_TIME = 365 days;
    uint256 public unlockTime;
    uint256 public totalSoldTokenAmount;
    address public benkei;
    address public router = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    bool    public isFinalized;

    //variable for display data
    uint256 public totalDepositedEth;
    mapping(address => uint256) public userDepositEthAmount;
    mapping(address => uint256) public userPurchasedTokenAmount;

    event userDeposit(uint256 amount, address user);
    event userRefunded(uint256 amount, address user);
    event userClaimed(uint256 amount, address user);

    constructor() {
    }

    function setConfig(
        uint256[2] memory _caps,
        uint256[2] memory _times,
        uint256[2] memory _rates,
        uint256 _lpPercent,
        uint256[2] memory _limits,
        address _benkei
    ) external onlyOwner {
        softCap = _caps[0];
        hardCap = _caps[1];
        startTime = _times[0];
        endTime = _times[1];
        minBuyPerParticipant = _limits[0];
        maxBuyPerParticipant = _limits[1];
        presaleRate = _rates[0];
        listingRate = _rates[1];
        liquidityPercent = _lpPercent;
        benkei = _benkei;
    }

    function changeToken(address _token) external onlyOwner {
        benkei = _token;
    }

    function changeRouter(address _router) external onlyOwner {
        router = _router;
    }

    function  changeTimes(uint256[2] memory _times) external onlyOwner {
        startTime = _times[0];
        endTime = _times[1];
    }

    function changePresaleRate(uint256 _rate) external onlyOwner {
        presaleRate = _rate;
    }

    function invest() external payable nonReentrant {
        require(startTime < block.timestamp, "Presale: Sale is not open yet");
        require(endTime > block.timestamp, "Presale: Sale is already closed");

        uint256 _amount = msg.value;
        require(_amount + totalDepositedEth <= hardCap, "Presale: Hardcap reached");

        userDepositEthAmount[msg.sender] += _amount;
        require(
            userDepositEthAmount[msg.sender] >= minBuyPerParticipant,
            "Presale: Min contribution not reached"
        );
        require(
            userDepositEthAmount[msg.sender] <= maxBuyPerParticipant,
            "Presale: Max contribution not reached"
        );
        uint256 tokenAmount = _amount * presaleRate / 10 ** 18;
        totalSoldTokenAmount += tokenAmount;
        userPurchasedTokenAmount[msg.sender] += tokenAmount;
        totalDepositedEth += _amount;
        emit userDeposit(_amount, msg.sender);
    }

    function claimToken() external nonReentrant {
        _isSatisfySale();
        require(isFinalized, "Presale: Presale is not finalized yet");
        uint256 amountEarned = userPurchasedTokenAmount[msg.sender];
        require(amountEarned > 0, "Presale: User have no token to claim");
        userPurchasedTokenAmount[msg.sender] = 0;
        IERC20(benkei).transfer(msg.sender, amountEarned);
        emit userClaimed(amountEarned, msg.sender);
    }

    function finishSale() external onlyOwner nonReentrant {
        _isSatisfySale();

        uint256 balance = address(this).balance;
        uint256 ethAmountForLiquidity =  balance * liquidityPercent / 100;
        uint256 tokenAmountforliquidity = ethAmountForLiquidity * listingRate / 10 ** 18;

        //add liquidity
        addLiquidity(ethAmountForLiquidity, tokenAmountforliquidity);
        unlockTime = block.timestamp + LP_LOCK_TIME;

        // distribute funds
        uint256 amount = address(this).balance / 3;
        payable(MARKETING_WALLET).transfer(amount);
        payable(OWNER_WALLET).transfer(amount);
        balance = address(this).balance;
        payable(DEV_WALLET).transfer(balance);

        // send remaining tokens to owner wallet
        balance = IERC20(benkei).balanceOf(address(this)) - totalSoldTokenAmount;
        IERC20(benkei).transfer(msg.sender, balance);
        isFinalized = true;
    }

    function reFund() external nonReentrant {
        require(block.timestamp >= endTime && totalDepositedEth < softCap, "Presale: Presale success!");

        uint256 _ethAmount = userDepositEthAmount[msg.sender];
        userDepositEthAmount[msg.sender] = 0;
        payable(msg.sender).transfer(_ethAmount);
        totalDepositedEth -= totalDepositedEth;
    }

    function getContractInfo()
        external
        view
        returns (
            uint256[2] memory,
            uint256[2] memory,
            uint256[2] memory,
            uint256[2] memory,
            address,
            uint256
        )
    {
        return (
            [softCap, hardCap],
            [startTime, endTime],
            [presaleRate, listingRate],
            [minBuyPerParticipant, maxBuyPerParticipant],
            benkei,
            totalDepositedEth
        );
    }

    function addLiquidity(
        uint256 ethAmount,
        uint256 tokenAmount
    ) internal returns (uint256 liquidity) {
        IERC20(benkei).approve(router, tokenAmount);

        (,, liquidity) = IUniswapV2Router02(router).addLiquidityETH{value: ethAmount}(
            benkei,
            tokenAmount,
            0,
            0,
            address(this),
            block.timestamp + 60
        );
    }
    
    function withdrawToken() external onlyOwner {
        require(totalDepositedEth < softCap && block.timestamp >= endTime, "Presale: Can't withdraw");
        uint256 balance = IERC20(benkei).balanceOf(address(this));
        IERC20(benkei).transfer(msg.sender, balance);
    }

    function unlockLPToken() external onlyOwner {
        require(block.timestamp >= unlockTime, "Can't unlock LP token");
        address swapFactory = IUniswapV2Router02(router).factory();
        address pair = IUniswapV2Factory(swapFactory).getPair(
            IUniswapV2Router02(router).WETH(),
            benkei
        );
        uint256 lpBalance = IERC20(pair).balanceOf(address(this));
        IERC20(pair).transfer(_msgSender(), lpBalance);
    }

    function getPresaleSuccess() external view returns (bool) {
        if (totalDepositedEth >= hardCap) {
            return true;
        } else if (block.timestamp >= endTime && totalDepositedEth >= softCap) {
            return true;
        } else {
            return false;
        }
    }

    function _isSatisfySale() private view {
        if(totalDepositedEth < hardCap){
            require(
                block.timestamp >= endTime,
                "Presale: Finishing launchpad does not available now"
            );
            require(
                totalDepositedEth >= softCap,
                "Launchpad(Normal): Soft cap not reached"
            );
        }
    }
}
