    "contracts/libraries/ApeSwapHelper.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "lib/v3-periphery-foundry/contracts/libraries/PoolAddress.sol";

library ApeSwapHelper {
    /**
     * @notice Calculates the amount required from desired amount after fees are taken out.
     * @param amount The desired amount.
     * @param fee The fee to take out.
     * @return The amount required after fees are taken out.
     */
    function calcAmountBeforeFees(
        uint256 amount,
        uint256 fee
    ) internal pure returns (uint256) {
        return (amount * 10_000) / (10_000 - fee);
    }

    struct PoolInfo {
        address buyToken;
        address sellToken;
        uint24 fee;
    }

    /**
     * @notice Calculates the pool key for a given pool.
     * @param _poolInfo The pool info.
     * @return The pool key.
     */
    function getPoolKey(
        PoolInfo memory _poolInfo
    ) internal pure returns (PoolAddress.PoolKey memory) {
        return
            PoolAddress.getPoolKey(
                _poolInfo.buyToken,
                _poolInfo.sellToken,
                _poolInfo.fee
            );
    }

    /**
     * @notice Computes the pool address for a given pool info.
     * @param factory The factory address.
     * @param _poolInfo The pool info.
     * @return The pool.
     */
    function getPool(
        address factory,
        PoolInfo memory _poolInfo
    ) internal pure returns (IUniswapV3Pool) {
        return getPool(factory, getPoolKey(_poolInfo));
    }

    /**
     * @notice Computes the pool address for a given pool key.
     * @param factory The factory address.
     * @param _poolKey The pool key.
     * @return The pool.
     */
    function getPool(
        address factory,
        PoolAddress.PoolKey memory _poolKey
    ) internal pure returns (IUniswapV3Pool) {
        return IUniswapV3Pool(PoolAddress.computeAddress(factory, _poolKey));
    }
}
"
    },
    "contracts/TokenTransfer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "./interfaces/ILenderCommitmentForwarder.sol";

abstract contract TokenTransfer {
    function _transferCollateral(
        ILenderCommitmentForwarder.CommitmentCollateralType tokenType,
        address token,
        address from,
        address to,
        uint256 amount,
        uint256 tokenId
    ) internal {
        if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC20
        ) {
            IERC20(token).transferFrom(from, to, amount);
        } else if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC721 ||
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC721_ANY_ID ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC721_MERKLE_PROOF
        ) {
            IERC721(token).safeTransferFrom(from, to, tokenId);
        } else if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC1155 ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC1155_ANY_ID ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC1155_MERKLE_PROOF
        ) {
            IERC1155(token).safeTransferFrom(from, to, tokenId, amount, "");
        } else {
            revert("Unsupported token type");
        }
    }

    function _approveCollateral(
        ILenderCommitmentForwarder.CommitmentCollateralType tokenType,
        address token,
        address spender,
        uint256 amount,
        uint256 tokenId
    ) internal {
        if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC20
        ) {
            IERC20(token).approve(spender, amount);
        } else if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC721 ||
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC721_ANY_ID ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC721_MERKLE_PROOF
        ) {
            IERC721(token).approve(spender, tokenId);
        } else if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC1155 ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC1155_ANY_ID ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC1155_MERKLE_PROOF
        ) {
            IERC1155(token).setApprovalForAll(spender, true);
        } else {
            revert("Unsupported token type");
        }
    }

    function _balanceOfCollateral(
        ILenderCommitmentForwarder.CommitmentCollateralType tokenType,
        address token,
        address account,
        uint256 tokenId
    ) internal view returns (uint256) {
        if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC20
        ) {
            return IERC20(token).balanceOf(account);
        } else if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC721 ||
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC721_ANY_ID ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC721_MERKLE_PROOF
        ) {
            return IERC721(token).ownerOf(tokenId) == account ? 1 : 0;
        } else if (
            tokenType ==
            ILenderCommitmentForwarder.CommitmentCollateralType.ERC1155 ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC1155_ANY_ID ||
            tokenType ==
            ILenderCommitmentForwarder
                .CommitmentCollateralType
                .ERC1155_MERKLE_PROOF
        ) {
            return IERC1155(token).balanceOf(account, tokenId);
        } else {
            revert("Unsupported token type");
        }
    }

    // TODO: 721 & 1155 receive support
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return
            bytes4(
                keccak256("onERC721Received(address,address,uint256,bytes)")
            );
    }

    function onERC1155Received(
        address,
        address,
        uint256 id,
        uint256 value,
        bytes calldata
    ) external pure returns (bytes4) {
        return
            bytes4(
                keccak256(
                    "onERC1155Received(address,address,uint256,uint256,bytes)"
                )
            );
    }
}
"
