contract SOSAX is ERCX {
    using Strings for uint256;
    string public dataURI;
    string public baseTokenURI;

    uint8 private constant _decimals = 18;
    uint256 private constant _totalTokens = 1_000_000_000_000_000;
    uint256 private constant _tokensPerNFT = 100_000_000_000;
    string private constant _name = "SOSAERCX";
    string private constant _ticker = "SOSAX";

    // max per wallet
    uint256 public maxWallet;

    constructor() ERCX("", _name, _ticker, _decimals, _totalTokens, _tokensPerNFT) {
        dataURI = "ipfs://bafybeibnaoxfr2idaw4xbm53s5u5hivy3n5ant3ut6lhieb5y7sq2kkz6m/";
        maxWallet = (_totalTokens * 10 ** _decimals) * 2 / 100;
    }

    function _afterTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids
    ) internal override {
        if(!whitelist[to]) {
            require(_balances[to] <= maxWallet, "Transfer exceeds maximum wallet");
        }
        super._afterTokenTransfer(operator, from, to, ids);
    }

    function setMaxWallet(uint256 percent) external onlyOwner {
        maxWallet = totalSupply * percent / 100;
    }

    function setDataURI(string memory _dataURI) public onlyOwner {
        dataURI = _dataURI;
    }

    function setTokenURI(string memory _tokenURI) public onlyOwner {
        baseTokenURI = _tokenURI;
    }

    function setURI(string memory newuri) external onlyOwner {
        _setURI(newuri);
    }

    function tokenURI(uint256 id) public view returns (string memory) {
        if(id >= _nextTokenId()) revert InputLengthMistmatch();

        if (bytes(super.uri(id)).length > 0)
            return super.uri(id);
        if (bytes(baseTokenURI).length > 0)
            return string(abi.encodePacked(baseTokenURI, id.toString()));
        else {
            uint8 seed = uint8(bytes1(keccak256(abi.encodePacked(id))));

            string memory image;
            string memory rarity;
            string memory description = "10,000 mischievous SOSAs on the blockchain, following the ERCX standard (a new and efficient adaption of ERC404).\n\n100 BILLION $XSOSA = 1 XSOSA NFT.\n\nMAX SUPPLY = 1 QUADRILLION $XSOSA.";

            if (seed <= 25) {
                image = "0.gif";
                rarity = "Legendary";
            } else if (seed <= 76) {
                image = "1.gif";
                rarity = "Super Rare";
            } else if (seed <= 153) {
                image = "2.gif";
                rarity = "Rare";
            } else if (seed <= 255) {
                image = "3.gif";
                rarity = "Normal";
            }

            string memory jsonPreImage = string(abi.encodePacked('{"name": "SOSAX #', id.toString(), '","description":"', description, '","image":"', dataURI, image));
            return string(abi.encodePacked("data:application/json;utf8,", jsonPreImage, '","attributes":[{"trait_type":"Rarity","value":"', rarity, '"}]}'));
        }
    }

    function uri(uint256 id) public view override returns (string memory) {
        return tokenURI(id);
    }

    function totalMinted() public view returns (uint256) {
        return _totalMinted();
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    }
  }
}}
