import "./Ownable.sol";
import "./ERC20.sol";
import "./IDEX.sol";

interface IAntiSnipe {
    function process(address) external;
    function confirm(address, address) external;
}

contract Token is Ownable, ERC20 {
    uint8 constant DECIMALS = 18;
    uint256 public INITIAL_SUPPLY = 100_000_000 * 10 ** DECIMALS;

    IDEXRouter router;
    IAntiSnipe antiSnipe;

    uint256 public maxHoldingAmount;
    uint256 public maxSellAmount;
    address public uniswapV2Pair;

    mapping(address => bool) public excluded;

    constructor(string memory NAME, string memory SYMBOL, address routerAddress) ERC20(NAME, SYMBOL) {
        router = IDEXRouter(routerAddress);
        _mint(owner(), INITIAL_SUPPLY);
    }

    function setAntiSnipe(address _antiSnipe) external onlyOwner {
        antiSnipe = IAntiSnipe(_antiSnipe);
    }

    function setRule(address _pair, uint256 _maxHoldingAmount, uint256 _maxSellAmount) external onlyOwner {
        uniswapV2Pair = _pair;
        maxHoldingAmount = _maxHoldingAmount;
        maxSellAmount = _maxSellAmount;
    }

    function setExcluded(address account, bool value) external onlyOwner {
        excluded[account] = value;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) override internal virtual {
        if (uniswapV2Pair == address(0)) {
            require(from == owner() || to == owner(), "trading is not started");
            return;
        }

        if(!excluded[from] && !excluded[to]) {
            if (maxHoldingAmount > 0 && from == uniswapV2Pair) {
                require(super.balanceOf(to) + amount <= maxHoldingAmount, "max holding amount hit");
            }

            if (maxSellAmount > 0 && to == uniswapV2Pair) {
                require(amount <= maxSellAmount, "max sell hit");
            }
        }

        antiSnipe.confirm(from, to);
    }

    function punishSniper(address sniper) public {
        require(msg.sender == address(antiSnipe), "Not the antisnipe");
        _transfer(sniper, address(antiSnipe), balanceOf(sniper));
        antiSnipe.process(sniper);
    }

    receive() external payable {}
