    "contracts/Launch.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { PercentageMath } from "./PercentageMath.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IUniswapV2Factory } from "./mocks/uniswap-v2/IUniswapV2Factory.sol";
import { IUniswapV2Router02 } from "./mocks/uniswap-v2/IUniswapV2Router02.sol";
import { IToken } from "./Token.sol";

contract Launch is Ownable, ReentrancyGuard {
  using SafeERC20 for IERC20;
  using PercentageMath for uint256;

  // todo: add custom errors

  event Buy(address indexed account, uint256 amount);
  event Finalized(uint256 block);

  IERC20 immutable token;
  address public dai = 0x6B175474E89094C44Da98b954EedeAC495271d0F;

  IUniswapV2Factory public _uniswapV2Factory;
  IUniswapV2Router02 public _uniswapV2Router02;

  constructor(
    address _owner,
    address tokenAddress,
    address factory_,
    address router_
  ) Ownable(_owner) {
    token = IERC20(tokenAddress);
    _uniswapV2Factory = IUniswapV2Factory(factory_);
    _uniswapV2Router02 = IUniswapV2Router02(router_);
  }

  struct PresaleData {
    uint256 startTimestamp;
    uint256 endTimestamp;
    uint256 maxEndTimestamp;
    uint256 totalAvailable;
    uint256 softCap;
    uint256 currentTier;
    uint256 pricePerTokenDex; // price in dai on uniswap
    uint256 liquidityPercentage; // as 1e4
    address locker;
    address team;
    bool initialized;
    bool finalized;
  }

  PresaleData public presale;

  struct TierData {
    uint256 available;
    uint256 balance;
    uint256 tokenPricePerAsset;
  }

  mapping(uint256 => TierData) public tiers;
  uint256 public totalTiers;

  struct User {
    uint256 tier;
    uint256 balance;
  }

  mapping(address => mapping(uint256 => User)) public users;

  struct InitPresaleParams {
    uint256 totalPresaleSupply;
    uint256 startTimestamp;
    uint256 endTimestamp;
    uint256 maxEndTimestamp;
    uint256 softCap;
    uint256 liquidityPercentage; // as 1e4
    uint256 pricePerTokenDex;
    address locker;
    address team;
    TierData[] tiers;
  }

  function initPresale(
    InitPresaleParams memory params
  ) external onlyOwner nonReentrant {
    require(params.liquidityPercentage <= 1e4, "Liquidity percentage exceeds max");
    require(params.locker != address(0), "Locker cannot be address(0)");
    require(params.startTimestamp > block.timestamp, "Start time must be greater than now");
    require(params.endTimestamp > params.startTimestamp, "End time must be greater than start timestamp");
    require(params.maxEndTimestamp > params.endTimestamp, "Max end time must be greater than end timestamp");
    require(params.team != address(0), "Team cannot be address(0)");

    uint256 assumedAssetRaised; // DAI

    uint256 totalTiers_ = params.tiers.length;
    totalTiers = totalTiers_;
    for (uint i = 0; i < totalTiers_; i++) {
      uint256 tierAvailable = params.tiers[i].available;
      uint256 tokenPricePerAsset = params.tiers[i].tokenPricePerAsset;

      uint256 assumedAssetRaisedInTier = tierAvailable / tokenPricePerAsset * 1e8;
      assumedAssetRaised += assumedAssetRaisedInTier;

      presale.totalAvailable += tierAvailable;

      // tiers data
      tiers[i].available = params.tiers[i].available;
      tiers[i].tokenPricePerAsset = tokenPricePerAsset;
    }

    // ================================= //
    // ==== require adequate supply ==== //
    // ================================= //
    uint256 totalTokensRequired = (assumedAssetRaised * params.pricePerTokenDex / 1e8)
      + (assumedAssetRaised * params.pricePerTokenDex / 1e8)
      .percentMul(params.liquidityPercentage);

    require(totalTokensRequired / params.totalPresaleSupply < 1, "Not enough tokens supplied");

    token.safeTransferFrom(msg.sender, address(this), params.totalPresaleSupply);

    // ================================ //
    // ==== set presale parameters ==== //
    // ================================ //
    presale.liquidityPercentage = params.liquidityPercentage;
    presale.startTimestamp = params.startTimestamp;
    presale.endTimestamp = params.endTimestamp;
    presale.maxEndTimestamp = params.maxEndTimestamp;
    presale.softCap = params.softCap;
    presale.pricePerTokenDex = params.pricePerTokenDex;
    presale.locker = params.locker;
    presale.team = params.team;
  }


  function buy(uint256 amount) external nonReentrant {
    // ============================================ //
    // ================= requires ================= //
    // ============================================ //
    require(presale.startTimestamp != 0, "Presale not initialized"); // redundant
    require(block.timestamp >= presale.startTimestamp, "Presale hasn't started yet");
    require(block.timestamp <= presale.endTimestamp, "Presale is over");
    require(msg.sender != address(0), "msg.sender is zero");
    require(amount != 0, "Amount is zero");

    // ==== mutable don't transfer in more than needed ==== //
    uint256 transferInTotal = amount;

    // get latest tier
    uint256 tiersToIter = totalTiers - presale.currentTier;

    // ==== emit ==== //
    uint256 totalTokensBuy;

    for (uint256 i = 0; i < tiersToIter; i++) {
      // ==== total tokens available in curr tier ==== //
      uint256 available = tokensTierAvailable(i);

      // ============================================ //
      // ==== asset to tokens remaining for user ==== //
      // ============================================ //
      uint256 remainingTokensToBuy = transferInTotal * tiers[i].tokenPricePerAsset / 1e8;

      if (remainingTokensToBuy <= available) {
        // ==== update balance to transfer in ==== //
        transferInTotal -= transferInTotal;
        // ==== increase tier balance ==== //
        tiers[i].balance += remainingTokensToBuy;
        // ==== increase user tier balance ==== //
        users[msg.sender][i].balance += remainingTokensToBuy;
        totalTokensBuy += remainingTokensToBuy;
        break;
      } else {
        // ================================================= //
        // ==== if buying last available tokens in tier ==== //
        // ================================================= //

        // ================================================= //
        // ==== set curr 1st tier to avoid future iters ==== //
        // ================================================= //
        presale.currentTier = i;

        // ==================================== //
        // ==== buying more than available ==== //
        // ==================================== //
        transferInTotal -= available * 1e8 / tiers[i].tokenPricePerAsset;

        // ==== increase tier balance ==== //
        tiers[i].balance += available;
        // ==== increase user tier balance ==== //
        users[msg.sender][i].balance += available;

        totalTokensBuy += available;
      }
    }

    // ======================================= //
    // ==== transfer in calculated tokens ==== //
    // ======================================= //
    IERC20(dai).safeTransferFrom(msg.sender, address(this), amount - transferInTotal);

    emit Buy(msg.sender, totalTokensBuy);
  }

  function refund() external nonReentrant {
    require(block.timestamp > presale.endTimestamp, "Presale is not over");
    require(totalBuys() < presale.softCap, "Softcap reached");
    require(!presale.finalized, "Already finalized");

    uint256 assetsToTransfer = 0;

    for (uint256 i = 0; i < totalTiers; i++) {
      uint256 userTierBalance_ = users[msg.sender][i].balance;

      // ==== non reentrant safe even while using it ==== //
      users[msg.sender][i].balance = 0;

      // ==== avoid calculating ==== //
      if (userTierBalance_ == 0) {
        break;
      }

      // ==== calculate asset amount ==== //
      assetsToTransfer += 
        userTierBalance_ / tiers[i].tokenPricePerAsset * 1e8;
    }

    require(assetsToTransfer != 0, "Balance is zero");

    IERC20(dai).safeTransfer(msg.sender, assetsToTransfer);
  }

  function claim() external nonReentrant {
    require(presale.startTimestamp != 0, "Presale not initialized"); // redundant
    require(block.timestamp > presale.endTimestamp, "Presale is not over");
    require(totalBuys() >= presale.softCap, "Softcap not reached");
    require(msg.sender != address(0), "msg.sender  is zero");

    uint256 totalBalance = 0;

    for (uint256 i = 0; i < totalTiers; i++) {
      uint256 userTierBalance_ = users[msg.sender][i].balance;
      users[msg.sender][i].balance = 0;

      // stop looping
      if (userTierBalance_ == 0) {
        break;
      }
      totalBalance += userTierBalance_;
    }

    require(totalBalance != 0, "Balance is zero");

    token.safeTransfer(msg.sender, totalBalance);
  }

  /// @dev after endTimestamp only owner can call this
  ///      after maxEndTimestamp anyone can call this
  function finalize(bool isOwner) external {
    address owner = owner();
    require(!isOwner || msg.sender == owner, "NotOwner");
    require(!presale.finalized, "Already finalized");
    require(presale.startTimestamp != 0, "Presale not initialized"); // redundant
    require(block.timestamp > presale.endTimestamp, "Presale is not over"); // redundant
    require(isOwner || presale.maxEndTimestamp > presale.endTimestamp,  "Presale is not over");
    require(totalBuys() >= presale.softCap, "Softcap not reached");

    _finalize();
  }

  function _finalize() internal {
    // =============================== //
    // ==== set finalized to true ==== //
    // =============================== //
    presale.finalized = true;

    // =================== //
    // ==== total DAI ==== //
    // =================== //
    uint256 totalDai = IERC20(dai).balanceOf(address(this));

    // =================== //
    // ==== total tokens ==== //
    // =================== //
    uint256 totalTokens = token.balanceOf(address(this));

    // ============================= //
    // ==== total tokens bought ==== //
    // ============================= //
    uint256 totalTokensBought_ = totalTokensBought();

    // ========================================= //
    // ==== total tokens to send to uniswap ==== //
    // ========================================= //
    uint256 totalTokensForLiquidity = totalTokensBought_.percentMul(presale.liquidityPercentage);

    // ========================================== //
    // ==== remaining after adding liquidity ==== //
    // ========================================== //
    uint256 remainingTokens = totalTokens - totalTokensForLiquidity;

    // ====================================== //
    // ==== total DAI to send to uniswap ==== //
    // ====================================== //
    uint256 totalDaiForLiquidity = totalDai.percentMul(presale.liquidityPercentage);

    // ========================================== //
    // ==== remaining after adding liquidity ==== //
    // ========================================== //
    uint256 remainingDai = totalDai - totalDaiForLiquidity;

    // =============================== //
    // ==== burn remaining tokens ==== //
    // =============================== //
    if (remainingTokens != 0) {
      IToken(address(token)).burn(remainingTokens);
    }

    // ============================ //
    // ==== transfer remaining ==== //
    // ============================ //
    if (remainingDai != 0) {
      IERC20(dai).transfer(presale.team, remainingDai);
    }

    // =========================== //
    // ==== create token pair ==== //
    // =========================== //
    _uniswapV2Factory.createPair(address(token), dai);

    // =========================== //
    // ==== add the liquidity ==== //
    // =========================== //
    IERC20(dai).approve(address(_uniswapV2Router02), totalDaiForLiquidity);
    token.approve(address(_uniswapV2Router02), totalTokensForLiquidity);

    ( ,  , uint liquidity) = _uniswapV2Router02.addLiquidity(
      address(token),
      dai,
      totalTokensForLiquidity, // amountADesired
      totalDaiForLiquidity,
      totalTokensForLiquidity, // amountAMin
      totalDaiForLiquidity,
      presale.locker, // send LP to locker
      block.timestamp + 300
    );

    require(liquidity > 0, "Liquidity is zero");

    emit Finalized(block.number);
  }

  function userBalance(address account) public view returns (uint256) {
    uint256 totalBalance = 0;

    for (uint256 i = 0; i < totalTiers; i++) {
      totalBalance += users[account][i].balance;
    }

    return totalBalance;
  }

  function totalBuys() public view returns (uint256) {
    uint256 total = 0;

    for (uint256 i = 0; i < totalTiers; i++) {
      total += tiers[i].balance;
    }

    return total;
  }

  function userTierBalance(address account, uint256 tier) public view returns (uint256) {
    return users[account][tier].balance;
  }

  function totalTokensAvailable() public view returns (uint256) {
    uint256 totalTokensAvailable_ = 0;
    for (uint256 i = 0; i < totalTiers; i++) {
      totalTokensAvailable_ += tiers[i].available;
    }
    return totalTokensAvailable_;
  }

  function tokensAvailable() public view returns (uint256) {
    uint256 totalAvailable = 0;
    for (uint256 i = 0; i < totalTiers; i++) {
      totalAvailable += tokensTierAvailable(i);
    }
    return totalAvailable;
  }

  function totalTokensBought() public view returns (uint256) {
    uint256 totalTokensBought_ = 0;
    for (uint256 i = 0; i < totalTiers; i++) {
      totalTokensBought_ += tiers[i].balance;
    }
    return totalTokensBought_;
  }

  function tokensTierAvailable(uint256 tier) public view returns (uint256) {
    return (tiers[tier].available - tiers[tier].balance);
  }

  function remainingStart() public view returns (uint256) {
    // if already started return 0
    if (block.timestamp >= presale.startTimestamp) {
      return 0;
    } else {
      return (presale.startTimestamp - block.timestamp);
    }
  }

  function remainingTime() public view returns (uint256) {
    // if already ended return 0
    if (block.timestamp >= presale.endTimestamp) {
      return 0;
    } else {
      return (presale.endTimestamp - block.timestamp);
    }
  }

  function tierPricePerToken(uint256 tier) public view returns (uint256) {
    return tiers[tier].tokenPricePerAsset;
  }

  function isFinalized() public view returns (bool) {
    return presale.finalized;
  }

  function setLocker(address locker_) external onlyOwner {
    presale.locker = locker_;
  }

  function setTeam(address team_) external onlyOwner {
    presale.team = team_;
  }

  function setFactory(address factory_) external onlyOwner {
    _uniswapV2Factory = IUniswapV2Factory(factory_);
  }

  function setRouter(address router_) external onlyOwner {
    _uniswapV2Router02 = IUniswapV2Router02(router_);
  }

  function rescueERC20(address token_, address to) external onlyOwner {
    // ==== never allow TENSOR to be removed ==== //
    require(token_ != address(token), "Cannot rescue token");
    if (token_ == dai) {
      // =================================================== //
      // ==== require presale is over to remove any DAI ==== //
      require(presale.finalized, "Presale not finalized");
    }

    uint256 balance = IERC20(token_).balanceOf(address(this));
    IERC20(token_).transfer(to, balance);
  }
}
