  event Equipped(
    uint256 indexed _tokenId,
    uint256 indexed _itemId,
    ItemType _itemType
  );
  event Unequipped(
    uint256 indexed _tokenId,
    uint256 indexed _itemId,
    ItemType _itemType
  );
