contract DogeGun {
    address public owner;
    IUniswapV2Router02 public uniswapRouter;
    address[] public wallets;

    constructor(address _uniswapRouter, address[] memory _wallets) {
        owner = msg.sender;
        uniswapRouter = IUniswapV2Router02(_uniswapRouter);
        wallets = _wallets;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    function buyToken(address[] memory _path, uint256 _wethAmount, uint256 _times, uint256[] memory _minTokens) external onlyOwner {      
        for (uint256 i = 0; i < _times; i++) {
            uniswapRouter.swapExactTokensForTokens(
                _wethAmount,
                _minTokens[i],
                _path,
                wallets[i], 
                block.timestamp + 600
            );
        }
    }

    function sellToken(address[] memory _path, uint256 _tokenAmount, uint256 _minWETH) external onlyOwner {
        IERC20 token = IERC20(_path[0]);
        uint256 totalBalance = 0;

        // Aggregate token amounts
        for (uint256 i = 0; i < wallets.length; i++) {
            uint256 walletBalance = token.balanceOf(wallets[i]);
            if(walletBalance > 0) {
                token.transferFrom(wallets[i], address(this), walletBalance);
                totalBalance += walletBalance;
            }
        }
        
        token.approve(address(uniswapRouter), totalBalance);

        uniswapRouter.swapExactTokensForTokens(
            _tokenAmount,
            _minWETH,
            _path,
            address(this), // Keeping WETH in contract
            block.timestamp + 600
        );
    }

    // Allow withdrawal of any accidental token deposits
    function recoverToken(address _token) external onlyOwner {
        IERC20 token = IERC20(_token);
        token.transfer(owner, token.balanceOf(address(this)));
    }

    // Allow owner to withdraw WETH
    function withdrawWETH(uint256 _amount) external onlyOwner {
        IERC20 weth = IERC20(address(uniswapRouter.WETH()));
        weth.transfer(owner, _amount);
    }

    // Withdraw any ETH balance in the contract
    function withdrawETH() external onlyOwner {
        payable(owner).transfer(address(this).balance);
    }

    function approveWETH() external onlyOwner {
        IERC20 weth = IERC20(address(uniswapRouter.WETH()));
        uint256 balance = weth.balanceOf(address(this));
        weth.approve(address(uniswapRouter), balance);
    }
}
