import { ERC721 } from "solady/src/tokens/ERC721.sol";
import { MerkleProofLib } from "solady/src/utils/MerkleProofLib.sol";
import { MessageHashUtils } from '@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol';
import { Ownable } from "solady/src/auth/Ownable.sol";
import { SafeTransferLib } from 'solady/src/utils/SafeTransferLib.sol';

contract Temporals is ERC721, Ownable {
    using MessageHashUtils for bytes32;

    uint256 public price = 0.111 ether;

    uint256 public totalSupply;

    uint256 public constant MAX_SUPPLY = 222;

    address public withdrawAddress = 0xc6DC2a8fEd1Ac6A79be4a199239b23f99ca07e4e;

    bytes32 public merkleRoot;

    string public baseTokenURI;

    mapping(uint160 => uint8) public addressToEarlyMintCount;

    enum MintStatus {
        CLOSED,
        ALLOWLIST,
        PUBLIC
    }

    MintStatus public mintStatus;

    /// ERC-721

    function name() public view virtual override returns (string memory) {
        return "Temporals";
    }

    function symbol() public view virtual override returns (string memory) {
        return "TMPRLS";
    }

    /// Errors

    error AlreadyMinted();
    error InvalidMintStatus();
    error MintedOut();
    error MissingWithdrawAddress();
    error NotOnTheList();
    error SetMerkleRoot();
    error WithdrawFailed();
    error WrongPrice();

    /// Constructor

    constructor() {
        _initializeOwner(msg.sender);

        // Team Mints
        _mint(0xfBA7737Aea0969cE95CC7f1879B58e8Fb8c16836, 0);
        _mint(0xC5Da7618e7629343A839cF06bbC30605E4F3Da9B, 1);
        _mint(0x3a6372B2013f9876a84761187d933DEe0653E377, 2);

        totalSupply = 3;
    }

    /// Mint

    modifier premintCheck(MintStatus _status) {
        if (totalSupply + 1 >= MAX_SUPPLY) revert MintedOut();
        if (mintStatus != _status) revert InvalidMintStatus();
        if (msg.value != price) revert WrongPrice();
        _;
    }

    function allowlistMint(bytes32[] calldata _merkleProof, uint256 _tokenId) payable external premintCheck(MintStatus.ALLOWLIST){
        if (merkleRoot == 0) revert SetMerkleRoot();

        if (addressToEarlyMintCount[uint160(msg.sender)] == 2) revert AlreadyMinted();

        // might just need this
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender));
        if (!MerkleProofLib.verifyCalldata(_merkleProof, merkleRoot, leaf)) revert NotOnTheList();

        unchecked {
            ++totalSupply;
            ++addressToEarlyMintCount[uint160(msg.sender)];
        }

        _mint(msg.sender, _tokenId);
    }

    function mint(uint256 _tokenId) payable external premintCheck(MintStatus.PUBLIC) {
        unchecked { ++totalSupply; }

        _mint(msg.sender, _tokenId);
    }

    function teamMint(address[] calldata _to, uint256[] calldata _tokenIds) external onlyOwner {
        for (uint i = 0; i < _to.length;) {
            _mint(_to[i], _tokenIds[i]);

            unchecked {
                ++totalSupply;
                ++i;
            }
        }
    }

    /// Virtual

    function _baseURI() internal view virtual returns (string memory) {
        return baseTokenURI;
    }

    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
        return string(abi.encodePacked(_baseURI(), uint2str(_tokenId)));
    }

    /// Setters

    function setPrice(uint256 _priceInWei) external onlyOwner {
        price = _priceInWei;
    }

    function setBaseURI(string calldata _uri) external onlyOwner {
        baseTokenURI = _uri;
    }

    function setWithdrawAddress(address _address) external onlyOwner {
        withdrawAddress = _address;
    }

    function setMerkleRoot(bytes32 _root) external onlyOwner {
        merkleRoot = _root;
    }

    function setMintStatus(uint256 _status) external onlyOwner {
        mintStatus = MintStatus(_status);
    }

    // Credit: Jalil.eth & Checks
    // https://www.contractreader.io/contract/mainnet/0x036721e5a769cc48b3189efbb9cce4471e8a48b1#utilities-18-19-16
    function uint2str(uint256 _i) internal pure returns (string memory _uintAsString) {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            ++len;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k = k - 1;
            uint8 temp = (48 + uint8(_i - (_i / 10) * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }

        return string(bstr);
    }

    /// Withdraw

    function withdraw() external onlyOwner {
        SafeTransferLib.safeTransferAllETH(withdrawAddress);
    }

}
"
