    "contracts/app/WmbApp.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "../interfaces/IWmbGateway.sol";
import "../interfaces/IWmbReceiver.sol";

/**
 * @title WmbApp
 * @dev Abstract contract to be inherited by applications to use Wanchain Message Bridge for send and receive messages between different chains.
 * All interfaces with WmbGateway have been encapsulated, so users do not need to have any interaction with the WmbGateway contract.
 */
abstract contract WmbApp is AccessControl, Initializable, IWmbReceiver {
    // The address of the WMB Gateway contract
    address public wmbGateway;

    // A mapping of remote chains and addresses that are trusted to send messages to this contract
    // fromChainId => fromAddress => trusted
    mapping (uint => mapping(address => bool)) public trustedRemotes;

    /**
     * @dev Initializes the contract with the given admin, WMB Gateway address, and block mode flag
     * @param admin Address of the contract admin
     * @param _wmbGateway Address of the WMB Gateway contract
     */
    function initialize(address admin, address _wmbGateway) virtual public initializer {
        // Initialize the AccessControl module with the given admin
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        wmbGateway = _wmbGateway;
    }

    /**
     * @dev Function to set the trusted remote addresses
     * @param fromChainIds IDs of the chains the messages are from
     * @param froms Addresses of the contracts the messages are from
     * @param trusted Trusted flag
     * @notice This function can only be called by the admin
     */
    function setTrustedRemotes(uint[] calldata fromChainIds, address[] calldata froms, bool[] calldata trusted) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "WmbApp: must have admin role to set trusted remotes");
        require(fromChainIds.length == froms.length && froms.length == trusted.length, "WmbApp: invalid input");
        for (uint i = 0; i < fromChainIds.length; i++) {
            trustedRemotes[fromChainIds[i]][froms[i]] = trusted[i];
        }
    }

    /**
     * @dev Function to estimate fee in native coin for sending a message to the WMB Gateway
     * @param toChain ID of the chain the message is to
     * @param gasLimit Gas limit for the message
     * @return fee Fee in native coin
     */
    function estimateFee(uint256 toChain, uint256 gasLimit) virtual public view returns (uint256) {
        return IWmbGateway(wmbGateway).estimateFee(toChain, gasLimit);
    }

    /**
     * @dev Function to receive a WMB message from the WMB Gateway
     * @param data Message data
     * @param messageId Message ID
     * @param fromChainId ID of the chain the message is from
     * @param from Address of the contract the message is from
     */
    function wmbReceive(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address from
    ) virtual external {
        // Only the WMB gateway can call this function
        require(msg.sender == wmbGateway, "WmbApp: Only WMB gateway can call this function");
        require(trustedRemotes[fromChainId][from], "WmbApp: Remote is not trusted");
        _wmbReceive(data, messageId, fromChainId, from);
    }

    /**
     * @dev Function to be implemented by the application to handle received WMB messages
     * @param data Message data
     * @param messageId Message ID
     * @param fromChainId ID of the chain the message is from
     * @param from Address of the contract the message is from
     */
    function _wmbReceive(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address from
    ) virtual internal;

    /**
     * @dev Function to send a WMB message to the WMB Gateway from this App
     * @param toChainId ID of the chain the message is to
     * @param to Address of the contract the message is to
     * @param data Message data
     * @return messageId Message ID
     */
    function _dispatchMessage(
        uint toChainId,
        address to,
        bytes memory data,
        uint fee
    ) virtual internal returns (bytes32) {
        return IWmbGateway(wmbGateway).dispatchMessage{value: fee}(toChainId, to, data);
    }

    /**
     * @dev Function to send batch WMB messages to the WMB Gateway from this App
     * @param toChainId ID of the chain the message is to
     * @param messages Messages data
     * @return messageId Message ID
     */
    function _dispatchMessageBatch(uint256 toChainId, Message[] memory messages, uint fee) virtual internal returns (bytes32) {
        return IWmbGateway(wmbGateway).dispatchMessageBatch{value: fee}(toChainId, messages);
    }
}
"
    },
    "contracts/app/WmbRetryableApp.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../utils/ExcessivelySafeCall.sol";
import "./WmbApp.sol";

/**
 * @title WmbRetryableApp
 * @dev Abstract contract to be inherited by applications to use Wanchain Message Bridge for send and receive messages and support retry failed transactions
 */
abstract contract WmbRetryableApp is WmbApp {
    // Import the ExcessivelySafeCall library for safe external function calls
    using ExcessivelySafeCall for address;

    // this is the gas reserve for storing the failed message data hash
    // so that we can retry it later
    // this is a constant value that is used in the wmbReceive function
    // and should be updated if the wmbReceive function is updated.
    uint256 public constant RESERVE_GAS_FOR_FAILED_MESSAGE_STORE = 50_000;

    // A mapping of failed messages by their chain ID, address, and message ID, with the value being the message hash
    // This is used to keep track of failed messages in order to retry them later
    // messageId => messageHash
    mapping(bytes32 => bytes32) public failedMessages;

    event MessageFailed(
        bytes32 indexed messageId,
        uint256 indexed fromChainId,
        address indexed from,
        bytes data,
        bytes reason
    );

    event RetryMessageSuccess(
        uint256 indexed fromChainId,
        address indexed from,
        bytes32 indexed messageId,
        bytes32 messageHash
    );

    event MessageDroped(
        bytes32 indexed messageId
    );

    /**
     * @dev Function to receive a WMB message from the WMB Gateway
     * @param data Message data
     * @param messageId Message ID
     * @param fromChainId ID of the chain the message is from
     * @param from Address of the contract the message is from
     */
    function wmbReceive(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address from
    ) override external {
        // Only the WMB gateway can call this function
        require(msg.sender == wmbGateway, "WmbApp: Only WMB gateway can call this function");
        require(trustedRemotes[fromChainId][from], "WmbApp: Remote is not trusted");

        (bool success, bytes memory reason) = address(this).excessivelySafeCall(gasleft() - RESERVE_GAS_FOR_FAILED_MESSAGE_STORE, 150, abi.encodeWithSelector(this.nonblockingWmbReceive.selector, data, messageId, fromChainId, from));
        // try-catch all errors/exceptions
        if (!success) {
            _storeFailedMessage(data, messageId, fromChainId, from, reason);
        }
    }

    // @dev internal use only for safe non-blocking call to _wmbReceive
    function nonblockingWmbReceive(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address from
    ) public {
        require(msg.sender == address(this), "WmbApp: Only this contract can call this function");
        // call the internal _wmbReceive function, which should be implemented by the inheriting contract
        _wmbReceive(data, messageId, fromChainId, from);
    }

    /**
     * @notice Retry a failed message by resending the same data
     * @param data The data of the failed message
     * @param messageId The ID of the failed message
     * @param fromChainId The ID of the source chain
     * @param from The address of the source contract
     */
    function retryMessage(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address from
    ) public virtual {
        // assert there is message to retry
        bytes32 messageHash = failedMessages[messageId];
        require(messageHash != bytes32(0), "WmbApp: no stored message");
        require(keccak256(abi.encodePacked(data, messageId, fromChainId, from)) == messageHash, "WmbApp: invalid message data");
        // clear the stored message
        delete failedMessages[messageId];
        // execute the message. revert if it fails again
        _wmbReceive(data, messageId, fromChainId, from);
        emit RetryMessageSuccess(fromChainId, from, messageId, messageHash);
    }

    /**
     * @dev Stores the failed message in the `failedMessages` mapping and emits a `MessageFailed` event
     * @param data The data of the failed message
     * @param messageId The ID of the failed message
     * @param fromChainId The ID of the chain the message was sent from
     * @param from The address the message was sent from
     * @param _reason The reason why the message failed
     */
    function _storeFailedMessage(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address from,
        bytes memory _reason
    ) internal virtual {
        failedMessages[messageId] = keccak256(abi.encodePacked(data, messageId, fromChainId, from));
        emit MessageFailed(messageId, fromChainId, from, data, _reason);
    }

    /**
     * @notice Drop a failed message by messageId
     * @param messageId The ID of the failed message
     */
    function _dropMessage(
        bytes32 messageId
    ) internal virtual {
        // clear the stored message
        delete failedMessages[messageId];
        emit MessageDroped(messageId);
    }
}
"
    },
    "contracts/examples/CCPoolV1.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../app/WmbApp.sol";

// Cross Chain Token Pool
contract CCPoolV1 is WmbApp {
    address public poolToken;

    // chain id => remote pool address
    mapping(uint => address) public remotePools;

    event CrossArrive(uint256 indexed fromChainId, address indexed from, address indexed to, uint256 amount, string crossType);

    event CrossRequest(uint256 indexed toChainId, address indexed from, address indexed to, uint256 amount);

    constructor(address admin, address _wmbGateway, address _poolToken) WmbApp() {
        initialize(admin, _wmbGateway);
        poolToken = _poolToken;
    }

    function configRemotePool(uint256 chainId, address remotePool) public onlyRole(DEFAULT_ADMIN_ROLE) {
        remotePools[chainId] = remotePool;
    }

    function crossTo(uint256 toChainId, address to, uint256 amount, uint gasLimit) public payable {
        // check support before transfer
        require(remotePools[toChainId] != address(0), "remote pool not configured");
        IERC20(poolToken).transferFrom(msg.sender, address(this), amount);
        uint fee = estimateFee(toChainId, gasLimit);
        _dispatchMessage(toChainId, remotePools[toChainId], abi.encode(msg.sender, to, amount, "crossTo"), fee);
        emit CrossRequest(toChainId, msg.sender, to, amount);
    }

    function _wmbReceive(
        bytes calldata data,
        bytes32 /*messageId*/,
        uint256 fromChainId,
        address /*fromSC*/
    ) internal override {
        (address fromAccount, address to, uint256 amount, string memory crossType) = abi.decode(data, (address, address, uint256, string));
        require(IERC20(poolToken).balanceOf(address(this)) >= amount, "Pool balance not enough");
        IERC20(poolToken).transfer(to, amount);
        emit CrossArrive(fromChainId, fromAccount, to, amount, crossType);
    }
}
"
    },
    "contracts/examples/CCPoolV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../app/WmbApp.sol";

// Cross Chain Token Pool
contract CCPoolV2 is WmbApp {
    address public poolToken;

    // chain id => remote pool address
    mapping(uint => address) public remotePools;

    event CrossArrive(uint256 indexed fromChainId, address indexed from, address indexed to, uint256 amount, string crossType);

    event CrossRequest(uint256 indexed toChainId, address indexed from, address indexed to, uint256 amount);

    event CrossRevert(uint256 indexed fromChainId, address indexed from, address indexed to, uint256 amount);

    error RevertFailed (
        address from,
        address to,
        uint256 amount,
        uint256 fromChainId
    );

    constructor(address admin, address _wmbGateway, address _poolToken) WmbApp() {
        initialize(admin, _wmbGateway);
        poolToken = _poolToken;
    }

    function configRemotePool(uint256 chainId, address remotePool) public onlyRole(DEFAULT_ADMIN_ROLE) {
        remotePools[chainId] = remotePool;
    }

    function crossTo(uint256 toChainId, address to, uint256 amount) public payable {
        require(remotePools[toChainId] != address(0), "remote pool not configured");

        IERC20(poolToken).transferFrom(msg.sender, address(this), amount);

        uint fee = estimateFee(toChainId, 800_000);

        _dispatchMessage(toChainId, remotePools[toChainId], abi.encode(msg.sender, to, amount, "crossTo"), fee);

        emit CrossRequest(toChainId, msg.sender, to, amount);
    }

    // Transfer in enough native coin for fee. 
    receive() external payable {}

    function _wmbReceive(
        bytes calldata data,
        bytes32 /*messageId*/,
        uint256 fromChainId,
        address fromSC
    ) internal override {
        (address fromAccount, address to, uint256 amount, string memory crossType) = abi.decode(data, (address, address, uint256, string));
        if (IERC20(poolToken).balanceOf(address(this)) >= amount) {
            IERC20(poolToken).transfer(to, amount);
            emit CrossArrive(fromChainId, fromAccount, to, amount, crossType);
        } else {
            if (keccak256(bytes(crossType)) == keccak256("crossTo")) {
                uint fee = estimateFee(fromChainId, 400_000);
                _dispatchMessage(fromChainId, fromSC, abi.encode(to, fromAccount, amount, "crossRevert"), fee);
                emit CrossRevert(fromChainId, fromAccount, to, amount);
            } else {
                revert RevertFailed(fromAccount, to, amount, fromChainId);
            }
        }
    }
}
"
