    function transferUnderlying(address to, uint256 value)
        public
        validRecipient(to)
        returns (bool)
    {
        // sub from balance of sender
        _benkeiBalances[msg.sender] = _benkeiBalances[msg.sender].sub(value);

        // add to balance of receiver
        _benkeiBalances[to] = _benkeiBalances[to].add(value);
        emit Transfer(msg.sender, to, _benkeiToFragment(value));
        return true;
    }

    /* - ERC20 functionality - */

    /**
     * @dev Transfer tokens to a specified address.
     * @param to The address to transfer to.
     * @param value The amount to be transferred.
     * @return True on success, false otherwise.
     */
    function transfer(address to, uint256 value)
        public
        override
        validRecipient(to)
        returns (bool)
    {
        // underlying balance is stored in benkeis, so divide by current scaling factor

        // note, this means as scaling factor grows, dust will be untransferrable.
        // minimum transfer value == benkeisScalingFactor / 1e24;

        // get amount in underlying
        uint256 benkeiValue = _fragmentToBenkei(value);

        // sub from balance of sender
        _benkeiBalances[msg.sender] = _benkeiBalances[msg.sender].sub(benkeiValue);

        // add to balance of receiver
        _benkeiBalances[to] = _benkeiBalances[to].add(benkeiValue);
        emit Transfer(msg.sender, to, value);

        return true;
    }

