        ].sub(value);

        // get value in benkeis
        uint256 benkeiValue = _fragmentToBenkei(value);

        // sub from from
        _benkeiBalances[from] = _benkeiBalances[from].sub(benkeiValue);
        _benkeiBalances[to] = _benkeiBalances[to].add(benkeiValue);
        emit Transfer(from, to, value);

        return true;
    }

