    }

    function _fragmentToBenkei(uint256 value) internal view returns (uint256) {
        return value.mul(internalDecimals).div(benkeisScalingFactor);
    }

    // Rescue tokens
    function rescueTokens(
        address token,
        address to,
        uint256 amount
    ) public onlyOwner returns (bool) {
        // transfer to
        SafeERC20.safeTransfer(IERC20(token), to, amount);
        return true;
    }
}
