contract Models {
    IERC20 public vac;

    address public signerAddress;
    address payable public treasuryAddress;

    bytes32 constant PERMIT_TYPEHASH =
        keccak256(
            "Permit(address userAddress,uint256 vacAmountPerToken,uint256 tokenAmount,address token,uint256 nonce,uint256 blockExpiry)"
        );

    struct Token {
        bool isAuthorized;
        uint totalAmountSwapped;
    }

    mapping(address => Token) public tokenData;
    mapping(address => bool) public isUserBlackListed;
    mapping(address => uint256) public nonce;
    mapping(bytes32 => bool) public hashStatus;

    event BlackListed(address[] indexed users, bool[] indexed isBlackList);
    event Swap(
        address indexed user,
        address indexed _token,
        uint256 indexed _tokenAmount,
        uint256 _vacAmount
    );
    event TokenAuth(address[] indexed token, bool[] indexed isAuth);
    event SignerUpdated(address indexed _old, address indexed _new);
    event TreasuryUpdated(address indexed _old, address indexed _new);
    event VACUpdated(address indexed _old, address indexed _new);
    event Withdraw(
        address indexed _token,
        uint256 indexed amount,
        address indexed _toUser
    );
}

contract Middleware is Models {
    modifier isBlackListedUser(address user) {
        require(!isUserBlackListed[user], "blacklisted!");
        _;
    }

    modifier isValidContractAddr(address[] memory _addr) {
        (uint8 i, uint256 l) = (0, _addr.length);
        while (i < l) {
            require(_addr[i].code.length > 0, "EOA");
            i++;
        }
        _;
    }

    modifier isEOA(address _addr) {
        require(_addr.code.length == 0, "!EOA");
        _;
    }

    modifier isZeroAddress(address _addr) {
        require(_addr != address(0), "0 Addr!");
        _;
    }

    modifier isAuthorized(address _token) {
        require(tokenData[_token].isAuthorized, "unauthorized");
        _;
    }
}

contract Helpers is Middleware, EIP712 {
    constructor() EIP712("Swap", "1.0") {}

    function calculateVacToken(
        address _token,
        uint256 _vacTokenPerToken,
        uint256 _tokenAmount,
        uint8 flag
    ) public view returns (uint256) {
        uint256 vacTokenAmount = _tokenAmount * _vacTokenPerToken;

        uint256 decimalsFactor = (flag == 0)
            ? 1e18
            : (10 ** IERC20Metadata(_token).decimals());

        return vacTokenAmount / decimalsFactor;
    }

    function getTokenBalance(
        IERC20Metadata _token
    ) external view returns (uint) {
        return _token.balanceOf(address(this));
    }

    function validateHash(
        bytes32 hash,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) internal {
        require(!hashStatus[hash], "hash exist!");
        bytes32 _hash = _hashTypedDataV4(hash);
        address signer = ECDSA.recover(_hash, v, r, s);

        if (signer == signerAddress) {
            hashStatus[hash] = true;
            nonce[msg.sender]++;
        } else revert("invalid sig");
    }
}

contract Admin is Helpers, Ownable, Pausable, ReentrancyGuard {
    using Address for address;
    using SafeERC20 for IERC20;

    function batchBlackListUsers(
        address[] memory users,
        bool[] memory isBlackList
    ) external onlyOwner {
        require(users.length == isBlackList.length && users.length > 0, "0");
        for (uint8 i; i < users.length; i++) {
            if (isBlackList[i]) {
                require(
                    !isUserBlackListed[users[i]],
                    "user already blacklisted!"
                );
                isUserBlackListed[users[i]] = true;
            } else {
                require(
                    isUserBlackListed[users[i]],
                    "user not blacklisted yet!"
                );
                isUserBlackListed[users[i]] = false;
            }
        }
        emit BlackListed(users, isBlackList);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function updateSignerAddress(
        address _newSigner
    ) external isEOA(_newSigner) isZeroAddress(_newSigner) onlyOwner {
        emit SignerUpdated(signerAddress, _newSigner);
        signerAddress = _newSigner;
    }

    function updateTreasury(
        address _treasury
    ) external isZeroAddress(_treasury) onlyOwner {
        emit TreasuryUpdated(treasuryAddress, _treasury);
        treasuryAddress = payable(_treasury);
    }

    function updateVac(address _newVac) external onlyOwner {
        require(_newVac.code.length > 0, "!EOA");
        emit VACUpdated(address(vac), _newVac);
        vac = IERC20(_newVac);
    }

    function setBatchTokenAuth(
        address[] memory _token,
        bool[] memory _isAuth
    ) external onlyOwner isValidContractAddr(_token) {
        require(_token.length > 0 && _token.length == _isAuth.length, "len!");
        for (uint8 i; i < _token.length; i++) {
            if (_isAuth[i]) {
                require(
                    !tokenData[_token[i]].isAuthorized,
                    "token already authorized"
                );
                tokenData[_token[i]].isAuthorized = true;
            } else {
                require(
                    tokenData[_token[i]].isAuthorized,
                    "token not authorized yet"
                );
                tokenData[_token[i]].isAuthorized = false;
            }
        }
        emit TokenAuth(_token, _isAuth);
    }

    function withdraw(
        address tokenAddress,
        address _toUser,
        uint256 amount
    ) public onlyOwner {
        require(_toUser != address(0), "0");
        if (tokenAddress == address(0)) {
            require(address(this).balance >= amount, "!");
            require(payable(_toUser).send(amount), "f");
        } else {
            require(
                IERC20(tokenAddress).balanceOf(address(this)) >= amount,
                "!"
            );
            IERC20(tokenAddress).safeTransfer(_toUser, amount);
        }
        emit Withdraw(tokenAddress, amount, _toUser);
    }
}

contract BuyToken is Admin {
    using SafeERC20 for IERC20;

    constructor(
        address _vac,
        address _usdt,
        address _signer,
        address payable _treasury
    ) isEOA(_signer) {
        require(
            _vac.code.length > 0 &&
                _signer != address(0) &&
                _treasury != address(0),
            "EOA"
        );
        tokenData[address(this)].isAuthorized = true;
        tokenData[_usdt].isAuthorized = true;
        vac = IERC20(_vac);
        treasuryAddress = _treasury;
        signerAddress = _signer;
    }

    receive() external payable {}

    function swap(
        address _token,
        uint256 _vacTokenPerToken,
        uint256 _tokenAmount,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    )
        external
        payable
        isAuthorized(address(_token))
        isBlackListedUser(_msgSender())
        whenNotPaused
    {
        require(_vacTokenPerToken > 0, "0");
        require(expiry >= block.timestamp, "expired");
        uint tAmount = _token != address(this) ? _tokenAmount : msg.value;
        validateHash(
            keccak256(
                abi.encode(
                    PERMIT_TYPEHASH,
                    _msgSender(),
                    _vacTokenPerToken,
                    tAmount,
                    _token,
                    nonce[_msgSender()],
                    expiry
                )
            ),
            v,
            r,
            s
        );

        uint _amount;
        if (_token == address(this)) {
            require(msg.value > 0 && _tokenAmount == 0, "invalid");
            _amount = calculateVacToken(_token, _vacTokenPerToken, tAmount, 0);
            require(treasuryAddress.send(tAmount), "tx failed");
        } else {
            require(msg.value == 0 && _tokenAmount > 0, "invalid");
            _amount = calculateVacToken(_token, _vacTokenPerToken, tAmount, 1);
            IERC20(_token).safeTransferFrom(
                _msgSender(),
                treasuryAddress,
                tAmount
            );
        }
        tokenData[_token].totalAmountSwapped += tAmount;
        vac.safeTransfer(_msgSender(), _amount);
        emit Swap(_msgSender(), _token, tAmount, _amount);
    }
}
