      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "openzeppelin/utils/Strings.sol";
import "./ControlledAccess.sol";

interface iNerd {
    function mint(uint256 quantity) external;
    function ownerMint(address to, uint256 quantity) external;
    function setIQToken(address iqAddress) external;
    function setBaseURI(string memory baseURI) external;
    function setPublicSaleMintPrice(uint256 price) external;
    function togglePublicSale() external;
}

error SaleNotLive();
error InsufficientFunds();

/// @title The CryptoNerdz minting wrapper
/// @author swetiyeti
contract NerdzWrapper is ControlledAccess {
    using Strings for uint256;

    uint256 public constant maxMint = 8;
    uint256 public constant collectionSize = 4444;

    uint256 public mintPrice = 0.025 ether;
    bool public saleActive = false;
    address public nerdzWallet = 0x163AAD4539Cb8D6F1d940956c0D57e163158474F;
    iNerd public Nerdz;

    constructor(address nerdz) {
        Nerdz = iNerd(nerdz);
    }

    /**
     * Public Mint Function
     * @notice This function will mint `quantity` nerdz.
     */
    function mint(uint256 quantity) external payable {
        if (!saleActive) revert SaleNotLive();
        if (msg.value < mintPrice * quantity) revert InsufficientFunds();
        Nerdz.ownerMint(msg.sender, quantity);
        if (msg.value > mintPrice * quantity) {
            payable(msg.sender).transfer(msg.value - mintPrice * quantity);
        }
    }

    /**
     * @notice Sends contract funds to `nerdzWallet`.
     */
    function withdraw() external onlyOwner {
        uint256 sendAmount = address(this).balance;
        payable(nerdzWallet).transfer(sendAmount);
    }

    /**
     * @notice Mint function only usable by contract owner. Use reserved for giveaways and promotions.
     */
    function ownerMint(address to, uint256 quantity) public onlyOwner {
        Nerdz.ownerMint(to, quantity);
    }

    /**
     * @notice Sets the IQ Token contract address.
     */
    function setIQToken(address iqAddress) external onlyOwner {
        Nerdz.setIQToken(iqAddress);
    }

    /**
     * @notice Function for updating the revealed token metadata URI.
     * @dev When setting this value, only replace _CID_ in the following: ipfs://_CID_/
     */
    function setBaseURI(string memory baseURI) public onlyOwner {
        Nerdz.setBaseURI(baseURI);
    }

    /**
     * @notice Sets the Nerdz mint price to `price`.
     */
    function setMintPrice(uint256 price) public onlyOwner {
        mintPrice = price;
    }

    /**
     * @notice Sets the payout wallet address to `wallet`.
     */
    function setNerdzWallet(address wallet) public onlyOwner {
        nerdzWallet = wallet;
    }

    /**
     * @notice Toggles availability of minting Nerdz.
     */
    function toggleSaleActive() public onlyOwner {
        saleActive = !saleActive;
    }
}
