        IERC20 toToken,
        uint256 amount,
        uint256 deadline
    )
        public
        payable
    {
        IUniswapV2Router02 router = verseRouter;
        // IUniswapV2Router02 router = tangoSwapRouter;

        IERC20 fromTokenReal = fromToken.isETH() ? WETH : fromToken;
        IERC20 toTokenReal = toToken.isETH() ? WETH : toToken;

        address[] memory path = new address[](2);
        path[0] = address(fromTokenReal);
        path[1] = address(toTokenReal);

        fromTokenReal.universalApproveMaxIfItsNotEnough(
            address(router),
            amount
        );

        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: amount}(
            0,
            path,
            address(this),
            deadline
        );
    }

