        ThinRaffle(
            _subscriptionId,
            _vrfCoordinator,
            _keyHash,
            _entryPrice,
            _requestConfirmations,
            _callbackGasLimit
        )
    {multisig = _multisig;}

    function burn(uint amount) public {
        _burn(msg.sender, amount);
    }

    function setwlCanBuyUntil(uint256 time) public {
        require(!wlCanBuyUntilSet, "wlCanBuyUntil already set");
        wlCanBuyUntil = time;
        wlCanBuyUntilSet = true;
    }

    function withdrawCHCH() external onlyOwner {
        _transfer(address(this), msg.sender, balanceOf(address(this)));
    }

    function _update(
        address from,
        address to,
        uint256 amount // Overrides the _transfer() function to use an optional transfer condition.
    )
        internal
        virtual
        override(
            ERC20v1 // Specifies only the ERC20 contract for the override.
        )
        nonReentrant // Prevents re-entrancy attacks.
    {
        if (block.timestamp > wlCanBuyUntil || msg.sender == multisig ) {
            super._update(from, to, amount); // Transfers 100% of amount to recipient.
        } else {
            require(
                balanceOf(from) >= amount,
                "ERC20: transfer amount exceeds balance"
            ); // Makes sure sender has the required token amount for the total.
            require(
                hasWonRaffle[from] == true,
                "This user doesnt have WL and WL buy window is not passed yet. Check WLtimeover for timestamp."
            );
            super._update(from, to, amount); // Perform transfer
        }
    }
}
