    "contracts/fatboi/ThinCrowdsale.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ERC20v1} from "./ERC20.sol";
import "./Ownable.sol";

abstract contract ThinCrowdsale is ERC20v1, ReentrancyGuard {
    mapping(address => uint256) public userBalances;
    mapping(address => bool) public claimed;
    mapping(address => bool) public entered;

    enum WITHDRAWALS {
        OPEN,
        CLOSED
    }
    WITHDRAWALS public withdrawals_state;

    bytes32 public immutable lpRoot;
    uint256 private rate;
    uint256 public entryPriceLP;
    uint256 public maxTokenAlloc;
    uint8 public participants;
    address payable private multisig;
    address public token;

    event TokensPurchased(
        address indexed purchaser,
        address indexed beneficiary,
        uint256 value,
        uint256 amount,
        bytes data
    );

    constructor(
        address payable _multisig,
        bytes32 _lproot,
        uint _entryPriceLP,
        uint256 _maxTokenAlloc
    ) {
        lpRoot = _lproot;
        multisig = _multisig;
        entryPriceLP = _entryPriceLP;
        maxTokenAlloc = _maxTokenAlloc;
        token = address(this);

        withdrawals_state = WITHDRAWALS.CLOSED;
    }

    function buyTokens(bytes32[] memory proof) public payable nonReentrant {
        require(
            msg.value == entryPriceLP,
            "Not the right amount of eth!, check entryPriceLP"
        );
        require(
            entered[msg.sender] != true,
            "Already has entered, cant allocate any more tokens"
        );
        uint256 weiAmount = msg.value;
        validateTree(msg.sender, proof);
        userBalances[msg.sender] += maxTokenAlloc;
        (bool sent, bytes memory data) = payable(multisig).call{value: msg.value}("");
        require(sent, "Failed to send Ether to multisig");
        entered[msg.sender] = true;
        participants++;
        emit TokensPurchased(
            _msgSender(),
            msg.sender,
            weiAmount,
            maxTokenAlloc,
            data
        );
    }

    function validateTree( 
        address _beneficiary,
        bytes32[] memory proof
    ) internal view {
        bytes32 leaf = keccak256(
            bytes.concat(keccak256(abi.encode(msg.sender)))
        );
        require(MerkleProof.verify(proof, lpRoot, leaf), "Invalid proof");
        require(
            _beneficiary != address(0),
            "Crowdsale: beneficiary is the zero address"
        );
    }

    function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
        IERC20(token).transfer(beneficiary, tokenAmount);
        claimed[msg.sender] = true;
    }

    function withdrawTokens() public {
        require(withdrawals_state == WITHDRAWALS.OPEN, "withdrawals closed");
        require(claimed[msg.sender] != true, "already claimed");
        uint256 amount = userBalances[msg.sender];
        require(amount > 0);
        userBalances[msg.sender] = 0;
        _deliverTokens(msg.sender, amount);
    }

    function OpenWithdrawals() public onlyOwner {
        withdrawals_state = WITHDRAWALS.OPEN;
    }

    function CloseWithdrawals() public onlyOwner {
        withdrawals_state = WITHDRAWALS.CLOSED;
    }
}
