    "contracts/fatboi/ThinRaffle.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Ownable.sol";


abstract contract ThinRaffle is VRFConsumerBaseV2, OwnableV1  {
    uint256 public seed;//where seed is stored.

    bool public winnersSelected;   //boolean for if SelectWinners() has been ran, can only be ran once so it doesnt overwrite existing WLs.
    uint256 public entryPrice;    //ticket entry price.
    uint64 public subscriptionId;    //subscriptionId for VRF. this is the subscription we will fill with LINK to have it spend from.
    address public vrfCoordinator;    //vrf coordinator.
    uint16 public ticketsSubmitted;    //keep track of how many tickets we have entered.
    uint8 public maxWinners = 100; //max amount of WLs we can pull in selectwinners. condition in for loop
    uint16 public maxTickets = 1000;    //max amount of tickets allowed
    mapping(uint16 => bool) indexChoosen; //mapping to keep track of which indexes we have already chosen in selectwinners.
    mapping(uint16 => address) public tickets; //mapping of tickets, with index as key.
    mapping(address => bool) public hasEnteredRaffle; //mapping of addresses that have entered the raffle.
    event WonRaffle(address winner, uint16 ticketIndex); //event for when someone wins the raffle.
    mapping(address => bool) public hasWonRaffle; //mapping of addresses that have won the raffle.

    //VRF stuff, storage of needed parameters.
    uint64 s_subscriptionId;
    bytes32 keyHash;
    uint16 requestConfirmations;
    uint32 callbackGasLimit;
    uint256[] public requestIds;
    uint256 public lastRequestId;
    uint256 public timeStampSinceLastRequest;


    mapping(uint256 => RequestStatus)
    public s_requests; /* requestId --> requestStatus */

    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        uint256[] randomWords; //contains randomNumbers when they are returned from VRF
    }

    //events are neat to have for the frontend.
    event RequestedRandomness(uint256 requestId);
    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);
    event NewEntrance(uint16, address);

    VRFCoordinatorV2Interface COORDINATOR; //define VRF Coordinator

    uint8 numWords = 1; //number of numbers we ask VRF for.

    constructor(
        uint64 _subscriptionId,
        address _vrfCoordinator,
        bytes32 _keyHash,
        uint256 _entryPrice,
        uint16 _requestConfirmations,
        uint32 _callbackGasLimit
    ) VRFConsumerBaseV2(_vrfCoordinator) {
        COORDINATOR = VRFCoordinatorV2Interface(_vrfCoordinator);
        subscriptionId = _subscriptionId;
        s_subscriptionId = _subscriptionId;
        vrfCoordinator = _vrfCoordinator;
        keyHash = _keyHash;
        entryPrice = _entryPrice;
        requestConfirmations = _requestConfirmations;
        callbackGasLimit = _callbackGasLimit;
    }


    function selectWinners() public onlyOwner {
        require(!winnersSelected, "winners already selected");
        require(ticketsSubmitted == maxTickets, "not there yet");
        require(seed != 0, "seed is missing, use getseed()");
        uint256 j = 0;
        for (uint256 i = 0; i < maxWinners; i++) {
            uint16 randomIndex = getRandom(i, j);
            if (!indexChoosen[randomIndex]) {
                address winner = tickets[randomIndex];
                indexChoosen[randomIndex] = true;
                hasWonRaffle[winner] = true;
            } else {
                j++;
                i--;
            }
        }

        winnersSelected = true;

    }

    function getRandom(uint i, uint j) private view returns (uint16) {
       return uint16(uint256(keccak256(abi.encodePacked(seed, blockhash(block.number - 1), i, j))) % ticketsSubmitted);
    }

    function enter() public payable {
        require(!hasEnteredRaffle[msg.sender], "Seems to already be entered, try a different wallet. 1 ticket per address");
        require(msg.value == entryPrice, "Not the correct amount of ETH!, check entryPrice");
        require(ticketsSubmitted < maxTickets, "Raffle is already full, sorry");
        //set tickets with sender´s address.
        //put it inside a ticket, then inside tickets array mapping.
        tickets[ticketsSubmitted] = msg.sender;
        hasEnteredRaffle[msg.sender] = true;
        ticketsSubmitted++;
        emit NewEntrance(ticketsSubmitted, msg.sender);
    }

    function setEntranceFee(
        uint256 newCostToEnter
    ) public onlyOwner returns (uint256) {
        entryPrice = newCostToEnter;
        return entryPrice;
    }

    function getSeed() public onlyOwner {

        require(seed == 0, "Seed exists, so cant get a new one");

        (uint96 balance, , ,) = COORDINATOR.getSubscription(subscriptionId);

        require(
            balance >= 0, 
            "Need enough LINK in VRF Subscription to request random numbers upon raffle end!"
        );
        uint256 requestId = requestRandomWords();
        emit RequestedRandomness(requestId);
    }

    // Assumes the subscription is funded sufficiently.
    function requestRandomWords()
    internal
    onlyOwner
    returns (uint256 requestId)
    {
        // Will revert if subscription is not set and funded.
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        timeStampSinceLastRequest = block.timestamp;
        emit RequestSent(requestId, numWords);
        return requestId;
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        require(s_requests[_requestId].exists, "request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;
        seed = _randomWords[0];
        emit RequestFulfilled(_requestId, _randomWords);
    }

    function getRequestStatus(
        uint256 _requestId
    ) public view returns (bool fulfilled, uint256[] memory randomWords) {
        require(s_requests[_requestId].exists, "request not found");
        RequestStatus memory request = s_requests[_requestId];
        return (request.fulfilled, request.randomWords);
    }

    function withdrawETH(uint256 amount) external onlyOwner {
        require(amount > 0, "Withdrawal amount must be greater than 0");
        require(address(this).balance >= amount, "Insufficient balance in the contract");
        payable(owner()).transfer(amount);
    }
}
