pragma solidity 0.7.5;

import "./SafeMath.sol";
import "./Address.sol";
import "./IERC20.sol";
import "./Ownable.sol";

contract subscription is Ownable{
    using Address for address;
    using SafeMath for uint256;

    address public USDC;
    address public USDT;
    uint256 public priceStable;
    uint256 public priceETH;
    address private forward;
    address private forwardUSA;

    event Payment(
        string currency,
        uint256 amount,
        string id,
        address sender,
        address forwardTo
    );

    constructor(address _USDC, address _USDT, address _forward, address _forwardUSA, uint256 _priceStable, uint256 _priceETH ){
        USDC = _USDC;
        USDT = _USDT;
        forward = _forward;
        forwardUSA = _forwardUSA;
        priceStable = _priceStable;
        priceETH = _priceETH;
    }

    function payUSDC(string memory _id, bool _world ) external returns(bool) {
        address to;
        if (_world == true) {to = forward;} else if (_world == false) {to = forwardUSA;} else {revert();}
        IERC20(USDC).transferFrom(msg.sender, to, priceStable);
        emit Payment("USDC", priceStable, _id, msg.sender, to);
        return true;
    }

    function payUSDT(string memory _id, bool _world) external returns(bool) {
        address to;
        if (_world == true) {to = forward;} else if (_world == false) {to = forwardUSA;} else {revert();}
        IERC20(USDT).transferFrom(msg.sender, to, priceStable);
        emit Payment("USDT", priceStable, _id, msg.sender, to);
        return true;
    }

    function payETH(string memory _id, bool _world) external payable returns(bool) {
        address to;
        if (_world == true) {to = forward;} else if (_world == false) {to = forwardUSA;} else {revert();}
        require(msg.value >= priceETH, "Dev: invalid amount");
        (bool sent, bytes memory data) = to.call{value: msg.value}("");
        require(sent, "Dev: Ether payment error");
        emit Payment("ETH", msg.value, _id, msg.sender, to);
        return true;
    }

    function setPriceStable(uint256 _price)external onlyOwner returns(bool) {
        priceStable = _price;
         return true;
    }

    function setPriceETH(uint256 _price)external onlyOwner returns(bool) {
        priceETH = _price;
         return true;
    }

    function setForward(address _forward) external onlyOwner returns(bool) {
        forward = _forward;
         return true;
    }

    function setForwardUSA(address _forwardUSA) external onlyOwner returns(bool) {
        forwardUSA = _forwardUSA;
         return true;
    }

    function recoverEther(uint256 _amount) external onlyOwner returns(bool){
        payable(owner()).transfer(_amount);
        return true;
    }

    function recoverERC20(address _tokenAddress, uint256 _amount) external onlyOwner returns(bool){
        IERC20 token = IERC20(_tokenAddress);
        token.transfer(owner(), _amount);
        return true;
    }

}
