        uint8[] tempo;
        uint8[] params;
    }

    struct TextOverlay {
        bytes svg;
        uint16 width;
        uint16 height;
    }

    struct MovementStruct {
        uint16 width;
        bytes svg;
    }

    struct CanvasStruct {
        uint16 scale;
        uint16 maxWidth;
